\echo '029_wcc_magic_invocations.sql'
-- Magic invocations (druid & priest) from temp TSV

CREATE TABLE IF NOT EXISTS wcc_magic_invocations (
  ms_id               varchar(10) PRIMARY KEY,  -- e.g. 'MS066'
  dlc_dlc_id          varchar(64) NOT NULL REFERENCES wcc_dlcs(dlc_id),

  magic_type_id       uuid NOT NULL,            -- ck_id('magic.gruid_invocations' | 'magic.priest_invocations')
  level_id            uuid NULL,                -- ck_id('level.*')
  form_id             uuid NULL,                -- ck_id('magic.form.*')
  effect_time_unit_id uuid NULL,                -- ck_id('time.unit.*')

  cult_or_circle_id   uuid NULL,                -- ck_id('witcher_cc.magic.invocation.cult_or_circle.'||ms_id)
  name_id             uuid NOT NULL,            -- ck_id('witcher_cc.magic.invocation.name.'||ms_id)
  effect_id           uuid NOT NULL,            -- ck_id('witcher_cc.magic.invocation.effect.'||ms_id)

  distance            text NULL,
  zone_size           text NULL,
  stamina_cast        text NULL,
  stamina_keeping     text NULL,
  damage              text NULL,
  effect_time_value   text NULL,

  defense_ids         uuid[] NULL
);

WITH raw_data (
  ms_id, dlc_dlc_id,
  magic_type_key, level_key,
  cult_ru, cult_en,
  name_ru, name_en,
  effect_ru,
  form_key,
  distance, zone_size,
  stamina_cast, stamina_keeping,
  damage,
  effect_time_value, effect_time_unit_key,
  defense_1, defense_2
) AS ( VALUES
  ('MS066', 'core', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Кипящая кровь$$, $$Boiling Blood$$, $$Накладывается на животное или неразумное чудовище, вызывая в нем злобу по отношению к назначенной цели, на которую тот будет пытаться напасть.$$, 'magic.form.direct', $$8$$, $$$$, $$3$$, $$$$, $$$$, $$1d10$$, 'time.unit.round', $$Воля*3$$, $$$$),
  ('MS067', 'core', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Проклятая болезнь$$, $$Cursed Illness$$, $$Вызывает у цели болезнь. В зависимости от вложенной <b>ВЫН</b>, цель получает: * 2: цель кашляет, получая <b><i>ошеломле-ние</i></b>, т.е. штраф -2 к <i>(Атаке)</i> и <i>(Защите)</i>, до следующего своего хода.  * 4: цель <b><i>дезориентирована</i></b> из-за сильной тошноты. Т.е. она не может совершать действий, а попасть по ней можно со <b>Сл:10</b>. Эффект спадет, если по цели попадут.  * 6: цель как бы <b><i>отравлена</i></b> (получает по 3 урона) из-за серьезной болезни.  - Снять эффекты болезни можно пробросив <i>(Стойкость)</i> против <i>(Сотворения инвокации)</i>.$$, 'magic.form.direct', $$8$$, $$$$, $$2-6$$, $$$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS068', 'core', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Друг животных$$, $$Friend to Wild Kind$$, $$Успокаивает одно животное. - Можно наложить на себя, тогда друид получает бонус +3 к <i>(Выживанию в дикой природе)</i> при общении с животными.$$, 'magic.form.direct', $$5$$, $$$$, $$1$$, $$$$, $$$$, $$1d6$$, 'time.unit.hour', $$Воля*3$$, $$$$),
  ('MS069', 'core', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Дар природы$$, $$Nature's Gift$$, $$Выращивает съедобные растения растений на любой почве. - Растений достаточно, чтобы насытить на 1 день количество персон, равное числу затраченных очков <b>ВЫН</b>.$$, 'magic.form.direct', $$2$$, $$$$, $$x$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS070', 'core', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Взор природы$$, $$Nature's Sight$$, $$Позволяет видеть существ, противоестественных для мира Неверленда. - Заклинатель может разглядеть чудовищ даже сквозь препятствия.  - Увиденные таким образом чудовища кажутся светящимися.$$, 'magic.form.zone_centered', $$$$, $$50$$, $$2$$, $$1$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS071', 'core', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Символ скрытого$$, $$Sigil of the Hidden$$, $$Укрывает растительностью и природным декором указанную область, скрывая содержимое невидимостью. - Даёт бонус +5 к <i>(Скрытности)</i>, но не позволяет никому двинуться. - Снять маскировку можно:  * Повторив инвокацию   * Нанести веткам 10 ПЗ урона$$, 'magic.form.zone_circle', $$$$, $$3$$, $$2$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS072', 'core', 'magic.gruid_invocations', 'level.journeyman', $$$$, $$$$, $$Благословение исцеления$$, $$Blessing of Healing$$, $$Эффект на выбор: - Восстанавливает 3 ПЗ за ход. - Лечит крит.ранения, для чего нужно применить спелл [уровень раны]*2+2 раз со <b>Сл:</b>[уровень раны]*2+12, а потом отдых для избавления от штрафов раны (не меньше одного дня): 1) Легкая: 8 - <b>ТЕЛ</b> дней 2) Средняя: 12 - <b>ТЕЛ</b> дней 3) Тяжёлая: 15 - <b>ТЕЛ</b> дней 4) Смертельная: штраф навсегда$$, 'magic.form.direct', $$2$$, $$$$, $$5$$, $$3$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS073', 'core', 'magic.gruid_invocations', 'level.journeyman', $$$$, $$$$, $$Животная сила$$, $$Primal Reservoir$$, $$Пробуждает внутренние резервы цели - Даёт бонус +2 к Урону в ближ-нем бою - Дает штраф -2 к <b>ИНТ</b> на время действия заклинания.$$, 'magic.form.direct', $$5$$, $$$$, $$6$$, $$$$, $$$$, $$2d6$$, 'time.unit.round', $$Сопротивление магии$$, $$$$),
  ('MS074', 'core', 'magic.gruid_invocations', 'level.journeyman', $$$$, $$$$, $$Нити жизни$$, $$Threads of Life$$, $$Позволяет увидеть нити жизни, связующие всё живое. - Можно видеть ПЗ, оставшиеся у видимых целей. - Можно видеть и крит. ранения.$$, 'magic.form.zone_centered', $$$$, $$10$$, $$4$$, $$2$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS075', 'core', 'magic.gruid_invocations', 'level.master', $$$$, $$$$, $$Управление природой$$, $$Shape Nature$$, $$Позволяет превратить небольшое дерево в голема. - Голем служит друиду во время действия заклинания, а потом превращается обратно в дерево. - С убитого голема можно полу-чить только 2dl0 древесины. - По остальным параметрам деревянный голем аналогичен нормальному.$$, 'magic.form.direct', $$10$$, $$$$, $$12$$, $$$$, $$$$, $$1d10$$, 'time.unit.round', $$$$, $$$$),
  ('MS076', 'core', 'magic.gruid_invocations', 'level.master', $$$$, $$$$, $$Песнь небес$$, $$Song of the Sky$$, $$Песнь небес меняет погоду вокруг заклинателя. Варианты погоды:   * Ясная: нет модификаторов  * Облачная: мало солнечного света, т.е.    - <b>Сл</b> для навыков возрастает на +2    - Штраф -3 к <i>(Вниманию)</i>.  * Ливень: гасит огонь  * Штормовой ветер: штраф -2 к <b>ЛВК</b> для дистанционных атак.  * Гроза: Каждый ход гроза с шансом 35% бьет молнией по каждой цели (кроме мага). Цель может спастись уворотом и не получить урон 8d6 с 75% шансом <b><i>загореться</i></b>, т.е. получать по 5 урона в каждую часть тела. Броня поглощает урон, но теряет 1 ПБ, как и оружие. Нужно потратить ход чтобы сбить пламя.$$, 'magic.form.zone_centered', $$$$, $$50$$, $$10$$, $$5$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS077', 'core', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Благословение удачи$$, $$Blessing of Good Fortune$$, $$Даёт цели очки <b>Удачи</b>. - Количество очков равно половине очков, которые вы набрали свыше <b>Сл:12</b> при броске <i>(Сотворения инвокаций)</i>. - Максимум можно дать 5 очков <b>Удачи</b>.$$, 'magic.form.direct', $$10$$, $$$$, $$1$$, $$$$, $$$$, $$$$, '', $$СЛ от ведущего$$, $$$$),
  ('MS078', 'core', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Благословение любви$$, $$Blessing of Love$$, $$Даёт цели бонус +3 к <i>Харизме</i> и <i>Соблазнению</i>.$$, 'magic.form.direct', $$5$$, $$$$, $$2$$, $$$$, $$$$, $$1d10$$, 'time.unit.round', $$$$, $$$$),
  ('MS079', 'core', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Священный свет$$, $$Holy Light$$, $$От рук жреца исходит свет, поднимая освещенность вокруг на 1 уровень. Эффекты освещенности: - Штраф к <b>Сл</b> применения навыков:   * Темнота: +5   * Слабый свет: +2   * Дневной свет: нет   * Яркий свет: +2 - Штраф к <i>(Вниманию)</i>:   * Темнота: -4   * Слабый свет: -2   * Дневной свет: нет   * Яркий свет: -3 - Штраф к <i>(Атаке)</i> и <i>(Защите)</i>:   * Темнота: -2   * Яркий свет: -2, если смотреть против источника света$$, 'magic.form.zone_centered', $$$$, $$5$$, $$1$$, $$$$, $$$$, $$3d10$$, 'time.unit.round', $$$$, $$$$),
  ('MS080', 'core', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Хранилище знаний$$, $$Vaults of Knowledge$$, $$Хранилище знаний позволяет заклинателю погрузиться в чертоги собственного разума, чтобы заново пережить любое своё воспоминание или вспомнить знание, когда-либо полученное.$$, 'magic.form.self', $$$$, $$$$, $$3$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS081', 'core', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Воды очищения$$, $$Waters of Clearance$$, $$Снимает эффект <b><i>опьянения</i></b>.  <b><i>Пьяная</i></b> цель имеет * штраф -2 к <b>РЕА</b>, <b>ЛВК</b>, <b>ИНТ</b> * штраф -3 к <i>(Словесной дуэли)</i> * шанс 25% забыть что делал, пока был пьяным.$$, 'magic.form.direct', $$5$$, $$$$, $$1$$, $$$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS082', 'core', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Паутина лжи$$, $$Web of Lies$$, $$Наводит беспорядок в голове цели, заставляя её сомневаться в любой информации и даже в собственных воспоминаниях. - Цель <b><i>дезориентирована</i></b>. То есть она не может совершать действий, а попасть по ней можно со <b>Сл:10</b>. Эффект спадет, если по цели попадут. - Чтобы снять эффект нужно в свой ход бросить 1d10 ниже <b>ИНТ</b> цели.$$, 'magic.form.direct', $$8$$, $$$$, $$3$$, $$$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS083', 'core', 'magic.priest_invocations', 'level.journeyman', $$$$, $$$$, $$Очищающее пламя$$, $$Cleansing Fire$$, $$Воспламеняет цель - Наносит разовый урон - Шанс 100% <b><i>поджечь</i></b> цель$$, 'magic.form.direct', $$10$$, $$$$, $$6$$, $$$$, $$3d6$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS084', 'core', 'magic.priest_invocations', 'level.journeyman', $$$$, $$$$, $$Святая защита$$, $$Holy Fortification$$, $$Укрепляет волю цели, позволяя ей совершить новую проверку против эффектов любого действующего на неё заклинания.$$, 'magic.form.direct', $$10$$, $$$$, $$5$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS085', 'core', 'magic.priest_invocations', 'level.journeyman', $$$$, $$$$, $$Свет истины$$, $$Light of Truth$$, $$Создаёт яркий белый огонёк, заставляющий одну цель говорить правду. - Каждый раунд цель проходит новую проверку <i>(Защиты)</i>. - При провале цель должна отвечать правду на любой заданный вопрос.$$, 'magic.form.direct', $$5$$, $$$$, $$4$$, $$2$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS086', 'core', 'magic.priest_invocations', 'level.master', $$$$, $$$$, $$Божественный портал$$, $$Divine Portal$$, $$<align="left">Создает портал на 1 раунд - Размер: 1x2 метра - Дальность: неограниченная - Можно пронести любые вещи - Отключенный во время прохождения портал наносит крит. рану "потеря конечности" или разрубит вещь надвое. - Цель - только знакомое место.</align>$$, 'magic.form.direct', $$-1$$, $$$$, $$12$$, $$$$, $$$$, $$1$$, 'time.unit.round', $$$$, $$$$),
  ('MS087', 'core', 'magic.priest_invocations', 'level.master', $$$$, $$$$, $$Божественная мудрость$$, $$Divine Wisdom$$, $$Позволяет получить ответ на один вопрос. Это не может быть предсказание будущего. - <b>Сл</b> проверки определяет ведущий в зависимости от секретности информации.$$, 'magic.form.direct', $$50$$, $$$$, $$10$$, $$5$$, $$$$, $$$$, '', $$СЛ от ведущего$$, $$$$),
  ('MS088', 'core', 'magic.priest_invocations', 'level.arch_priest', $$Корам Агх Тэра$$, $$Coram Agh Tera$$, $$Благословение смерти$$, $$Blessing of Death$$, $$Разрезает связывающие цель с этим миром узы жизни. - Цель оказывается в состоянии <b><i>при смерти</i></b>, будто получила урон.
 - Цель могут спасти союзники проверкой <i>(Первой помощи)</i> или <i>(Лечащим прикосновением)</i> со Сл:16, вернув ПЗ в изначальное состояние.  <b><i>При смерти</i></b> все параметры снижаются до 1/3 от нормы. Каждый раунд цель должна бросать 1d10 не выше её базовой <b>УСТ</b>. При провале цель безвозвратно умирает.$$, 'magic.form.direct', $$10$$, $$$$, $$16$$, $$$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS089', 'core', 'magic.priest_invocations', 'level.arch_priest', $$Вечный Огонь$$, $$The Eternal Fire$$, $$Вечный суд$$, $$Eternal Judgement$$, $$Заставляет цель <b><i>загореться</i></b> яр-ким белым пламенем с красными проблесками. - Наносит двойной урон от <b><i>горения</i></b>. - Может быть потушено лишь магическим образом либо полным погружением под воду на 3 раунда. - Всё, что коснётся этого пламени, получает обычное <b><i>горение</i></b>.  <b><i>Горящая</i></b> цель получает по 5 урона в каждую часть тела. Броня поглощает урон, но теряет 1 ПБ, как и оружие. Нужно потратить ход чтобы сбить пламя.$$, 'magic.form.direct', $$10$$, $$$$, $$16$$, $$$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS090', 'core', 'magic.priest_invocations', 'level.arch_priest', $$Фрейя$$, $$Freya$$, $$Храбрость Фрейи$$, $$Freya's Bravery$$, $$Призывает дух Фреи в тело жреца, создавая вокруг него свечение. - Цели становятся невосприим-чивы к <b><i>ужасу</i></b>. - Цели получают доп. 25 ПЗ. - Покинув зону инвокации, эффект длится ещё ld6 раундов пока цель не вернется в зону. - Не обязательно верить во Фрейю. - Эффект снимается с целей по воле жреца.$$, 'magic.form.zone_centered', $$$$, $$20$$, $$16$$, $$$$, $$$$, $$20$$, 'time.unit.round', $$$$, $$$$),
  ('MS091', 'core', 'magic.priest_invocations', 'level.arch_priest', $$Мелитэле$$, $$Melitele$$, $$Целительный покой$$, $$Healing Rest$$, $$Погружает цели в глубокую кому, во время которой раны целей заживают. - Количество целей равно значениею навыка <i>(Сотворение заклинаний). </i> - Цели не реагирую ни на что. - После комы цели очнутся с пол-ным запасом ПЗ. - Снимаются все остаточные временные штрафы от вылеченных еще до инвокации критических ранений.$$, 'magic.form.zone_circle', $$$$, $$5$$, $$16$$, $$$$, $$$$, $$24$$, 'time.unit.hour', $$$$, $$$$),
  ('MS092', 'core', 'magic.priest_invocations', 'level.arch_priest', $$Крева$$, $$Kreve$$, $$Божественная удача$$, $$Luck of the Father$$, $$Позволяет потратить очки <b>Удачи</b> на благословение Кревы.  - На время инвокации потраченные в начале очки <b>Удачи</b> действуют на любой бросок жреца. - По желанию жреца эффект действует как штраф или бонус на любой бросок в области доступа. - Максимум вложенной удачи ограничен тройным значением <i>(Сотворения заклинаний)</i> жреца$$, 'magic.form.zone_centered', $$$$, $$10$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.hour', $$$$, $$$$),
  ('MS093', 'core', 'magic.priest_invocations', 'level.arch_priest', $$Великое Солнце$$, $$The Great Sun$$, $$Белое пламя$$, $$White Flame$$, $$Вокруг жреца появляется аура Белого пламени. - Освещенность становится "Яркий свет", т.е. все получают:  * Штраф +2 к <b>Сл</b> при проверке навыков  * Штраф -3 к <i>(Вниманию)</i>  * Штраф -2 к <i>(Атаке)</i> и <i>(Защите)</i>, если смотреть в сторону жреца. - Растапливается любой лёд - Рассеиваются любые спеллы воды - Новые заклинания воды можно соз-дать только при успешной встречной проверке <i>(Сотворения заклинаний)</i>. - Уязвимые к Солнцу существа полу-чают двойные эффекты уязвимости.$$, 'magic.form.zone_centered', $$$$, $$10$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.hour', $$Сопротивление магии$$, $$$$),
  ('MS177', 'exp_toc', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Благословенное оружие$$, $$Blessed Weapon$$, $$Создает светящуюся руну на оружии ближнего боя, облегчая и уравновешивая его. - Оружие получает эффект <b>Сбалансированного</b> на время действия.$$, 'magic.form.direct', $$2$$, $$$$, $$2$$, $$$$, $$$$, $$30$$, 'time.unit.minute', $$$$, $$$$),
  ('MS178', 'exp_toc', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Божественное вдохновение$$, $$Divine Inspiration$$, $$Благословляет цель проницательностью. - Давая бонус +2 к одному навыку. - Цель должна иметь хотя бы 1 уровень в этом навыке. - Можно использовать раз в день.$$, 'magic.form.direct', $$8$$, $$$$, $$2$$, $$$$, $$$$, $$10$$, 'time.unit.minute', $$$$, $$$$),
  ('MS179', 'exp_toc', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Двойственная тьма$$, $$Duplicitous Darkness$$, $$Окутывает разум цели ложными мыслями, скрывающими истину. - Цель получает +2 к проверкам <i>(Обмана)</i> - Цель нельзя заставить сказать правду магией. - Нельзя шпионить за целью телепатией. - Попытка исследовать цель магией не обнаруживает заклинание. Только при успешной проверке <i>(Внимания)</i> со <b>Сл:20</b>.$$, 'magic.form.direct', $$2$$, $$$$, $$2$$, $$$$, $$$$, $$1$$, 'time.unit.hour', $$$$, $$$$),
  ('MS180', 'exp_toc', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Свет покаяния$$, $$Light of Penance$$, $$Призывает силу божества, истощая волю цели к сопротивлению. - Цель теряет 2d6 <b>ВЫН</b>. - Если порог энергии цели 1 или выше, она теряет еще 2d6 <b>ВЫН</b>.$$, 'magic.form.direct', $$6$$, $$$$, $$3$$, $$$$, $$2d6$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS181', 'exp_toc', 'magic.priest_invocations', 'level.novice', $$$$, $$$$, $$Божественное присутствие$$, $$Presence of the Divine$$, $$Призывает аспект божества, делая осанку жреца внушительной. - Ваш голос слышен издалека - Бонус +4 к <i>(Запугиванию)</i>. - В качестве альтернативы мож-но сделать до 6 целей в пределах 10м невосприим-чивыми к страху.$$, 'magic.form.self', $$$$, $$$$, $$3$$, $$$$, $$$$, $$10$$, 'time.unit.minute', $$$$, $$$$),
  ('MS182', 'exp_toc', 'magic.priest_invocations', 'level.journeyman', $$$$, $$$$, $$Благословение богатства$$, $$Blessing of Abundance$$, $$Наделяет божественным провидением. - При первом лутании чего бы то ни было, вы или ваш игровой мастер бросаете дважды и берёте большее значение. - Это касается каждого найден-ного предмета в том же источнике. Например, при лутании монстра бросаешь дважды на каждый возможный вид лута с него.$$, 'magic.form.self', $$$$, $$$$, $$6$$, $$$$, $$$$, $$1$$, 'time.unit.day', $$$$, $$$$),
  ('MS183', 'exp_toc', 'magic.priest_invocations', 'level.journeyman', $$$$, $$$$, $$Клеймо увядания$$, $$Brand of Withering$$, $$Помечает цель символом разложения, лишая её способности к исцелению. - Цель не может восстан-авливать ПЗ от любых эффектов, включая естест-венное исцеление.$$, 'magic.form.direct', $$8$$, $$$$, $$6$$, $$$$, $$$$, $$7$$, 'time.unit.day', $$Сопротивление магии$$, $$$$),
  ('MS184', 'exp_toc', 'magic.priest_invocations', 'level.journeyman', $$$$, $$$$, $$Поиск ищущих$$, $$Seek the Seekers$$, $$Поиск ищущих реагирует на попытки гадать о вас или ком-либо в пределах 10м от вас. - Срабатывает на Гидромантию, Пиромантию и т.д. - Вы не только поймете, что стали целью гадания, но и увидете лицо прорицателя. - Чтобы понять, что пойман прорицатель должен пробросить <i>(Образование)</i> или <i>(Магические познания)</i> со <b>Сл:20</b>. - Вам сообщат, если прорицатель понял, что был пойман.$$, 'magic.form.self', $$$$, $$$$, $$6$$, $$$$, $$$$, $$1$$, 'time.unit.day', $$$$, $$$$),
  ('MS185', 'exp_toc', 'magic.priest_invocations', 'level.master', $$$$, $$$$, $$Божественное перо$$, $$Quill of the Divine$$, $$Проникает в разум цели, изменяя её воспоминания. Эффект зависит от вложенной ВЫН:  *14: вызывает ретроградную амнезию у цели, блокируя воспоминания  *16: можно внедрить созданные воспоминания. Цель получает бонус +5 к <i>(Защите)</i>, если прежде не подвергалась блокировке воспоминаний.$$, 'magic.form.direct', $$2$$, $$$$, $$14-16$$, $$$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS186', 'exp_toc', 'magic.priest_invocations', 'level.master', $$$$, $$$$, $$Сагитта Аурея$$, $$Sagitta Aurea$$, $$Метает волшебное копье из золотого света. - Копье движется по прямой, сквозь несколько целей. Каждая из них получает одинаковый урон при провале <i>(Защиты)</i>. - Демоны также получают урон и вытесняются из тела. - Демоны, поврежденные этой инвокацией, не могут использовать способности в следующем раунде.$$, 'magic.form.direct', $$20$$, $$$$, $$14$$, $$$$, $$7d6$$, $$$$, '', $$Уклонение$$, $$Атлетика$$),
  ('MS187', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Эпона$$, $$Epona$$, $$Благословенный табун$$, $$Blessed Herd$$, $$Призывает до 10 диких лошадей и наделяет их магией. - Лошади пребывают в следующий час. - Возможны 2 варианта для каждой лошади на выбор: "Лошадь" и "Боевой конь". - Сбруя боевого коня не улучшает ПБ - Выбранные существа получат +5 для <i>(Верховой езды)</i> при использовании этих коней.$$, 'magic.form.self', $$$$, $$$$, $$16$$, $$$$, $$$$, $$7$$, 'time.unit.day', $$$$, $$$$),
  ('MS188', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Свальблод$$, $$Svalblod$$, $$Кровь берсерка$$, $$Blood of the Berserker$$, $$Превращает вас в Гигантского Медведя. - Все предметы сливаются с формой. - Получаете способности Гигантского Медведя, но теряете профессиональные способности и магию. - Ваши Параметры и Навыки заменяются на значения Гигантского Медведя, если они выше ваших. - Урон и критические раны переносятся в обе стороны при смене облика. ПЗ при этом не падает ниже 1.$$, 'magic.form.self', $$$$, $$$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.day', $$$$, $$$$),
  ('MS189', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Вейопатис$$, $$Veyopatis$$, $$Чемпион реки$$, $$Champion of the River$$, $$Помечает цель речной водой, делая её вашим чемпионом.  - Чемпион получает сопроти-вление всем источникам урона. То есть урон, прошедший за броню уменьшается вдвое. - Чемпион получает бонус +5 ко всем броскам.$$, 'magic.form.direct', $$10$$, $$$$, $$16$$, $$5$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS190', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Дана Меадбх$$, $$Dana Meadbh$$, $$Праздник изобилия$$, $$Feast of Plenty$$, $$<align="left">Благословляет пиршество живительными эффектами. Есть два условия: 1) Пир должен быть размахом как минимум на 20 человек, но не ограничен сверху. 2) Пир должен быть приготовлен из местных продуктов - Поевшие на пиру получают:   * Восстанавление 6d6 ПЗ   * Немедленная обработка всех критических ран (но штрафы после обработки всё-таки сохраняются до заживления).   * Снятие <b><i>отравления</i></b>   * Излечение от <b><i>болезней</i></b></align>$$, 'magic.form.direct', $$2$$, $$$$, $$16$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS191', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Пророк Лебеда$$, $$The Prophet Lebioda$$, $$Чудо Лебеды$$, $$Miracle of Lebioda$$, $$Приказывает телу цели исцелить себя. - Цель полностью исцеляет одну критическую рану, включая штраф после обработки, или снимает штраф от уже обработанной раны. - Рана обрабатывается так, как если бы она не была нанесена (включая отрастание конеч-ностей, глаз и т.д.).$$, 'magic.form.direct', $$2$$, $$$$, $$16$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS192', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Нехалени$$, $$Nehaleni$$, $$Предзнаменование несчастья$$, $$Omen of Misfortune$$, $$Заполнить ваш разум проблесками возможного будущего. - Бросайте 1d10, пока не получите 3 результата, не равных 1 и 10, и запишите эти числа. - В течение дня, если цель рядом бросит на d10 одно из этих значений, то вы можете заменить результат на крит провал без использования действия.$$, 'magic.form.zone_centered', $$$$, $$20$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.day', $$$$, $$$$),
  ('MS193', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Морриган$$, $$Morrigan$$, $$Возмездие ворона$$, $$Retribution of the Raven$$, $$Вы блокируете связь со стихийными планами, сводя на нет всю магию. - Только выбранные вами цели или те, кто успешно защитился от этой инвокации, могут использовать магию любой формы. - Эту инвокацию нельзя рассеять.$$, 'magic.form.zone_centered', $$$$, $$20$$, $$16$$, $$5$$, $$$$, $$$$, '', $$Сопротивление магии$$, $$$$),
  ('MS194', 'exp_toc', 'magic.priest_invocations', 'level.arch_priest', $$Лилвани$$, $$Lilvani$$, $$Серебряный свет$$, $$Silverlight$$, $$Вы помечаете до 5 человек благословением лунного света. - Затронутые персонажи не получают штрафов за ПЗ ниже  Порога Ранения (т.е. их <b>РЕА</b>, <b>ЛВК</b>, <b>ИНТ</b>, <b>ВОЛЯ</b> не падают вдвое) или состояния При Смерти (т.е. все параметры не падают втрое). - При первом падении ПЗ ниже единицы, их количество остается равным 1. - Эффект длится до следующего рассвета.$$, 'magic.form.direct', $$2$$, $$$$, $$16$$, $$$$, $$$$, $$0-24$$, 'time.unit.hour', $$$$, $$$$),
  ('MS195', 'exp_toc', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Травничество$$, $$Herbalism$$, $$Позволяет создать эликсир из растительных ингредиентов. - Если все ингредиенты для эликсира являются растениями, вы можете бросать проверки <i>(Сотворения заклинаний)</i> вместо <i>(Алхимии)</i> при создании и  восстановлени.$$, 'magic.form.direct', $$2$$, $$$$, $$2$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS196', 'exp_toc', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Печать щедрости$$, $$Touch of Bounty$$, $$Эффект зависит от того, на что друид наложил печать:-  Испорченную еду или питье можно снова сделать свежими и вкусными. - Кормовое растение становится в два раза более сытным. - Почву можно сделать в два раза более урожайной одним из двух способов:  * Растения станут в два раза больше.  * Урожай зреет в два раза быстрее.$$, 'magic.form.direct', $$2$$, $$$$, $$1$$, $$$$, $$$$, $$365$$, 'time.unit.day', $$$$, $$$$),
  ('MS197', 'exp_toc', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Голос советника$$, $$Voice of the Counselor$$, $$Делает слова вдохновенными. - Вы получаете бонус +4 к <i>(Лидерству)</i>. - Эффект на выбор:   * Можно шепнуть на ухо одной цели в пределах 10 метров.   * Сделать голос громким, слы-шимым издалека.$$, 'magic.form.self', $$$$, $$$$, $$3$$, $$$$, $$$$, $$10$$, 'time.unit.minute', $$$$, $$$$),
  ('MS198', 'exp_toc', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Паутина корней$$, $$Web of Roots$$, $$<align="left">Позволяет управлять корнями дерева. - Корни имеют 15 ПЗ. - Можно менять цель атаки корней. - Варианты движений корней:    * <b><i>Схватить</i></b> цель, т.е. цель не может ходить и получает ко всем физическим действиям -2 штраф. Сбежать можно, бросая раз в ход проверку <i>(Защиты)</i> против новой проверки <i>(Сотворения заклинаний)</i>, или разрубив корни.    * Сделать подсечку, <i><b>сбив с ног</b></i> цель. Такая цель получает штраф -2 к <i>(Атаке)</i> и <i>(Защите)</i>, пока не потратит переме-щение на то чтобы встать.    * Разоружить цель, выбив оружие на (1d6)/2 метров, либо со штрафом -3 к <i>(Сотворению заклинаний)</i> схватить его корнем (но не использовать).</align>$$, 'magic.form.direct_bounce', $$10$$, $$$$, $$2$$, $$1$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS199', 'exp_toc', 'magic.gruid_invocations', 'level.novice', $$$$, $$$$, $$Слово призыва$$, $$Word of Summoning$$, $$Призывает ближайшее дикое животное. - Зверь прибежит за 1d6 раундов. - Зверь дружелюбно и помогает, если друид не предает и не злоупотребляет животным. - По истечению инвокации зверь  становится нейтральным. - Можно использовать 1 раз в день в одной и той же области. - Зверь зависит от вложенной <b>ВЫН</b>:  * 2: маленький (птица, змей, кот...)  * 5: средний (волка, пантера, кабан..)  * 10: большой (медведь, бык, конь..)$$, 'magic.form.direct', $${inf}$$, $$$$, $$2-10$$, $$$$, $$$$, $$10$$, 'time.unit.minute', $$$$, $$$$),
  ('MS200', 'exp_toc', 'magic.gruid_invocations', 'level.journeyman', $$$$, $$$$, $$Осквернение Бигелоу$$, $$Bigelow's Befoulment$$, $$Заражает еду и напитки бактериями.  * 5: съевшему дает <b><i>болезнь</i></b> на 1 час.  * 10: съевшему дает <b><i>отравление</i></b>.  <b><i>Болезнь</i></b>: Штраф -2 ко всем действиям, штраф -25% <b>ВЫН</b>, по требованию ГМ нужны проверки <i>(Стойкости) </i>Сл:14 (при провале получаем <b><i>Тошноту</i></b>). Лечится <i>(Лечащим прикосновением)</i>, со Сл:15 и ночным отдыхом. <b><i>Тошнота</i></b>: раз в 3 раунда при броске 1d10 не ниже <b>ТЕЛ</b> пропускаем ход из-за рвоты. <b><i>Отравление</i></b>: цель получает 3 урона/ход. Для снятия эффекта нужно пробросить <i>(Стойкость)</i> со <b>Сл:15,</b> потратив действие.$$, 'magic.form.zone_cube', $$4$$, $$2$$, $$5-10$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS201', 'exp_toc', 'magic.gruid_invocations', 'level.journeyman', $$$$, $$$$, $$Кровь горы$$, $$Blood of the Mountain$$, $$Наделяет цель огромной силой. - Атаки ближнего боя нельзя па-рировать - Атаки ближнего боя наносят двойной разрушающий урон оружию, щитам и доспехам. - Цель невосприимчива к эффек-там, сбивающим с ног.$$, 'magic.form.direct', $$6$$, $$$$, $$6$$, $$$$, $$$$, $$10$$, 'time.unit.minute', $$$$, $$$$),
  ('MS202', 'exp_toc', 'magic.gruid_invocations', 'level.journeyman', $$$$, $$$$, $$Печать охоты$$, $$Sigil of the Hunt$$, $$Метит до 6 целей как «безопасные» или «добыча».   * «Безопасные» вызывают у зверей дружелюбние, а монстры становятся нейтральны.   * «Добыча» воспринимается хищниками и монстрами как цель для атаки. - Метки кажутся идентичными, но различимы при проверке Образования (Сл 16)$$, 'magic.form.direct', $$2$$, $$$$, $$5$$, $$$$, $$$$, $$1$$, 'time.unit.day', $$Сопротивление магии$$, $$$$),
  ('MS203', 'exp_toc', 'magic.gruid_invocations', 'level.master', $$$$, $$$$, $$Друидский тотем$$, $$Druidic Totem$$, $$Пожертвовав посохом из Дуба и омелы, создаете двухметровый тотем с 20ПЗ, который связывает вас с природой.  - Дает бонус фокусировке +4, т.е. заклинания становятся дешевле на 4 очка <b>ВЫН</b>. - При этом снимает бонус фокусиров-ке от ритуала Дуба и Омелы. - Все животные дружественные. - Дополнительный бонус зависит от местности. - Можно иметь только один активный тотем. Создание нового превращает старый в обычное растение.
<b>Поля</b>: <b>Скор</b> друида равна 12. <b>Горы</b>: Сопротивление дробящему, рубящему и колющему урону. <b>Под землей</b>: Улучшенное Ночное Видение и бонус +4 к проверкам <i>(Скрытности)</i>. <b>Лес</b>: Регенерация 3 ПЗ/ход на 1 час при потреблении фрукта из области тотема. <b>Болото</b>: Невосприимчивость к <b><i>отравлениям</i></b> и <b><i>болезням</i></b>. <b>Берег</b>: Возможность дышать под водой и отсутствие штрафов за бой/маневры в воде. <b>Пустыня</b>: Невосприимчивость к жаре и возможность всегда найти источник воды. Один раз в день восстановление всей <b>ВЫН</b> при потреблении воды из области тотема. <b>Тундра</b>: Невосприимчивость к условиям снега и льда, иммунитет к <b><i>поджогу</i></b> и <b><i>заморозке</i></b>, сопротивление урону от огня и льда.$$, 'magic.form.zone_circle', $$2$$, $$1600$$, $$14$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS204', 'exp_toc', 'magic.gruid_invocations', 'level.master', $$$$, $$$$, $$Гнев природы$$, $$Wrath of Nature$$, $$Природа бушует вокруг друида. - Требует действие полного хода в каждом раунде. - Цель всегда бросает проверку <i>(Защиты)</i> против проверки навыка именно <i>(Сотворения заклинаний)</i> друида. - Эффект инвокации зависит от типа местности, где она создана.
*<b>Поля</b>: либо спавн 1 торнадо в пределах 10м, либо существующий направить в пределах зоны шторма. Торнадо (куб 4 метра) двигается на 10м/ход, наносит 4d6 урона по торсу с отбросом на 10м куда укажет ГМ при контакте и провале защиты через  <i>(Атлетику)</i>.   *<b>Горы</b>: удар молнией по цели в зоне шторма. При провале  с уроном 6d6 по торсу и шансом <b><i>поджечь</i></b> 50%. Защита через <i>(Уклонение)</i> или <i>(Атлетику)</i>.   *<b>Под землей</b>: удар спавном сталактита или сталаг-мита с 5d6 урона. Вырост остается как укрытие с 20ПБ. Защита через <i>(Уклонение)</i>, <i>(Атлетику)</i>, <i>(Блок)</i>.   *<b>Лес</b>: либо спавн клубка корней с основой <i>(Борьбы)</i> равной 16 или базе <i>(Сотворения заклинаний)</i>, который не ходит, но бьет на 2м. Либо управление одним клубком чтобы захватить, обездвижить, удушить, обезаружить или сбить с ног цель.   *<b>Болото</b>: спавн облака болотного газа радиусом 6м. Цели получают <b><i>болезнь</i></b> при контакте (защита через <i>(Стойкость)</i>). Облако висит до конца инвокации. При контакте с огнем облако взрывается с 5d6 урона в торс и шансом 75% <b><i>загореться</i></b>.   *<b>Берег</b>: спавн смерча на воде, из которого вырываются 2 струи, бьющие на 20м с уроном 3d6 и отбрасыванием на 6м. Защита через <i>(Уклонение)</i> или <i>(Атлетику)</i>.   *<b>Пустыня</b>: спавн облака горящих песков радиусом 6м на 1 раунд. Цели при контакте получают <b><i>ослепление</i></b> на 5 раундов и <b><i>горение</i></b>. Защита через <i>(Уклонение)</i> или <i>(Атлетику)</i>.   *<b>Тундра</b>: заковать одну цель в лёд (защита через <i>(Уклонение)</i>, <i>(Атлетику)</i>, <i>(Сопротивление магии)</i>). Лёд блокирует любые действия цели. Снять эффект можно через проверку (Силы) со Сл:16 или если союзник нанесёт льду 10ПЗ урона (или 5ПЗ урона огнем), но лишний урон пойдет в закованную цель.$$, 'magic.form.zone_centered', $$$$, $$60$$, $$14$$, $$6$$, $$$$, $$$$, '', $$СЛ от ведущего$$, $$$$),
  ('MS205', 'exp_toc', 'magic.gruid_invocations', 'level.arch_druid', $$Блеобхерис$$, $$Bleobheris$$, $$Тень Блеобхериса$$, $$Shade of Bleobheris$$, $$Призывает великий дуб, успокаи-вающий сердца. - Звери становятся послушными. - Бойцы теряют инициативу и не могут совершать враждебные действия. - Подавляет предубеждения, отменяя штрафы от Социального положения или чудовищного наследия. - Цель может на каждом ходу пройти проверку <i>(Защиты)</i>. - Инвокация перестает действо-вать на цель после атаки.$$, 'magic.form.zone_circle', $$$$, $$50$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.day', $$Сопротивление магии$$, $$$$),
  ('MS206', 'exp_toc', 'magic.gruid_invocations', 'level.arch_druid', $$Гвин Гарн$$, $$Gwyn Gern$$, $$Ветры тайги$$, $$Winds of the Taiga$$, $$Создает метель, ослепляющую всех в области и создающую условия Снега и Льда. - Выбранные вами союзники не ослеплены, невосприимчивы к снегу и льду, а их атаки имеют 50% шанс <b><i>заморозить</i></b>.  <b><i>Заморозка</i></b>: тело цели плохо слушается, а одежда покрывается наледью. Цель получает штраф -3 к <b>Скор</b> и -1 к <b>РЕА</b>. Лёд можно сломать, пройдя проверку <i>(Силы)</i> со <b>Сл:16</b> и потратив действие​​.$$, 'magic.form.zone_circle', $$$$, $$1600$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.hour', $$$$, $$$$),
  ('MS207', 'exp_toc', 'magic.gruid_invocations', 'level.arch_druid', $$Каэд Мырквид$$, $$Caed Myrkvyd$$, $$Святилище Чёрной рощи$$, $$Sanctuary of the Black Grove$$, $$Настраивает вас и (опционально) союзников на деревья в зоне действия. - Прикоснувшись к дереву, вы видите сквозь все деревья в этой области и можете сосредоточиться на конкретном месте. - Вы и ваши союзники можете проходить через любое дерево в этой области и выходить из другого дерева. - Союзники не могут видеть сквозь деревья, но могут указывать, куда хотят идти.$$, 'magic.form.zone_circle', $$$$, $$1600$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.hour', $$$$, $$$$),
  ('MS208', 'exp_toc', 'magic.gruid_invocations', 'level.arch_druid', $$Клан Ворона$$, $$Raven Clan$$, $$Заговор Матери$$, $$Conspiracy of the Mother$$, $$<align="left">Призывает до 10 воронов, если они есть на расстоянии до полумили. - Пока вороны не дальше 20м:   * У вас есть телепатическая связь с воронами.    * Вороны разумны и верны приказам.    * Некоторые параметры и навыки получают усиление.    * Касаясь воронов, можно смотреть их воспоминания.    * Вороны всегда знают где вы</align>$$, 'magic.form.zone_centered', $$$$, $$20$$, $$16$$, $$$$, $$$$, $$$$, '', $$$$, $$$$),
  ('MS209', 'exp_toc', 'magic.gruid_invocations', 'level.arch_druid', $$Гединейт$$, $$Gedynneith$$, $$Колодец знаний$$, $$Well of Knowledge$$, $$На тронутом дереве отрастает миска со светящейся волшебной водой. - 20 человек, которые пьют из миски, получают на один день:   * бонус +4 к <i>(Инициативе)</i> и <i>(Вниманию)</i>   * невосприимчивость к <b><i>Ошеломлению</i></b>, <b><i>Дезориентации</i></b> и <b><i>Галлюцинациям</i></b>   * видят сквозь иллюзии.   * если Порог Энергии был выше нуля, он получат бонус +4, но <i>(Сопротивление Магии)</i> получает штраф -4. - После окончания заклинания дерево возвращается в нормальное состояние, оставляя выгравированный символ друида. - Эффект можно получить не чаще раза в неделю.$$, 'magic.form.direct', $$2$$, $$$$, $$16$$, $$$$, $$$$, $$1$$, 'time.unit.day', $$$$, $$$$)
),
invocation_effects_en (ms_id, text_en) AS (
  VALUES
    ('MS066', 'Cast on an animal or mindless monster, causing it to become enraged toward a designated target and attempt to attack that target.'),
    ('MS067', 'Afflicts the target with a sickness. Depending on the STA invested, the target suffers:
* 2: the target coughs and becomes Stunned (–2 to Attack and Defense) until their next turn.
* 4: the target is Dazed from severe nausea. They cannot take actions, and attacks against them have DC 10. The effect ends if the target is hit.
* 6: the target is effectively Poisoned (takes 3 damage per turn).
- To remove the sickness effects, the target may roll (Endurance) vs your (Invocation Casting) check.'),
    ('MS068', 'Calms one animal.
- You may cast it on yourself; then the druid gains +3 to (Wilderness Survival) when interacting with animals.'),
    ('MS069', 'Grows edible plants in any soil.
- The plants are enough to feed, for 1 day, a number of people equal to the STA spent.'),
    ('MS070', 'Allows you to see creatures unnatural to the world of Neverland.
- You can see monsters even through obstacles.
- Monsters seen this way appear to glow.'),
    ('MS071', 'Covers a chosen area with vegetation and natural decor, hiding its contents with invisibility.
- Grants +5 to (Stealth) but prevents anyone from moving.
- The camouflage can be removed by:
  * repeating the invocation, or
  * dealing 10 damage to the branches.'),
    ('MS072', 'Choose one effect:
- Restores 3 HP per turn.
- Heals critical wounds: you must cast the blessing [wound level]*2+2 times at DC [wound level]*2+12, then rest to remove wound penalties (at least one day):
  1) Light: DC 8 — BODY days
  2) Moderate: DC 12 — BODY days
  3) Severe: DC 15 — BODY days
  4) Mortal: penalty is permanent'),
    ('MS073', 'Awakens the target''s primal reserves.
- +2 to melee damage
- -2 to INT for the duration.'),
    ('MS074', 'Reveals the threads of life that connect all living things.
- You can see the remaining HP of visible targets.
- You can also see critical wounds.'),
    ('MS075', 'Allows you to turn a small tree into a golem.
- The golem serves the druid for the duration, then turns back into a tree.
- A destroyed golem yields only 2d10 units of wood.
- Otherwise, the wooden golem is the same as a normal golem.'),
    ('MS076', 'Changes the weather around the caster. Possible weather:
* Clear: no modifiers
* Cloudy: little sunlight:
  - +2 to DCs of skill checks
  - -3 to (Awareness)
* Downpour: extinguishes fire
* Storm winds: -2 DEX for ranged attacks
* Thunderstorm: each turn, with a 35% chance, lightning strikes each target (except the caster). A target may dodge; on a hit it deals 8d6 damage with a 75% chance to set the target on fire.
(Burning rules: 5 damage to each body part each round; armor absorbs damage but loses 1 SP; it takes a turn to put out the flames.)'),
    ('MS077', 'Grants the target Luck points.
- The number of points equals half the points you rolled above DC 12 on your (Invocation Casting) check.
- Maximum: 5 Luck points.'),
    ('MS078', 'Grants the target +3 to (Charisma) and (Seduction).'),
    ('MS079', 'Light emanates from the priest''s hands, increasing the light level in the area by 1.
Lighting effects:
- DC modifier to skill checks:
  * Darkness: +5
  * Dim light: +2
  * Daylight: none
  * Bright light: +2
- Awareness modifier:
  * Darkness: -4
  * Dim light: -2
  * Daylight: none
  * Bright light: -3
- Attack and Defense modifier:
  * Darkness: -2
  * Bright light: -2 if looking toward the light source'),
    ('MS080', 'Allows the caster to enter the vaults of their own mind, reliving any memory or recalling any knowledge they have ever learned.'),
    ('MS081', 'Removes the Drunk condition.
A Drunk target suffers:
* -2 to REF, DEX, and INT
* -3 to (Verbal Combat)
* 25% chance to forget what they did while drunk'),
    ('MS082', 'Sows confusion in the target''s mind, making them doubt any information and even their own memories.
- The target is Dazed: they cannot take actions, and attacks against them have DC 10. The effect ends if the target is hit.
- To end the effect on their turn, the target must roll 1d10 under their INT.'),
    ('MS083', 'Ignites the target.
- Deals one-time damage.
- 100% chance to set the target on fire.'),
    ('MS084', 'Strengthens the target''s will, allowing them to make a new check against the effects of any spell currently affecting them.'),
    ('MS085', 'Creates a bright white light that forces one target to speak the truth.
- Each round, the target makes a new Defense check.
- On a failure, the target must answer truthfully to any question asked.'),
    ('MS086', 'Creates a portal for 1 round.
- Size: 1×2 meters
- Range: unlimited
- Any items can be carried through
- If the portal shuts while something is passing through, it inflicts the critical wound "lost limb" or cuts an item in half
- Destination: familiar places only.'),
    ('MS087', 'Allows you to receive an answer to one question. It cannot be a prediction of the future.
- The GM sets the DC based on how secret the information is.'),
    ('MS088', 'Cuts the bonds of life that tie the target to this world.
- The target becomes Dying, as if they had taken lethal damage.
- Allies may save the target with (First Aid) or Healing Touch at DC 16, restoring HP to its previous state.
Dying: all stats are reduced to 1/3 of normal. Each round the target rolls 1d10 under their base WILL; on a failure they die permanently.'),
    ('MS089', 'Sets the target on fire with bright white flame with red flashes.
- Deals double Burning damage.
- Can only be extinguished by magic or by being fully submerged in water for 3 rounds.
- Anything that touches this flame suffers normal Burning.
(Burning rules: 5 damage to each body part each round; armor absorbs damage but loses 1 SP; it takes a turn to put out the flames.)'),
    ('MS090', 'Calls the spirit of Freya into the priest''s body, creating a glow around them.
- Targets become immune to Fear.
- Targets gain +25 HP.
- If a target leaves the invocation''s area, the effect persists for 1d6 rounds unless they return to the area.
- Faith in Freya is not required.
- The priest may end the effect on targets at will.'),
    ('MS091', 'Puts targets into a deep coma during which their wounds heal.
- Number of targets equals the caster''s (Spell Casting) skill value.
- Targets do not react to anything.
- After the coma, targets wake with full HP.
- All temporary residual penalties from critical wounds healed before the invocation are removed.'),
    ('MS092', 'Allows you to spend Luck points as Kreve''s blessing.
- During the invocation, the Luck points spent at the start apply to any roll the priest makes.
- At the priest''s choice, the effect can be a bonus or a penalty to any roll within the area.
- Maximum Luck invested is limited to 3× the priest''s (Spell Casting) skill value.'),
    ('MS093', 'An aura of the White Flame appears around the priest.
- Lighting becomes Bright Light, so everyone suffers:
  * +2 to DCs of skill checks
  * -3 to (Awareness)
  * -2 to (Attack) and (Defense) if looking toward the priest
- Melts any ice
- Dispels any water spells
- New water spells can only be created if they win an opposed (Spell Casting) check
- Creatures vulnerable to the Sun suffer double vulnerability effects.'),
    ('MS177', 'Creates a glowing rune on a melee weapon, making it lighter and better balanced.
- The weapon gains the Balanced effect for the duration.'),
    ('MS178', 'Blesses the target with divine insight.
- Grants +2 to one skill.
- The target must have at least 1 rank in that skill.
- Can be used once per day.'),
    ('MS179', 'Wraps the target''s mind in false thoughts that conceal the truth.
- The target gains +2 to (Deceit) checks.
- The target cannot be forced to speak the truth by magic.
- You cannot spy on the target via telepathy.
- Magical investigation does not reveal the spell unless someone succeeds on an (Awareness) check at DC 20.'),
    ('MS180', 'Calls upon divine power, draining the target''s will to resist.
- The target loses 2d6 STA.
- If the target''s Energy Threshold is 1 or higher, they lose an additional 2d6 STA.'),
    ('MS181', 'Calls an aspect of a deity, making the priest''s posture imposing.
- Your voice can be heard from far away.
- +4 to (Intimidation).
- Alternatively, you may make up to 6 targets within 10 m immune to fear.'),
    ('MS182', 'Grants divine providence.
- The first time you loot anything, you (or the GM) roll twice and take the higher result.
- This applies to each item found from the same source (e.g., when looting a monster, roll twice for each loot entry).'),
    ('MS183', 'Marks the target with a symbol of decay, preventing healing.
- The target cannot regain HP from any source, including natural healing.'),
    ('MS184', 'Reacts to attempts to scry you or anyone within 10 m of you.
- Triggers on Hydromancy, Pyromancy, etc.
- You learn that you were targeted and you see the scryer''s face.
- For the scryer to realize they were caught, they must succeed on an (Education) or (Magical Knowledge) check at DC 20.
- You are notified if the scryer realizes they were caught.'),
    ('MS185', 'Enters the target''s mind and alters their memories. Effect depends on STA invested:
* 14: causes retrograde amnesia, blocking memories
* 16: allows you to implant fabricated memories
The target gains +5 to Defense if they have not previously been subjected to memory blocking.'),
    ('MS186', 'Hurls a magical spear of golden light.
- The spear travels in a straight line through multiple targets; each takes the same damage if they fail their Defense.
- Demons also take damage and are forced out of the body.
- Demons hit by this invocation cannot use abilities in the next round.'),
    ('MS187', 'Summons up to 10 wild horses and empowers them with magic.
- The horses remain for the next hour.
- For each horse, choose one of two variants: "Horse" or "Warhorse".
- Warhorse tack does not improve SP.
- Chosen creatures gain +5 to (Riding) when using these horses.'),
    ('MS188', 'Transforms you into a Giant Bear.
- All items merge into the form.
- You gain the Giant Bear''s abilities, but lose professional abilities and magic.
- Your stats and skills are replaced by the Giant Bear''s values if they are higher than yours.
- Damage and critical wounds carry over both ways when changing form. HP does not drop below 1.'),
    ('MS189', 'Marks the target with river water, making them your champion.
- The champion gains resistance to all sources of damage: damage that gets past armor is halved.
- The champion gains +5 to all rolls.'),
    ('MS190', 'Blesses a feast with revitalizing effects. Requirements:
1) The feast must be for at least 20 people (no upper limit).
2) The feast must be cooked from local products.
Those who eat gain:
* 6d6 HP restored
* Immediate treatment of all critical wounds (post-treatment penalties still remain until healed)
* Removal of Poison
* Cure of Diseases'),
    ('MS191', 'Commands the target''s body to heal itself.
- The target fully heals one critical wound (including post-treatment penalties), or removes the penalty from an already treated wound.
- The wound is treated as if it had never happened (including regrowing limbs, eyes, etc.).'),
    ('MS192', 'Fills your mind with flashes of possible futures.
- Roll 1d10 until you get 3 results that are not 1 or 10; write those numbers down.
- During the day, if someone near you rolls one of those numbers on a d10, you may replace their result with a critical failure without spending an action.'),
    ('MS193', 'Blocks the connection to elemental planes, negating all magic.
- Only targets chosen by you (or those who successfully resisted this invocation) can use any form of magic.
- This invocation cannot be dispelled.'),
    ('MS194', 'Marks up to 5 people with a blessing of moonlight.
- They do not suffer penalties from having HP below the Wound Threshold (i.e., their REF/DEX/INT/WILL are not halved) or from the Dying state (i.e., their stats are not reduced to 1/3).
- The first time their HP would drop below 1, it instead remains at 1.
- Lasts until the next dawn.'),
    ('MS195', 'Allows you to brew an elixir from plant ingredients.
- If all ingredients in the elixir are plants, you may roll (Spell Casting) instead of (Alchemy) when crafting and recovering it.'),
    ('MS196', 'Effect depends on what the druid marks:
- Spoiled food or drink becomes fresh and tasty again.
- Feed crops become twice as filling.
- Soil becomes twice as productive in one of two ways:
  * plants grow twice as large, or
  * crops ripen twice as fast.'),
    ('MS197', 'Makes your words inspirational.
- You gain +4 to (Leadership).
- Choose one:
  * Whisper to one target within 10 meters.
  * Make your voice loud enough to be heard from afar.'),
    ('MS198', 'Allows you to control a tree''s roots.
- Roots have 15 HP.
- You may change the roots'' attack target.
Root actions:
* Grab: the target cannot move and suffers -2 to all physical actions. Escape by rolling Defense once per turn vs a new (Spell Casting) check, or by cutting the roots.
* Trip: knocks the target prone; they suffer -2 to Attack and Defense until they spend movement to stand.
* Disarm: knocks the target''s weapon away by (1d6)/2 meters, or (at -3 to Spell Casting) grab it with a root (but not use it).'),
    ('MS199', 'Summons the nearest wild animal.
- The beast arrives in 1d6 rounds.
- The beast is friendly and helps as long as the druid does not betray or abuse it.
- When the invocation ends, the beast becomes neutral.
- Can be used once per day in the same area.
- The summoned beast depends on STA invested:
  * 2: small (bird, snake, cat…)
  * 5: medium (wolf, panther, boar…)
  * 10: large (bear, bull, horse…)'),
    ('MS200', 'Infects food and drinks with bacteria.
* 5: the consumer gets Disease for 1 hour.
* 10: the consumer gets Poisoned.
Disease: -2 to all actions, -25% STA; the GM may call for Endurance checks at DC 14 (on failure: Nausea). Cured by Healing Touch at DC 15 and a night''s rest.
Nausea: every 3 rounds, if a 1d10 roll is not under BODY, you lose your turn from vomiting.
Poisoned: the target takes 3 damage per turn. To remove, roll Endurance at DC 15, spending an action.'),
    ('MS201', 'Grants the target immense strength.
- Melee attacks cannot be parried.
- Melee attacks deal double destructive damage to weapons, shields, and armor.
- The target is immune to effects that knock them prone.'),
    ('MS202', 'Marks up to 6 targets as either "safe" or "prey".
* "Safe" targets appear friendly to beasts and monsters become neutral.
* "Prey" targets are perceived by predators and monsters as a target to attack.
- The marks look identical, but can be distinguished with an (Education) check at DC 16.'),
    ('MS203', 'By sacrificing an oak-and-mistletoe staff, you create a 2-meter totem with 20 HP that links you to nature.
- Grants +4 Focus, reducing spell STA costs by 4.
- Removes the Focus bonus from the Oak & Mistletoe ritual.
- All animals are friendly.
- Additional bonus depends on terrain.
- You can have only one active totem; creating a new one turns the old one into a normal plant.
Additional terrain bonuses:
* Plains: the druid''s SPD becomes 12.
* Mountains: resistance to bludgeoning, slashing, and piercing.
* Underground: improved night vision and +4 to (Stealth).
* Forest: regeneration 3 HP/turn for 1 hour when consuming a fruit from the totem area.
* Swamp: immunity to Poison and Disease.
* Coast: can breathe underwater and no penalties for combat/maneuvers in water.
* Desert: immunity to heat, always able to find water; once per day fully restore STA by drinking water from the totem area.
* Tundra: immunity to snow/ice conditions; immunity to Burning and Freezing; resistance to fire and ice damage.'),
    ('MS204', 'Nature rages around the druid.
- Requires a full-round action each round.
- The target always rolls Defense against the druid''s (Spell Casting) check.
- The effect depends on terrain:
* Plains: either spawn one tornado within 10 m, or direct an existing one within the storm. The tornado (4 m cube) moves 10 m/turn, deals 4d6 torso damage, and pushes the target 10 m (GM chooses direction) on contact and failed defense via (Athletics).
* Mountains: lightning strike on a target in the storm. On failure: 6d6 torso damage and 50% chance to ignite. Defense via (Dodge/Escape) or (Athletics).
* Underground: spawn a stalactite/stalagmite strike for 5d6 damage; the growth remains as cover with 20 SP. Defense via (Dodge/Escape), (Athletics), or (Block).
* Forest: either spawn a root mass with (Brawling) base 16 or your (Spell Casting) base; it doesn''t move but attacks within 2 m. Or control one root mass to grab, immobilize, suffocate, disarm, or trip a target.
* Swamp: spawn a swamp-gas cloud (radius 6 m). Targets get Disease on contact (defense via (Endurance)). The cloud lasts until the invocation ends. If it contacts fire, it explodes for 5d6 torso damage and 75% chance to ignite.
* Coast: spawn a waterspout with two jets, range 20 m, dealing 3d6 damage and pushing 6 m. Defense via (Dodge/Escape) or (Athletics).
* Desert: spawn a cloud of burning sands (radius 6 m) for 1 round. Targets on contact are Blinded for 5 rounds and suffer Burning. Defense via (Dodge/Escape), (Athletics), or (Resist Magic)).
* Tundra: encase one target in ice (defense via (Dodge/Escape), (Athletics), or (Resist Magic)). Ice prevents actions. Remove with Strength check DC 16, or if an ally deals 10 damage to the ice (or 5 fire damage); excess damage carries into the target.'),
    ('MS205', 'Summons a great oak that calms hearts.
- Beasts become obedient.
- Combatants lose initiative and cannot take hostile actions.
- Suppresses prejudice, removing penalties from Social Standing or monstrous heritage.
- Each turn a target may make a Defense check.
- The invocation stops affecting a target after they attack.'),
    ('MS206', 'Creates a blizzard that blinds everyone in the area and creates Snow and Ice conditions.
- Chosen allies are not blinded, are immune to snow and ice, and their attacks have a 50% chance to freeze.
Freezing: the target''s body becomes sluggish and their clothing is coated with ice. The target suffers -3 SPD and -1 REF. The ice can be broken with a Strength check at DC 16, spending an action.'),
    ('MS207', 'Attunes you (and optionally allies) to the trees in the area.
- By touching a tree, you can see through all trees in the area and focus on a specific spot.
- You and your allies can pass through any tree in the area and emerge from another tree.
- Allies cannot see through trees, but can indicate where they want to go.'),
    ('MS208', 'Summons up to 10 ravens (if any are within half a mile).
While the ravens are within 20 m:
* you have a telepathic link with them
* they are intelligent and obey commands
* some stats and skills are enhanced
* by touching a raven you can view its memories
* the ravens always know where you are'),
    ('MS209', 'A bowl with glowing magical water grows from a touched tree.
- Up to 20 people who drink from it gain for one day:
  * +4 to Initiative and Awareness
  * immunity to Stunned, Dazed, and Hallucinations
  * ability to see through illusions
  * if their Energy Threshold is above 0, it increases by +4, but (Resist Magic) suffers -4
- After the spell ends, the tree returns to normal, leaving an engraved druidic symbol.
- A creature can benefit from this no more than once per week.')
),
ins_i18n AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    -- cult/circle
    SELECT ck_id('witcher_cc.magic.invocation.cult_or_circle.'||rd.ms_id),
           'magic',
           'invocation_cults',
           'ru',
           rd.cult_ru
      FROM raw_data rd
     WHERE nullif(rd.cult_ru,'') IS NOT NULL
    UNION ALL
    SELECT ck_id('witcher_cc.magic.invocation.cult_or_circle.'||rd.ms_id),
           'magic',
           'invocation_cults',
           'en',
           rd.cult_en
      FROM raw_data rd
     WHERE nullif(rd.cult_en,'') IS NOT NULL
    UNION ALL
    -- names
    SELECT ck_id('witcher_cc.magic.invocation.name.'||rd.ms_id),
           'magic',
           'invocation_names',
           'ru',
           rd.name_ru
      FROM raw_data rd
     WHERE nullif(rd.name_ru,'') IS NOT NULL
    UNION ALL
    SELECT ck_id('witcher_cc.magic.invocation.name.'||rd.ms_id),
           'magic',
           'invocation_names',
           'en',
           rd.name_en
      FROM raw_data rd
     WHERE nullif(rd.name_en,'') IS NOT NULL
    UNION ALL
    -- effects (effect_1 + effect_2 already merged; HTML stripped)
    SELECT ck_id('witcher_cc.magic.invocation.effect.'||rd.ms_id),
           'magic',
           'invocation_effects',
           'ru',
           regexp_replace(replace(replace(rd.effect_ru, chr(11), E'\n'), chr(13), ''), '<[^>]+>', '', 'g')
      FROM raw_data rd
     WHERE nullif(rd.effect_ru,'') IS NOT NULL
    UNION ALL
    -- effects EN (from manual translations)
    SELECT ck_id('witcher_cc.magic.invocation.effect.'||iee.ms_id),
           'magic',
           'invocation_effects',
           'en',
           iee.text_en
      FROM invocation_effects_en iee
  ) foo
  ON CONFLICT (id, lang) DO UPDATE
    SET text = EXCLUDED.text
)
INSERT INTO wcc_magic_invocations (
  ms_id, dlc_dlc_id,
  magic_type_id,
  level_id, form_id, effect_time_unit_id,
  cult_or_circle_id,
  name_id, effect_id,
  distance, zone_size,
  stamina_cast, stamina_keeping,
  damage,
  effect_time_value,
  defense_ids
)
SELECT rd.ms_id
     , rd.dlc_dlc_id
     , ck_id(rd.magic_type_key) AS magic_type_id
     , CASE WHEN nullif(rd.level_key,'') IS NOT NULL THEN ck_id(rd.level_key) ELSE NULL END AS level_id
     , CASE WHEN nullif(rd.form_key,'') IS NOT NULL THEN ck_id(rd.form_key) ELSE NULL END AS form_id
     , CASE
         WHEN rd.effect_time_unit_key LIKE 'time.unit.%' THEN ck_id(rd.effect_time_unit_key)
         ELSE NULL
       END AS effect_time_unit_id
     , CASE WHEN nullif(rd.cult_ru,'') IS NOT NULL OR nullif(rd.cult_en,'') IS NOT NULL THEN ck_id('witcher_cc.magic.invocation.cult_or_circle.'||rd.ms_id) ELSE NULL END AS cult_or_circle_id
     , ck_id('witcher_cc.magic.invocation.name.'||rd.ms_id) AS name_id
     , ck_id('witcher_cc.magic.invocation.effect.'||rd.ms_id) AS effect_id
     , nullif(rd.distance,'')
     , nullif(rd.zone_size,'')
     , nullif(rd.stamina_cast,'')
     , nullif(rd.stamina_keeping,'')
     , nullif(rd.damage,'')
     , CASE
         WHEN nullif(rd.effect_time_value,'') IS NULL THEN NULL
         WHEN rd.effect_time_unit_key LIKE 'time.unit.%' THEN nullif(rd.effect_time_value,'')
         WHEN nullif(rd.effect_time_unit_key,'') IS NOT NULL THEN (nullif(rd.effect_time_value,'') || ' ' || rd.effect_time_unit_key)
         ELSE nullif(rd.effect_time_value,'')
       END AS effect_time_value
     , ARRAY_REMOVE(ARRAY[
         CASE btrim(rd.defense_1)
           WHEN 'Уклонение' THEN ck_id('skill.dodge')
           WHEN 'Атлетика' THEN ck_id('skill.athletics')
           WHEN 'Блокирование' THEN ck_id('skill.blocking')
           WHEN 'Сопротивление магии' THEN ck_id('skill.resist_magic')
           WHEN 'Сопротивление убеждению' THEN ck_id('skill.resist_coercion')
           WHEN 'СЛ от ведущего' THEN ck_id('skill.gm_dc')
           WHEN 'Воля*3' THEN ck_id('parameter.will_x3')
           ELSE NULL
         END,
         CASE btrim(rd.defense_2)
           WHEN 'Уклонение' THEN ck_id('skill.dodge')
           WHEN 'Атлетика' THEN ck_id('skill.athletics')
           WHEN 'Блокирование' THEN ck_id('skill.blocking')
           WHEN 'Сопротивление магии' THEN ck_id('skill.resist_magic')
           WHEN 'Сопротивление убеждению' THEN ck_id('skill.resist_coercion')
           WHEN 'СЛ от ведущего' THEN ck_id('skill.gm_dc')
           WHEN 'Воля*3' THEN ck_id('parameter.will_x3')
           ELSE NULL
         END
       ], NULL)::uuid[] AS defense_ids
  FROM raw_data rd
ON CONFLICT (ms_id) DO UPDATE
SET
  dlc_dlc_id = EXCLUDED.dlc_dlc_id,
  magic_type_id = EXCLUDED.magic_type_id,
  level_id = EXCLUDED.level_id,
  form_id = EXCLUDED.form_id,
  effect_time_unit_id = EXCLUDED.effect_time_unit_id,
  cult_or_circle_id = EXCLUDED.cult_or_circle_id,
  name_id = EXCLUDED.name_id,
  effect_id = EXCLUDED.effect_id,
  distance = EXCLUDED.distance,
  zone_size = EXCLUDED.zone_size,
  stamina_cast = EXCLUDED.stamina_cast,
  stamina_keeping = EXCLUDED.stamina_keeping,
  damage = EXCLUDED.damage,
  effect_time_value = EXCLUDED.effect_time_value,
  defense_ids = EXCLUDED.defense_ids;
