  is_official BOOLEAN NOT NULL DEFAULT TRUE,
  name_id     uuid NOT NULL,            -- ck_id('witcher_cc.items.dlc.name.'||dlc_id)
  UNIQUE (su_su_id, dlc_id)
);

-- –õ–æ–∫–∞–ª–∏–∑–∞—Ü–∏—è (–æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ)
