INSERT INTO answer_options (an_id, su_su_id, qu_qu_id, label, sort_order, metadata)
SELECT 'wcc_race_elf'
     , meta.su_su_id
     , meta.qu_id
     , ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'_o'|| to_char(3, 'FM9900') ||'.'|| meta.entity ||'.'|| meta.entity_field) AS label
     , 3 AS sort_order
     , '{}'::jsonb AS metadata
  FROM meta;

