-- –£–∑–µ–ª: –í –∫–∞–∫–æ–π —à–∫–æ–ª–µ –≤—ã –æ–±—É—á–∞–ª–∏—Å—å?

-- –í–æ–ø—Ä–æ—Å
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_witcher_school' AS qu_id
                , 'questions' AS entity)
, ins_body AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
    SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body') AS id
         , meta.entity, 'body', v.lang, v.text
      FROM (VALUES
              ('ru', '–ì–¥–µ –ø—Ä–æ—Ö–æ–¥–∏–ª–∏ –æ–±—É—á–µ–Ω–∏–µ –∏ –∫–∞–∫–∏–µ –æ—Å–æ–±–µ–Ω–Ω–æ—Å—Ç–∏ —à–∫–æ–ª—ã –≤—ã —É–Ω–∞—Å–ª–µ–¥–æ–≤–∞–ª–∏?'),
              ('en', 'Where did you train and what traits of the school did you inherit?')
           ) AS v(lang, text)
      CROSS JOIN meta
  )
, c_vals(lang, num, text) AS (
    VALUES
      ('ru', 1, '–®–∞–Ω—Å'),
      ('ru', 2, '–≠—Ñ—Ñ–µ–∫—Ç'),
      ('ru', 3, '–®–∫–æ–ª–∞'),
      ('en', 1, 'Chance'),
      ('en', 2, 'Effect'),
      ('en', 3, 'School')
)
, ins_c   AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
    SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| to_char(c_vals.num, 'FM9900') ||'.'|| meta.entity ||'.'|| 'column_name') AS id
         , meta.entity, 'column_name', c_vals.lang, c_vals.text
      FROM c_vals
      CROSS JOIN meta
)
INSERT INTO questions (qu_id, su_su_id, title, body, qtype, metadata)
SELECT meta.qu_id
     , meta.su_su_id
     , NULL
     , ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body')
     , 'single_table'
     , jsonb_build_object(
         'dice', 'd_weighed',
         'columns', (
           SELECT jsonb_agg(ck_id('witcher_cc' ||'.'|| 'wcc_witcher_school' ||'.'|| to_char(num, 'FM9900') ||'.'|| 'questions' ||'.'|| 'column_name')::text ORDER BY num)
           FROM (SELECT DISTINCT num FROM c_vals) AS cols
         ),
         'path', jsonb_build_array(
           ck_id('witcher_cc.hierarchy.witcher')::text,
           ck_id('witcher_cc.hierarchy.witcher_school')::text
         )
       )
    FROM meta;

-- –û—Ç–≤–µ—Ç—ã
WITH
raw_data AS (
  SELECT 'ru' AS lang, raw_data_ru.*
    FROM (VALUES
      (1, 0.2, '–ù–µ—Ç —à—Ç—Ä–∞—Ñ–æ–≤ –ø—Ä–∏ —Å–∏–ª—å–Ω–æ–π –∞—Ç–∞–∫–µ', '<b><span style="color: #808080">–®–∫–æ–ª–∞ –í–æ–ª–∫–∞</span></b><br>–í—ã —É—á–∏–ª–∏—Å—å –≤ –ö–∞—ç—Ä –ú–æ—Ä—Ö–µ–Ω–µ –≤ –≤—ã—Å–æ–∫–∏—Ö –°–∏–Ω–∏—Ö –≥–æ—Ä–∞—Ö. –û–±—É—á–µ–Ω–∏–µ –±—ã–ª–æ —Ç—Ä—É–¥–Ω—ã–º –∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω—ã–º, –æ—Å–Ω–æ–≤–∞–Ω–Ω—ã–º –Ω–∞ —Å–±–∞–ª–∞–Ω—Å–∏—Ä–æ–≤–∞–Ω–Ω–æ–º –ø–æ–¥—Ö–æ–¥–µ –∫ –≤–µ–¥—å–º–∞—á—å–µ–π –ø—Ä–æ—Ñ–µ—Å—Å–∏–∏. –í–∞—Å —É—á–∏–ª–∏ –±–∏—Ç—å —Å–∏–ª—å–Ω–æ –∏ –±—ã—Å—Ç—Ä–æ, —á—Ç–æ–±—ã –∫–∞–∫ –º–æ–∂–Ω–æ —Å–∫–æ—Ä–µ–µ –∑–∞–∫–æ–Ω—á–∏—Ç—å –æ—Ö–æ—Ç—É.'),
      (2, 0.2, '+2 –∫ –≠–Ω–µ—Ä–≥–∏–∏', '<b><span style="color: #4A9BD6">–®–∫–æ–ª–∞ –ì—Ä–∏—Ñ–æ–Ω–∞</span></b><br>–í–∞—Å –æ–±—É—á–∞–ª–∏ –≤ –ö–∞—ç—Ä –ò –°–µ—Ä–µ–Ω –Ω–∞ –ø–æ–±–µ—Ä–µ–∂—å–µ —É –î—Ä–∞–∫–æ–Ω—å–∏—Ö –≥–æ—Ä. –û–±—É—á–µ–Ω–∏–µ –±—ã–ª–æ —Å–æ—Å—Ä–µ–¥–æ—Ç–æ—á–µ–Ω–æ –Ω–∞ —Å—Ä–∞–∂–µ–Ω–∏–∏ —Å –Ω–µ—Å–∫–æ–ª—å–∫–∏–º–∏ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞–º–∏ –æ–¥–Ω–æ–≤—Ä–µ–º–µ–Ω–Ω–æ –∏ –Ω–∞ –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–º –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–∏ –≤–∞—à–∏—Ö –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–Ω—ã—Ö –º–∞–≥–∏—á–µ—Å–∫–∏—Ö —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–µ–π.'),
      (3, 0.2, '–ù–µ–≤–æ—Å–ø—Ä–∏–∏–º—á–∏–≤–æ—Å—Ç—å –∫–æ –≤—Å–µ–º –Ω–µ–º–∞–≥–∏—á–µ—Å–∫–∏–º –ø–æ–ø—ã—Ç–∫–∞–º –æ–±–æ–ª—å—â–µ–Ω–∏—è', '<b><span style="color: #E85A7F">–®–∫–æ–ª–∞ –ö–æ—Ç–∞</span></b><br>–í—ã –æ–±—É—á–∞–ª–∏—Å—å –≤ –∫–∞—Ä–∞–≤–∞–Ω–µ –î–∏–Ω –ú–∞—Ä–≤ ‚Äî —Å—Ç—Ä–∞–Ω—Å—Ç–≤—É—é—â–µ–º –æ—Ç—Ä—è–¥–µ –≤–µ–¥—å–º–∞–∫–æ–≤, –≥–æ—Ç–æ–≤—ã—Ö —Ä–∞–±–æ—Ç–∞—Ç—å –Ω–∞ –ª—é–±–æ–≥–æ, –∫—Ç–æ –∑–∞–ø–ª–∞—Ç–∏—Ç. –ú—É—Ç–∞—Ü–∏–∏ –∏ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∫–∏ –∏—Å–∫–∞–ª–µ—á–∏–ª–∏ –≤–∞—à—É –ø—Å–∏—Ö–∏–∫—É, –∏ –≤–∞–º –ø—Ä–∏—Ö–æ–¥–∏—Ç—Å—è —Å–¥–µ—Ä–∂–∏–≤–∞—Ç—å –∂–µ—Å—Ç–æ–∫–æ—Å—Ç—å –∏ —Ç—è–≥—É –∫ –Ω–∞—Å–∏–ª–∏—é.'),
      (4, 0.2, '–ù–µ—Ç —à—Ç—Ä–∞—Ñ–æ–≤ –∑–∞ –ø–∞—Ä–Ω–æ–µ –æ—Ä—É–∂–∏–µ', '<b><span style="color: #4EC04E">–®–∫–æ–ª–∞ –ó–º–µ–∏</span></b><br>–í—ã —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∞–ª–∏—Å—å –≤ –ì–æ—Ä—Ç–≥—É—Ä –ì–≤–µ–¥ –≤ –≥–ª—É–±–∏–Ω–Ω—ã—Ö –ø–µ—â–µ—Ä–∞—Ö —Ö—Ä–µ–±—Ç–∞ –¢–∏—Ä –¢–æ—Ö–∞–∏—Ä. –í –æ—Ç–ª–∏—á–∏–µ –æ—Ç –ø—Ä–æ—á–∏—Ö –≤–µ–¥—å–º–∞–∫–æ–≤, –≤—ã –æ–±—É—á–µ–Ω—ã –≤–ª–∞–¥–µ–Ω–∏—é –¥–≤—É–º—è –∫–ª–∏–Ω–∫–∞–º–∏ –∏ —É–±–∏–π—Å—Ç–≤—É —á—É–¥–æ–≤–∏—â –º–µ—Ç–æ–¥–∞–º–∏ –Ω–∞—ë–º–Ω—ã—Ö —É–±–∏–π—Ü.'),
      (5, 0.2, '-2 –∫ –°–∫–æ–≤–∞–Ω–Ω–æ—Å—Ç–∏ –î–≤–∏–∂–µ–Ω–∏–π', '<b><span style="color: #A67C00">–®–∫–æ–ª–∞ –ú–µ–¥–≤–µ–¥—è</span></b><br>–í—ã –ø—Ä–æ—Ö–æ–¥–∏–ª–∏ –æ–±—É—á–µ–Ω–∏–µ –Ω–∞ –∑–∞—Å–Ω–µ–∂–µ–Ω–Ω—ã—Ö –≤–µ—Ä—à–∏–Ω–∞—Ö –≥–æ—Ä –ê–º–µ–ª–ª, –≤ –•—ç—Ä–Ω –ö–∞–¥—É—Ö. –í—ã –∑–∞–∫–∞–ª–∏–ª–∏ —Ç–µ–ª–æ, —á—Ç–æ–±—ã –≤—ã–¥–µ—Ä–∂–∏–≤–∞—Ç—å –ª—é–±—ã–µ –ø–æ–≤—Ä–µ–∂–¥–µ–Ω–∏—è, –∏ –Ω–∞—É—á–∏–ª–∏—Å—å –±—ã—Å—Ç—Ä–æ –∏ —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ –¥–≤–∏–≥–∞—Ç—å—Å—è –≤ —Ç—è–∂—ë–ª–æ–π —Å—Ç–∞–ª—å–Ω–æ–π –±—Ä–æ–Ω–µ.'),
      (6, 0.2, '–ù–µ —Ç—Ä–µ–±—É–µ—Ç—Å—è –¥–µ–π—Å—Ç–≤–∏–µ –¥–ª—è –∏–∑–≤–ª–µ—á–µ–Ω–∏—è/—É–±–∏—Ä–∞–Ω–∏—è —â–∏—Ç–∞, –Ω–µ—Ç —à—Ç—Ä–∞—Ñ–æ–≤ –∑–∞ –ø–∞—Ä–∏—Ä–æ–≤–∞–Ω–∏–µ —â–∏—Ç–æ–º, –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Ä—É–∫—É —Å–æ —â–∏—Ç–æ–º –¥–ª—è –∑–Ω–∞–∫–æ–≤, –±–æ–º–± –∏ —ç–ª–∏–∫—Å–∏—Ä–æ–≤.', '<b><span style="color: #FF8800">–®–∫–æ–ª–∞ –ú–∞–Ω—Ç–∏–∫–æ—Ä—ã</span></b><br>–í–µ–¥—å–º–∞–∫–∏ —à–∫–æ–ª—ã –ú–∞–Ω—Ç–∏–∫–æ—Ä—ã ‚Äî —ç—Ç–æ –≤ –ø–µ—Ä–≤—É—é –æ—á–µ—Ä–µ–¥—å –∑–∞—â–∏—Ç–Ω–∏–∫–∏, —á—Ç–æ –ø—Ä–µ–∫—Ä–∞—Å–Ω–æ –æ—Ç—Ä–∞–∂–µ–Ω–æ –≤ –∏—Ö –±–æ–µ–≤–æ–π –ø–æ–¥–≥–æ—Ç–æ–≤–∫–µ. –û–Ω–∏ –≤–µ–ª–∏–∫–æ–ª–µ–ø–Ω–æ –æ–±—É—á–µ–Ω—ã –æ–±—Ä–∞—â–µ–Ω–∏—é —Å–æ —Å–ø–µ—Ü–∏–∞–ª—å–Ω—ã–º–∏ —â–∏—Ç–∞–º–∏, –∫–æ—Ç–æ—Ä—ã–µ –¥–ª—è –Ω–∏—Ö –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç –º–∞—Å—Ç–µ—Ä-–æ—Ä—É–∂–µ–π–Ω–∏–∫ –∏–∑ –∫—Ä–µ–ø–æ—Å—Ç–∏ –ë–µ—Ö–µ–ª—å—Ç –ù–∞—Ä.'),
      (7, 0.2, '-3 –∫ –ø—Ä–æ–≤–µ—Ä–∫–µ –≤—Ä–∞–≥–∞ –Ω–∞ –∑–∞—Ö–≤–∞—Ç, +3 –∫ —Å–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—é —Ä–∞–∑–æ—Ä—É–∂–µ–Ω–∏—é –Ω–∞ 1 –º–∏–Ω—É—Ç—É –∑–∞ 1 –í—ã–Ω –∏ 1 –î–µ–π—Å—Ç–≤–∏–µ.', '<b><span style="color: #8B8B5A">–®–∫–æ–ª–∞ –£–ª–∏—Ç–∫–∏</span></b><br>–ù–µ—É–¥–∞—á–Ω—ã–µ –º—É—Ç–∞—Ü–∏–∏ ‚Äú–®–∫–æ–ª—ã –£–ª–∏—Ç–∫–∏‚Äù –æ–∫–∞–∑–∞–ª–∏ –Ω–µ—Å–∫–æ–ª—å–∫–æ –º–æ—â–Ω—ã—Ö –≤–æ–∑–¥–µ–π—Å—Ç–≤–∏–π –Ω–∞ –µ–µ —á–ª–µ–Ω–æ–≤, –Ω–∞–∏–±–æ–ª–µ–µ –∑–∞–º–µ—Ç–Ω—ã–º –∏–∑ –∫–æ—Ç–æ—Ä—ã—Ö –±—ã–ª–æ –∏–∑–º–µ–Ω–µ–Ω–∏–µ –∏—Ö –ø–æ—Ç–æ–≤—ã—Ö –∂–µ–ª–µ–∑. –í —Å—Ç—Ä–µ—Å—Å–æ–≤—ã—Ö —Å–∏—Ç—É–∞—Ü–∏—è—Ö, –∫–æ–≥–¥–∞ –ø—É–ª—å—Å —É—á–∞—â–∞–µ—Ç—Å—è, —Ç–∞–∫–∏—Ö –∫–∞–∫ –±–æ–π, –≤–µ–¥—å–º–∞–∫ –®–∫–æ–ª—ã –£–ª–∏—Ç–∫–∏ –ø–æ—Ç–µ–µ—Ç –≤—è–∑–∫–∏–º, –ø–æ—Ö–æ–∂–∏–º –Ω–∞ —Å–ª–∏–∑—å –≤–µ—â–µ—Å—Ç–≤–æ–º, –∫–æ—Ç–æ—Ä–æ–µ –Ω–µ–∏–∑–º–µ–Ω–Ω–æ –ø—Ä–æ—Å–∞—á–∏–≤–∞–µ—Ç—Å—è —Å–∫–≤–æ–∑—å –µ–≥–æ –æ–¥–µ–∂–¥—É –∏–ª–∏ –¥–æ—Å–ø–µ—Ö–∏, –¥–µ–ª–∞—è –∏—Ö —Å–∫–æ–ª—å–∑–∫–∏–º–∏ –Ω–∞ –æ—â—É–ø—å.')
    ) AS raw_data_ru(num, probability, effect, txt)
  UNION ALL
  SELECT 'en' AS lang, raw_data_en.*
    FROM (VALUES
      (1, 0.2, 'No Penalty For Strong Strikes', '<b><span style="color: #808080">The Wolf School</span></b><br>You trained at Kaer Morhen in the heights of the Blue Mountains. Your training was tough and structured, focusing on a very rounded approach to the Witcher profession. You were taught to strike hard and fast to end hunts quickly.'),
      (2, 0.2, '+2 Vigor Threshold', '<b><span style="color: #4A9BD6">The Gryphon School</span></b><br>You were trained at Kaer Y Seren along the coastal side of the Dragon Mountains. Your training was heavily focused on fighting any number of opponents and using your limited magical power to its greatest potential.'),
      (3, 0.2, 'Immune to Non-Magical Charm Attempts', '<b><span style="color: #E85A7F">The Cat School</span></b><br>You trained in the Dyn Marv Caravan, a traveling troop of witchers who sold their skills to anyone who could pay, for any job. Their mutations and training flayed your emotions, and you struggle against violent, cruel impulses.'),
      (4, 0.2, 'No Penalties for Dual Wielding', '<b><span style="color: #4EC04E">The Viper School</span></b><br>You trained at Gorthwr Gvaed in the deep chasms of the Tir Tochair Mountains. Unlike other witchers, you were trained on twin blades and an assassination-based approach to killing monsters.'),
      (5, 0.2, '-2 to Overall Armor Penalty', '<b><span style="color: #A67C00">The Bear School</span></b><br>You trained in the snowy heights of the Amell Mountains at Haern Cadvch. You conditioned your body to endure all manner of punishment and move quickly and efficiently in heavy steel armor.'),
      (6, 0.2, 'No action required to draw/stow a shield; no penalties for parrying with a shield; you can use the shield hand for signs, bombs, and potions.', '<b><span style="color: #FF8800">The Manticore School</span></b><br>Witchers of the Manticore School are defenders first and foremost and their training reflects that. These witchers are extensively trained in the use of special shields crafted for them by master craftsman at Behelt Nar.'),
      (7, 0.2, '-3 to enemy grapple checks; +3 to resist Disarm for 1 minute for 1 Stamina and 1 Action.', '<b><span style="color: #8B8B5A">The Snail School</span></b><br>The botched mutations of The School of the Snail had a few potent effects on its members, most notably being a change to their sweat glands. When in stressful or pulse pounding situations such as combat, a witcher of The School of the Snail sweats a viscous, mucus-like substance which invariably leaks through their clothing or armor, making them slick to the touch.')
    ) AS raw_data_en(num, probability, effect, txt)
),
vals AS (
  SELECT
    ('<td>' || to_char(probability*100, 'FM990.00') || '%</td>'
     || '<td>' || effect || '</td>'
     || '<td>' || txt || '</td>') AS text,
    num, probability, lang
  FROM raw_data
)
, ins_visible_rules AS (
  -- –ü—Ä–∞–≤–∏–ª–∞ –≤–∏–¥–∏–º–æ—Å—Ç–∏ DLC-–≤–∞—Ä–∏–∞–Ω—Ç–æ–≤ —à–∫–æ–ª –∏ –ø—Ä–æ–≤–µ—Ä–∫–∏ DLC (–ø—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ DLC –≤ state.dlcs)
  INSERT INTO rules (ru_id, name, body)
  VALUES
    (ck_id('witcher_cc.rules.is_manticore_school'), 'is_manticore_school', '{"in":["dlc_sch_manticore",{"var":["dlcs",[]]}]}'::jsonb),
    (ck_id('witcher_cc.rules.is_snail_school'), 'is_snail_school', '{"in":["dlc_sch_snail",{"var":["dlcs",[]]}]}'::jsonb),
    (ck_id('witcher_cc.rules.is_dlc_wt_enabled'), 'is_dlc_wt_enabled', '{"in":["dlc_wt",{"var":["dlcs",[]]}]}'::jsonb)
  ON CONFLICT (ru_id) DO NOTHING
)
, meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_witcher_school' AS qu_id
                , 'answer_options' AS entity
                , 'label' AS entity_field)
, ins_label AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'_o'|| to_char(vals.num, 'FM9900') ||'.'|| meta.entity ||'.'|| meta.entity_field) AS id
       , meta.entity, meta.entity_field, vals.lang, vals.text
  FROM vals
  CROSS JOIN meta
)
