WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_witcher_school' AS qu_id)
, schools(opt, school_code) AS (
  VALUES
    ('o01', 'wolf'),
    ('o02', 'gryphon'),
    ('o03', 'cat'),
    ('o04', 'viper'),
    ('o05', 'bear'),
    ('o06', 'manticore'),
    ('o07', 'snail')
)
INSERT INTO effects (scope, an_an_id, body)
SELECT
  'character' AS scope,
  meta.qu_id || '_' || schools.opt AS an_an_id,
  jsonb_build_object(
    'set',
    jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.school'),
      jsonb_build_object(
        'i18n_uuid',
        ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'_'|| schools.opt ||'.school.name')::text
      )
    )
  ) AS body
FROM meta
CROSS JOIN schools
UNION ALL
SELECT
  'character' AS scope,
  meta.qu_id || '_' || schools.opt AS an_an_id,
  jsonb_build_object(
    'set',
    jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.logicFields.witcher_school'),
      schools.school_code
    )
  ) AS body
FROM meta
CROSS JOIN schools
;

-- DLC "–°–Ω–∞—Ä—è–∂–µ–Ω–∏–µ –≤–µ–¥—å–º–∞–∫–∞" (dlc_wt): —Å—Ç–∞—Ä—Ç–æ–≤–æ–µ —Å–Ω–∞—Ä—è–∂–µ–Ω–∏–µ —à–∫–æ–ª—ã
-- –ö–ª–∞–¥—ë–º –ø—Ä–µ–¥–º–µ—Ç—ã –≤ —Ç–µ –∂–µ –º–µ—Å—Ç–∞ –∏ –≤ —Ç–æ–º –∂–µ —Ñ–æ—Ä–º–∞—Ç–µ, —á—Ç–æ –∏ –º–∞–≥–∞–∑–∏–Ω: –æ–±—ä–µ–∫—Ç + {amount, sourceId}
-- (–∫–∞–∫ –≤ applyShopNode: { ...row, amount, sourceId }).
WITH
  wt AS (
    SELECT body AS expr
      FROM rules
     WHERE ru_id = ck_id('witcher_cc.rules.is_dlc_wt_enabled')
  )
INSERT INTO effects (scope, an_an_id, body)
-- –í–æ–ª–∫
SELECT 'character', 'wcc_witcher_school_o01',
  jsonb_build_object(
    'set',
    jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.gear.weapons'),
      jsonb_build_object(
        'jsonlogic_expression',
        jsonb_build_object(
          'if',
          jsonb_build_array(
            wt.expr,
            jsonb_build_object(
              'concat_arrays',
              jsonb_build_array(
                jsonb_build_object('var', jsonb_build_array('characterRaw.gear.weapons', jsonb_build_array())),
                jsonb_build_array(
                  jsonb_build_object('w_id', 'W136', 'sourceId', 'weapons', 'amount', 1),
                  jsonb_build_object('w_id', 'W129', 'sourceId', 'weapons', 'amount', 1)
                )
              )
            ),
            jsonb_build_object('var', jsonb_build_array('characterRaw.gear.weapons', jsonb_build_array()))
          )
        )
      )
    )
  )
FROM wt
UNION ALL
SELECT 'character', 'wcc_witcher_school_o01',
  jsonb_build_object(
    'set',
    jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.gear.armors'),
