INSERT INTO effects (scope, an_an_id, body)
SELECT
  'character' AS scope,
  v.an_id AS an_an_id,
  jsonb_build_object(
    'set',
    jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.professional_gear_options'),
      jsonb_build_object(
        'tokens', 2,
        'items', jsonb_build_array('T105', 'A014', 'W012', 'WT004'),
        'bundles', jsonb_build_array(
          jsonb_build_object(
            'bundleId', 'witcher_throwing_knives',
            'displayName', jsonb_build_object('i18n_uuid', ck_id('witcher_cc.wcc_profession_shop.bundle.witcher_throwing_knives')::text),
            'items', jsonb_build_array(
              jsonb_build_object(
                'sourceId', 'weapons',
                'itemId', 'W107',
                'quantity', 5
              )
            )
          )
        )
      )
    )
  ) AS body
FROM (
  VALUES
    ('wcc_profession_o02')
) AS v(an_id);

-- –≠—Ñ—Ñ–µ–∫—Ç—ã: —Å—Ç–∞—Ä—Ç–æ–≤—ã–µ –¥–µ–Ω—å–≥–∏ (–≤–µ—Ç–≤–ª–µ–Ω–∏–µ –ø–æ DLC dlc_wt)
-- dlcs –Ω–∞—Ö–æ–¥—è—Ç—Å—è –≤ —Å–æ—Å—Ç–æ—è–Ω–∏–∏ –≤ –º–∞—Å—Å–∏–≤–µ dlcs
INSERT INTO effects (scope, an_an_id, body)
SELECT
  'character' AS scope,
  v.an_id AS an_an_id,
  jsonb_build_object(
    'set',
    jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.money.crowns'),
      jsonb_build_object(
        'jsonlogic_expression',
        jsonb_build_object(
          'if',
          jsonb_build_array(
            jsonb_build_object(
              'in',
              jsonb_build_array(
                'dlc_wt',
                jsonb_build_object('var', jsonb_build_array('dlcs', jsonb_build_array()))
              )
            ),
            jsonb_build_object(
              '*',
              jsonb_build_array(
                10,
                jsonb_build_object('d6', jsonb_build_array())
              )
            ),
            jsonb_build_object(
              '*',
              jsonb_build_array(
                50,
                jsonb_build_object(
                  '+',
                  jsonb_build_array(
                    jsonb_build_object('d6', jsonb_build_array()),
                    jsonb_build_object('d6', jsonb_build_array())
                  )
                )
              )
            )
          )
        )
      )
    )
  ) AS body
FROM (
  VALUES
    ('wcc_profession_o02'),
    ('wcc_profession_o02_wt_wolf'),
    ('wcc_profession_o02_wt_gryphon'),
    ('wcc_profession_o02_wt_cat'),
    ('wcc_profession_o02_wt_viper'),
    ('wcc_profession_o02_wt_bear'),
    ('wcc_profession_o02_wt_manticore'),
    ('wcc_profession_o02_wt_snail')
) AS v(an_id);

