                    <td>Innkeep</td> <td>3</td>
                    <td>Low</td>      <td>2</td>
                    <td>Blackmailed</td>      <td>3</td>
                </tr>
                <tr>
                    <td>Town</td>  <td>4</td>
                    <td>Artisan</td> <td>5</td>
                    <td>Average</td>  <td>3</td>
                    <td>Indebted</td>  <td>5</td>
