            rule_parts.is_human_expr,
            rule_parts.is_elf_expr
          )
        ),
        jsonb_build_object('!', rule_parts.exp_toc_expr)
      )
    ) AS body
  FROM rule_parts
  ON CONFLICT (ru_id) DO NOTHING
  RETURNING ru_id
)
