-- >>> BEGIN sql/012_ch_language.sql

\echo '012_ch_language.sql'
-- –£–∑–µ–ª: –í—ã–±–æ—Ä –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ–≥–æ —è–∑—ã–∫–∞ (–¥–ª—è –ë–∞—Ä–¥–∞ –∏ –¢–æ—Ä–≥–æ–≤—Ü–∞)

-- –í–æ–ø—Ä–æ—Å
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_ch_language' AS qu_id
                , 'questions' AS entity)
-- i18n –∑–∞–ø–∏—Å–∏ –¥–ª—è –≤–∞—Ä–∏–∞–Ω—Ç–æ–≤ body —Å –ø–æ–¥—Å—Ç–∞–Ω–æ–≤–∫–æ–π —è–∑—ã–∫–∞
, ins_body_dwarvish AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_dwarvish') AS id
       , meta.entity, 'body', v.lang, v.text
    FROM (VALUES
      ('ru', '–í—ã–±–µ—Ä–∏—Ç–µ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–π —è–∑—ã–∫<br><br>–í–∞—à —Ä–æ–¥–Ω–æ–π —è–∑—ã–∫: –ö—Ä–∞—Å–Ω–æ–ª—é–¥—Å–∫–∏–π'),
      ('en', 'Choose an additional language<br><br>Your native language: Dwarvish')
    ) AS v(lang, text)
    CROSS JOIN meta
  ON CONFLICT (id, lang) DO NOTHING
)
, ins_body_elder_speech AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_elder_speech') AS id
       , meta.entity, 'body', v.lang, v.text
    FROM (VALUES
      ('ru', '–í—ã–±–µ—Ä–∏—Ç–µ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–π —è–∑—ã–∫<br><br>–í–∞—à —Ä–æ–¥–Ω–æ–π —è–∑—ã–∫: –°—Ç–∞—Ä—à–∞—è —Ä–µ—á—å'),
      ('en', 'Choose an additional language<br><br>Your native language: Elder Speech')
    ) AS v(lang, text)
    CROSS JOIN meta
  ON CONFLICT (id, lang) DO NOTHING
)
, ins_body_northern AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_northern') AS id
       , meta.entity, 'body', v.lang, v.text
    FROM (VALUES
      ('ru', '–í—ã–±–µ—Ä–∏—Ç–µ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–π —è–∑—ã–∫<br><br>–í–∞—à —Ä–æ–¥–Ω–æ–π —è–∑—ã–∫: –í—Å–µ–æ–±—â–∏–π'),
      ('en', 'Choose an additional language<br><br>Your native language: Common Speech')
    ) AS v(lang, text)
    CROSS JOIN meta
  ON CONFLICT (id, lang) DO NOTHING
)
, ins_body_default AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_default') AS id
       , meta.entity, 'body', v.lang, v.text
    FROM (VALUES
      ('ru', '–í—ã–±–µ—Ä–∏—Ç–µ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–π —è–∑—ã–∫'),
      ('en', 'Choose an additional language')
    ) AS v(lang, text)
    CROSS JOIN meta
  ON CONFLICT (id, lang) DO NOTHING
)
INSERT INTO questions (qu_id, su_su_id, title, body, qtype, metadata)
SELECT meta.qu_id
     , meta.su_su_id
     , NULL
     , ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_default')::uuid
     , 'single'::question_type
     , jsonb_build_object(
         'path', jsonb_build_array(
           ck_id('witcher_cc.hierarchy.identity')::text
         ),
         'body',
         jsonb_build_object(
           'jsonlogic_expression',
           jsonb_build_object(
             'if',
             jsonb_build_array(
               jsonb_build_object('==', jsonb_build_array(
                 jsonb_build_object('var', 'characterRaw.logicFields.home_language'),
                 'Dwarvish'
               )),
               ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_dwarvish')::text,
               jsonb_build_object(
                 'if',
                 jsonb_build_array(
                   jsonb_build_object('==', jsonb_build_array(
                     jsonb_build_object('var', 'characterRaw.logicFields.home_language'),
                     'Elder Speech'
                   )),
                   ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_elder_speech')::text,
                   jsonb_build_object(
                     'if',
                     jsonb_build_array(
                       jsonb_build_object('==', jsonb_build_array(
                         jsonb_build_object('var', 'characterRaw.logicFields.home_language'),
                         'Common Speech'
                       )),
                       ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_northern')::text,
                       ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body_default')::text
                     )
                   )
                 )
               )
             )
           )
         )
       )
  FROM meta;

-- –ü—Ä–∞–≤–∏–ª–∞ –≤–∏–¥–∏–º–æ—Å—Ç–∏ –¥–ª—è –æ–ø—Ü–∏–π
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_ch_language' AS qu_id)
-- –ü—Ä–∞–≤–∏–ª–æ: —Ä–æ–¥–Ω–æ–π —è–∑—ã–∫ –ù–ï Dwarvish
, rule_not_dwarvish AS (
  INSERT INTO rules (ru_id, name, body)
  SELECT
    ck_id('witcher_cc.rules.wcc_ch_language.not_dwarvish') AS ru_id,
    'wcc_ch_language_not_dwarvish' AS name,
    jsonb_build_object('!=', jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.logicFields.home_language'),
      'Dwarvish'
    )) AS body
  FROM meta
  ON CONFLICT (ru_id) DO NOTHING
  RETURNING ru_id
)
-- –ü—Ä–∞–≤–∏–ª–æ: —Ä–æ–¥–Ω–æ–π —è–∑—ã–∫ –ù–ï Elder Speech
, rule_not_elder_speech AS (
  INSERT INTO rules (ru_id, name, body)
  SELECT
    ck_id('witcher_cc.rules.wcc_ch_language.not_elder_speech') AS ru_id,
    'wcc_ch_language_not_elder_speech' AS name,
    jsonb_build_object('!=', jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.logicFields.home_language'),
      'Elder Speech'
    )) AS body
  FROM meta
  ON CONFLICT (ru_id) DO NOTHING
  RETURNING ru_id
)
-- –ü—Ä–∞–≤–∏–ª–æ: —Ä–æ–¥–Ω–æ–π —è–∑—ã–∫ –ù–ï Common Speech
, rule_not_common_speech AS (
  INSERT INTO rules (ru_id, name, body)
  SELECT
    ck_id('witcher_cc.rules.wcc_ch_language.not_common_speech') AS ru_id,
    'wcc_ch_language_not_common_speech' AS name,
    jsonb_build_object('!=', jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.logicFields.home_language'),
      'Common Speech'
    )) AS body
  FROM meta
  ON CONFLICT (ru_id) DO NOTHING
  RETURNING ru_id
)
SELECT 1;

-- –û–ø—Ü–∏–∏ –æ—Ç–≤–µ—Ç–æ–≤
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_ch_language' AS qu_id
                , 'answer_options' AS entity
                , 'label' AS entity_field)
, vals AS (
  SELECT v.*
  FROM (VALUES
    ('ru', 1, '–ö—Ä–∞—Å–Ω–æ–ª—é–¥—Å–∫–∏–π —è–∑—ã–∫'),
    ('en', 1, 'Dwarvish'),
    ('ru', 2, '–°—Ç–∞—Ä—à–∞—è —Ä–µ—á—å'),
    ('en', 2, 'Elder Speech'),
    ('ru', 3, '–í—Å–µ–æ–±—â–∏–π'),
    ('en', 3, 'Common Speech')
  ) AS v(lang, num, text)
)
, ins_label AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
      SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'_o'|| to_char(vals.num, 'FM9900') ||'.'|| meta.entity ||'.'|| meta.entity_field) AS id
           , meta.entity, meta.entity_field, vals.lang, vals.text
        FROM vals
        CROSS JOIN meta
)
INSERT INTO answer_options (an_id, su_su_id, qu_qu_id, label, sort_order, visible_ru_ru_id, metadata)
  SELECT 'wcc_ch_language_o' || to_char(vals.num, 'FM9900') AS an_id,
         meta.su_su_id,
         meta.qu_id,
         ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'_o'|| to_char(vals.num, 'FM9900') ||'.'|| meta.entity ||'.'|| meta.entity_field) AS label,
         vals.num AS sort_order,
         CASE vals.num
           WHEN 1 THEN ck_id('witcher_cc.rules.wcc_ch_language.not_dwarvish')
           WHEN 2 THEN ck_id('witcher_cc.rules.wcc_ch_language.not_elder_speech')
           WHEN 3 THEN ck_id('witcher_cc.rules.wcc_ch_language.not_common_speech')
         END AS visible_ru_ru_id,
         '{}'::jsonb AS metadata
    FROM (SELECT DISTINCT num FROM vals) AS vals
    CROSS JOIN meta
  ON CONFLICT (an_id) DO NOTHING;

-- –≠—Ñ—Ñ–µ–∫—Ç—ã: –¥–æ–±–∞–≤–ª–µ–Ω–∏–µ –Ω–∞–≤—ã–∫–∞ —è–∑—ã–∫–∞ –≤ characterRaw.skills.initial[]
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_ch_language' AS qu_id)
, skill_mapping (an_id_suffix, skill_name) AS ( VALUES
    ('01', 'language_dwarvish'),
    ('02', 'language_elder_speech'),
    ('03', 'language_common_speech')
)
INSERT INTO effects (scope, an_an_id, body)
SELECT
  'character' AS scope,
  'wcc_ch_language_o' || sm.an_id_suffix AS an_an_id,
  jsonb_build_object(
    'add',
    jsonb_build_array(
      jsonb_build_object('var', 'characterRaw.skills.initial'),
      sm.skill_name
    )
  ) AS body
FROM skill_mapping sm;

-- –ü—Ä–∞–≤–∏–ª–æ –¥–ª—è –ø–µ—Ä–µ—Ö–æ–¥–∞ –∏–∑ 011: –ø—Ä–æ—Ñ–µ—Å—Å–∏—è –ë–∞—Ä–¥ –∏–ª–∏ –¢–æ—Ä–≥–æ–≤–µ—Ü
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_ch_language' AS qu_id)
, rule_bard_or_merchant AS (
  INSERT INTO rules (ru_id, name, body)
  SELECT
    ck_id('witcher_cc.rules.wcc_ch_language.bard_or_merchant') AS ru_id,
    'wcc_ch_language_bard_or_merchant' AS name,
    jsonb_build_object('or', jsonb_build_array(
      jsonb_build_object('==', jsonb_build_array(
        jsonb_build_object('var', 'characterRaw.logicFields.profession'),
        'Bard'
      )),
      jsonb_build_object('==', jsonb_build_array(
        jsonb_build_object('var', 'characterRaw.logicFields.profession'),
        'Merchant'
      ))
    )) AS body
  FROM meta
  ON CONFLICT (ru_id) DO NOTHING
  RETURNING ru_id
)
SELECT 1;

-- –ü–µ—Ä–µ—Ö–æ–¥—ã
-- –ò–∑ 011_ch_name –ø–æ –ø—Ä–∞–≤–∏–ª—É (–ø—Ä–æ—Ñ–µ—Å—Å–∏—è –ë–∞—Ä–¥ –∏–ª–∏ –¢–æ—Ä–≥–æ–≤–µ—Ü)
INSERT INTO transitions (from_qu_qu_id, to_qu_qu_id, ru_ru_id, priority)
  SELECT 'wcc_ch_name', 'wcc_ch_language', r.ru_id, 1
    FROM (SELECT ru_id FROM rules WHERE name = 'wcc_ch_language_bard_or_merchant') r;
-- <<< END sql/012_ch_language.sql

-- >>> BEGIN sql/013_ch_age.sql

\echo '013_ch_age.sql'
-- –£–∑–µ–ª: –ë—Ä–∞—Ç—å—è –∏ —Å—ë—Å—Ç—Ä—ã - –û—Å–Ω–æ–≤–Ω–∞—è —á–µ—Ä—Ç–∞ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∞

-- –í–æ–ø—Ä–æ—Å
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id
                , 'wcc_ch_age' AS qu_id
                , 'questions' AS entity)
, ins_body AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
    SELECT ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body') AS id
         , meta.entity, 'body', v.lang, v.text
      FROM (VALUES
        ('ru', '–°–∫–æ–ª—å–∫–æ –≤–∞–º –ª–µ—Ç? –í–µ–¥—å–º–∞–∫–∞–º –º–æ–∂–µ—Ç –±—ã—Ç—å –æ—Ç 50 –¥–æ 260 –ª–µ—Ç, –¥–ª—è –æ—Å—Ç–∞–ª—å–Ω—ã—Ö —Ä–∞—Å –≤ –ø—Ä–∞–≤–∏–ª–∞—Ö –Ω–µ—Ç –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–π..'),
        ('en', 'How old are you? Witchers can be anywhere from 50 to 260 years old. For other races, there are no restrictions in the rules.')
      ) AS v(lang, text)
      CROSS JOIN meta
  )
INSERT INTO questions (qu_id, su_su_id, title, body, qtype, metadata)
SELECT meta.qu_id
     , meta.su_su_id
     , NULL
     , ck_id(meta.su_su_id ||'.'|| meta.qu_id ||'.'|| meta.entity ||'.'|| 'body')
       , 'value_numeric'
       , jsonb_build_object(
          'type'        , 'int',
          'defaultValue', 18,
          'placeholder' , 'Years',
          'valueTarget' , 'characterRaw.age',
          'counterSet'  , jsonb_build_array(
                            jsonb_build_object('id','lifeEventsCounter','value', 0)
                          ),
          'path', jsonb_build_array(
            ck_id('witcher_cc.hierarchy.identity')::text,
            ck_id('witcher_cc.hierarchy.character_age')::text
          ),
          -- min: 50 –¥–ª—è –≤–µ–¥—å–º–∞–∫–∞, 0 –¥–ª—è –æ—Å—Ç–∞–ª—å–Ω—ã—Ö —Ä–∞—Å
          'min', jsonb_build_object(
            'if', jsonb_build_array(
              jsonb_build_object('==', jsonb_build_array(
                jsonb_build_object('var', 'characterRaw.logicFields.race'),
                'Witcher'
              )),
              50,
              0
            )
          ),
          -- max: 260 –¥–ª—è –≤–µ–¥—å–º–∞–∫–∞, –Ω–µ –≤—ã—Å—Ç–∞–≤–ª—è–µ–º –¥–ª—è –æ—Å—Ç–∞–ª—å–Ω—ã—Ö (null)
          'max', jsonb_build_object(
            'if', jsonb_build_array(
              jsonb_build_object('==', jsonb_build_array(
                jsonb_build_object('var', 'characterRaw.logicFields.race'),
                'Witcher'
              )),
              260,
              null
            )
          ),
          -- min_rand: 50 –¥–ª—è –≤–µ–¥—å–º–∞–∫–∞, 18 –¥–ª—è –æ—Å—Ç–∞–ª—å–Ω—ã—Ö —Ä–∞—Å
          'min_rand', jsonb_build_object(
            'if', jsonb_build_array(
              jsonb_build_object('==', jsonb_build_array(
                jsonb_build_object('var', 'characterRaw.logicFields.race'),
                'Witcher'
              )),
              50,
              18
            )
          ),
          -- max_rand: 260 –¥–ª—è –≤–µ–¥—å–º–∞–∫–∞, 60 –¥–ª—è —á–µ–ª–æ–≤–µ–∫–∞, 150 –¥–ª—è –∫—Ä–∞—Å–Ω–æ–ª—é–¥–∞, 300 –¥–ª—è —ç–ª—å—Ñ–∞
          'max_rand', jsonb_build_object(
            'if', jsonb_build_array(
              jsonb_build_object('==', jsonb_build_array(
                jsonb_build_object('var', 'characterRaw.logicFields.race'),
                'Witcher'
              )),
              260,
              jsonb_build_object(
                'if', jsonb_build_array(
                  jsonb_build_object('==', jsonb_build_array(
                    jsonb_build_object('var', 'characterRaw.logicFields.race'),
                    'Human'
                  )),
                  60,
                  jsonb_build_object(
                    'if', jsonb_build_array(
                      jsonb_build_object('==', jsonb_build_array(
                        jsonb_build_object('var', 'characterRaw.logicFields.race'),
                        'Dwarf'
                      )),
                      150,
                      jsonb_build_object(
                        'if', jsonb_build_array(
                          jsonb_build_object('==', jsonb_build_array(
                            jsonb_build_object('var', 'characterRaw.logicFields.race'),
                            'Elf'
                          )),
                          300,
                          null
                        )
                      )
                    )
                  )
                )
              )
            )
          )
         ) AS metadata
  FROM meta;
  
