             SELECT jsonb_agg(ck_id('witcher_cc' ||'.'|| 'wcc_past_family_status' ||'.'|| to_char(num, 'FM9900') ||'.'|| 'questions' ||'.'|| 'column_name')::text ORDER BY num)
             FROM (SELECT DISTINCT num FROM c_vals) AS cols
