INSERT INTO transitions (from_qu_qu_id, to_qu_qu_id, via_an_an_id, priority)
  SELECT 'wcc_life_events_relationshipsstory', 'wcc_life_events_relationshipsstory_details', 'wcc_life_events_relationshipsstory_o02', 2;
