-- –û—Ç–≤–µ—Ç—ã (50/50)
WITH
raw_data AS (
  SELECT 'ru' AS lang, raw_data_ru.*
  FROM (VALUES
    (1, 0.5, '–ú—É–∂—Å–∫–æ–π'),
