      ('ru', 3, '–í—ã–≥–æ–¥–∞'),
      ('ru', 4, '–°–æ—é–∑–Ω–∏–∫'),
      ('ru', 5, '–û—Ö–æ—Ç–∞'),
      ('ru', 6, '–†–∏—Å–∫ - –û–ø–∞—Å–Ω—ã–µ —Å–æ–±—ã—Ç–∏—è'),
      ('ru', 7, '–†–∏—Å–∫ - –†–∞–Ω—ã'),
      ('ru', 8, '–†–∏—Å–∫ - –í—Ä–∞–≥'),
      ('en', 1, 'Saftey'),
