  ) AS raw_data_en(num, Saftey, Nothing, Benefit, Ally, a_hunt, Danger_events, Wounds, Enemies)
)
, vals AS (
  SELECT '<td>' || Saftey || '</td>'
         || '<td>' || Nothing || '</td>'
         || '<td>' || Benefit || '</td>'
         || '<td>' || Ally || '</td>'
