                , 'wcc_witcher_events' AS qu_id
                , 'answer_options' AS entity
                , 'label' AS entity_field)
, raw_data AS (
  SELECT 'ru' AS lang, raw_data_ru.*
  FROM (VALUES
    (1, 1, 0.1, '–í—ã–≥–æ–¥–∞'),
