    (1, 3, 0.1, '–û—Ö–æ—Ç–∞'),
    (1, 4, 0.7, '–ù–∏—á–µ–≥–æ'),

    (2, 1, 0.1, '–í—ã–≥–æ–¥–∞'),
    (2, 2, 0.1, '–°–æ—é–∑–Ω–∏–∫'),
    (2, 3, 0.3, '–û—Ö–æ—Ç–∞'),
    (2, 4, 0.5, '–ù–∏—á–µ–≥–æ'),

    (3, 1, 0.2, '–í—ã–≥–æ–¥–∞'),
