    (4, 2, 0.225, '–°–æ—é–∑–Ω–∏–∫'),
    (4, 3, 0.225, '–û—Ö–æ—Ç–∞'),
    (4, 4, 0.3, '–ù–∏—á–µ–≥–æ')
  ) AS raw_data_ru(group_id, num, probability, txt)

  UNION ALL

  SELECT 'en' AS lang, raw_data_en.*
  FROM (VALUES
    (1, 1, 0.1, 'Benefit'),
    (1, 2, 0.1, 'Ally'),
    (1, 3, 0.1, 'A Hunt'),
    (1, 4, 0.7, 'Nothing'),

    (2, 1, 0.1, 'Benefit'),
    (2, 2, 0.1, 'Ally'),
    (2, 3, 0.3, 'A Hunt'),
    (2, 4, 0.5, 'Nothing'),

    (3, 1, 0.2, 'Benefit'),
    (3, 2, 0.2, 'Ally'),
