  r.id,
  vals.num,
  jsonb_build_object(
           'probability', vals.probability
  ) || ('{'|| coalesce(vals.counter_txt,'') ||'}')::jsonb
FROM vals
CROSS JOIN meta
JOIN rules_vals r ON vals.group_id = r.group_id
ON CONFLICT (an_id) DO NOTHING;

