\echo '003_wcc_item_effect_conditions.sql'
CREATE TABLE IF NOT EXISTS wcc_item_effect_conditions (
    ec_id          varchar(10) PRIMARY KEY, -- e.g. 'EC001'
    description_id uuid NOT NULL            -- ck_id('witcher_cc.items.effect_condition.description.'||ec_id)
);

COMMENT ON TABLE wcc_item_effect_conditions IS
  '–°–ø—Ä–∞–≤–æ—á–Ω–∏–∫ —É—Å–ª–æ–≤–∏–π –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è/–∞–∫—Ç–∏–≤–∞—Ü–∏–∏ —ç—Ñ—Ñ–µ–∫—Ç–æ–≤. –õ–æ–∫–∞–ª–∏–∑—É–µ–º–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ —Ö—Ä–∞–Ω–∏—Ç—Å—è –≤ i18n_text —á–µ—Ä–µ–∑ –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω—ã–π UUID (ck_id).';

COMMENT ON COLUMN wcc_item_effect_conditions.ec_id IS
  'ID —É—Å–ª–æ–≤–∏—è —ç—Ñ—Ñ–µ–∫—Ç–∞ (–Ω–∞–ø—Ä–∏–º–µ—Ä EC001). –ü–µ—Ä–≤–∏—á–Ω—ã–π –∫–ª—é—á.';

COMMENT ON COLUMN wcc_item_effect_conditions.description_id IS
  'i18n UUID –¥–ª—è –æ–ø–∏—Å–∞–Ω–∏—è —É—Å–ª–æ–≤–∏—è. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''witcher_cc.items.effect_condition.description.''||ec_id).';

WITH raw_data (ec_id, description_ru, description_en) AS ( VALUES
    ('EC001', '–ï—Å–ª–∏ –æ—Ä—É–∂–∏–µ —Ä–∞—Å–∫–∞–ª–µ–Ω–æ', 'If the weapon is heated'),
    ('EC002', '–ï—Å–ª–∏ –æ—Ä—É–∂–∏–µ –≥–æ—Ä–∏—Ç', 'If the weapon is burning'),
    ('EC003', '–ü—Ä–∏ –∞–ª—å–ø–∏–Ω–∏–∑–º–µ', 'While climbing'),
    ('EC004', '–ï—Å–ª–∏ –≤ —Å–µ–¥–ª–µ', 'While mounted'),
    ('EC005', '–í –¥–∏–∫–æ–π —Å—Ä–µ–¥–µ', 'In the wilderness'),
    ('EC006', '–û–≥–æ–Ω—å', 'Fire'),
    ('EC007', '–í–æ–¥–∞', 'Water'),
    ('EC008', '–ó–µ–º–ª—è', 'Earth'),
    ('EC009', '–í–æ–∑–¥—É—Ö', 'Air'),
    ('EC010', '–°–º–µ—à–∞–Ω–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã', 'Mixed elements')
),
ins_descriptions AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    SELECT ck_id('witcher_cc.items.effect_condition.description.'||rd.ec_id),
           'items',
           'effect_condition_descriptions',
           'ru',
           rd.description_ru
      FROM raw_data rd
     WHERE nullif(rd.description_ru,'') is not null
    UNION ALL
    SELECT ck_id('witcher_cc.items.effect_condition.description.'||rd.ec_id),
           'items',
           'effect_condition_descriptions',
           'en',
           rd.description_en
      FROM raw_data rd
     WHERE nullif(rd.description_en,'') is not null
  ) foo
  ON CONFLICT (id, lang) DO NOTHING
)
INSERT INTO wcc_item_effect_conditions (ec_id, description_id)
SELECT rd.ec_id,
       ck_id('witcher_cc.items.effect_condition.description.'||rd.ec_id) AS description_id
  FROM raw_data rd
ON CONFLICT (ec_id) DO UPDATE
SET description_id = EXCLUDED.description_id;



