\echo '005_wcc_item_weapons.sql'
CREATE TABLE IF NOT EXISTS wcc_item_weapons (
    w_id            varchar(10) PRIMARY KEY,          -- e.g. 'W001'
    dlc_dlc_id      varchar(64) NOT NULL REFERENCES wcc_dlcs(dlc_id), -- source_id (core, hb, dlc_*, exp_*)
    weapon_class_id uuid NOT NULL,                    -- ck_id('weapons.'||class_id) where class_id is wt_crossbow, wt_sword, etc.

    name_id         uuid NOT NULL,                    -- ck_id('witcher_cc.items.weapon.name.'||w_id)

    is_piercing     uuid NULL,                        -- ck_id('witcher_cc.items.weapon.damage_type.P')
    is_slashing     uuid NULL,                        -- ck_id('witcher_cc.items.weapon.damage_type.S')
    is_bludgeoning  uuid NULL,                        -- ck_id('witcher_cc.items.weapon.damage_type.B')
    is_elemental    uuid NULL,                        -- ck_id('witcher_cc.items.weapon.damage_type.E')

    accuracy        integer NOT NULL,                 -- can be negative
    crafted_by_id      uuid NULL,                        -- ck_id('witcher_cc.items.weapon.crafted_by.Humans'|'Non-humans'|'Witchers')
    availability_id    uuid NULL,                        -- ck_id('witcher_cc.items.weapon.availability.E'|'C'|'P'|'R'|'U')
    dmg             varchar(32) NULL,                 -- e.g. '4d6+3' (kept as text)

    damage_dices    integer NULL,
    damage_modifier integer NULL,

    reliability     integer NOT NULL,
    hands           integer NULL,

    range           varchar(32) NULL,                 -- e.g. '100' or '–¢–µ–ª*4' (as text)
    concealment_id     uuid NULL,                        -- ck_id('witcher_cc.items.weapon.concealment.T'|'S'|'L'|'N/A')
    enhancements    integer NOT NULL DEFAULT 0,

    weight           numeric(12,1) NULL,               -- stored after REPLACE(',', '.')
    price           integer NULL,               -- stored after REPLACE(',', '.')

    description_id  uuid NOT NULL                     -- ck_id('witcher_cc.items.weapon.description.'||w_id)
);

COMMENT ON TABLE wcc_item_weapons IS
  '–û—Ä—É–∂–∏–µ/–±–æ–µ–ø—Ä–∏–ø–∞—Å—ã/–±–æ–º–±—ã –∏ —Ç.–ø. –•—Ä–∞–Ω–∏—Ç –ª–æ–∫–∞–ª–∏–∑—É–µ–º—ã–µ –ø–æ–ª—è –∫–∞–∫ i18n UUID.';

COMMENT ON COLUMN wcc_item_weapons.dlc_dlc_id IS
  'FK –Ω–∞ wcc_dlcs.dlc_id (–∏—Å—Ç–æ—á–Ω–∏–∫/–ø–∞–∫–µ—Ç: core/hb/dlc_*/exp_*).';

COMMENT ON COLUMN wcc_item_weapons.weapon_class_id IS
  'i18n UUID –¥–ª—è –∫–ª–∞—Å—Å–∞ –æ—Ä—É–∂–∏—è. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''weapons.''||class_id) –≥–¥–µ class_id —ç—Ç–æ wt_crossbow, wt_sword –∏ —Ç.–¥.';

COMMENT ON COLUMN wcc_item_weapons.name_id IS
  'i18n UUID –¥–ª—è –Ω–∞–∑–≤–∞–Ω–∏—è –æ—Ä—É–∂–∏—è. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''witcher_cc.items.weapon.name.''||w_id).';

COMMENT ON COLUMN wcc_item_weapons.description_id IS
  'i18n UUID –¥–ª—è –æ–ø–∏—Å–∞–Ω–∏—è –æ—Ä—É–∂–∏—è. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''witcher_cc.items.weapon.description.''||w_id).';

COMMENT ON COLUMN wcc_item_weapons.availability_id IS
  'i18n UUID –¥–ª—è –¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç–∏ –æ—Ä—É–∂–∏—è. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç –∫–ª—é—á–∏ –∏–∑ —Å–ª–æ–≤–∞—Ä—è: ck_id(''availability.''||<E|C|P|R|U>). –ó–Ω–∞—á–µ–Ω–∏—è: –ü–æ–≤—Å–µ–º–µ—Å—Ç–Ω—ã–π (E), –û–±—ã—á–Ω—ã–π (C), –†–µ–¥–∫–∏–π (P), –†–µ–¥–∫–∏–π (R), –£–Ω–∏–∫–∞–ª—å–Ω—ã–π (U).';

COMMENT ON COLUMN wcc_item_weapons.crafted_by_id IS
  'i18n UUID –¥–ª—è –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—è –æ—Ä—É–∂–∏—è. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç –∫–ª—é—á–∏ –∏–∑ —Å–ª–æ–≤–∞—Ä—è: ck_id(''crafted_by.''||<humans|non-humans|witchers>). –ó–Ω–∞—á–µ–Ω–∏—è: –õ—é–¥–∏ (humans), –ù–µ–ª—é–¥–∏ (non-humans), –í–µ–¥—å–º–∞–∫–∏ (witchers).';

COMMENT ON COLUMN wcc_item_weapons.concealment_id IS
  'i18n UUID –¥–ª—è —Å–∫—Ä—ã—Ç–Ω–æ—Å—Ç–∏ –æ—Ä—É–∂–∏—è. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç –∫–ª—é—á–∏ –∏–∑ —Å–ª–æ–≤–∞—Ä—è: ck_id(''concealment.''||<T|S|L|N/A>). –ó–Ω–∞—á–µ–Ω–∏—è: –ú (T), –ù (S), –ö (L), –ù/C (N/A).';

COMMENT ON COLUMN wcc_item_weapons.is_piercing IS
  'i18n UUID –¥–ª—è —Ç–∏–ø–∞ —É—Ä–æ–Ω–∞: –∫–æ–ª—é—â–∏–π. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç –∫–ª—é—á–∏ –∏–∑ —Å–ª–æ–≤–∞—Ä—è: ck_id(''damage_type.P''). –ó–Ω–∞—á–µ–Ω–∏—è: –ö (P).';

COMMENT ON COLUMN wcc_item_weapons.is_slashing IS
  'i18n UUID –¥–ª—è —Ç–∏–ø–∞ —É—Ä–æ–Ω–∞: —Ä—É–±—è—â–∏–π. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç –∫–ª—é—á–∏ –∏–∑ —Å–ª–æ–≤–∞—Ä—è: ck_id(''damage_type.S''). –ó–Ω–∞—á–µ–Ω–∏—è: –† (S).';

COMMENT ON COLUMN wcc_item_weapons.is_bludgeoning IS
  'i18n UUID –¥–ª—è —Ç–∏–ø–∞ —É—Ä–æ–Ω–∞: –¥—Ä–æ–±—è—â–∏–π. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç –∫–ª—é—á–∏ –∏–∑ —Å–ª–æ–≤–∞—Ä—è: ck_id(''damage_type.B''). –ó–Ω–∞—á–µ–Ω–∏—è: –î (B).';

COMMENT ON COLUMN wcc_item_weapons.is_elemental IS
  'i18n UUID –¥–ª—è —Ç–∏–ø–∞ —É—Ä–æ–Ω–∞: —Å—Ç–∏—Ö–∏–π–Ω—ã–π. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç –∫–ª—é—á–∏ –∏–∑ —Å–ª–æ–≤–∞—Ä—è: ck_id(''damage_type.E''). –ó–Ω–∞—á–µ–Ω–∏—è: –° (E).';

