    ('W166', 'core', 'wt_axe', '–¢–æ–ø–æ—Ä', 'Hand Axe', NULL, 'TRUE', NULL, NULL, 0, 'humans', 'E', '2d6+1', 2, 1, 10, 1, NULL, 'M', 0, '1', '205', '–ü–æ–ª—é–±–∏–ª —è —Å–æ –≤—Ä–µ–º–µ–Ω–µ–º –ø—Ä–æ—Å—Ç–æ–π —Ç–æ–ø–æ—Ä. –†—É–±–∏–ª –∏–º —Å–Ω–∞—á–∞–ª–∞ –¥—Ä–æ–≤–∞. –ö—Ä–µ–ø–∫–∏–π —Ç–∞–∫–æ–π –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç. –ê —É–∂ –µ—Å–ª–∏ —ç—Ç–∏–º –º–æ–∂–Ω–æ –¥–µ—Ä–µ–≤–æ —Ä—É–±–∏—Ç—å, —Ç–æ –∫–æ—Å—Ç–∏ –∏ –ø–æ–¥–∞–≤–Ω–æ.'),
    ('W167', 'core', 'wt_axe', '–¢–æ–ø–æ—Ä –±–µ—Ä—Å–µ—Ä–∫–∞', 'Berserker‚Äôs Axe', NULL, 'TRUE', NULL, NULL, 0, 'humans', 'P', '6d6', 6, NULL, 15, 2, NULL, 'XL', 1, '3', '960', '–ù–∞ –æ—Å—Ç—Ä–æ–≤–∞—Ö –°–∫–µ–ª–ª–∏–≥–µ –º–Ω–æ–≥–æ –≤–æ–ª–∫–æ–ª–∞–∫–æ–≤ –∏ —Å–∏—Ä–µ–Ω, –∞ –≤–µ–¥—å–º–∞–∫–æ–≤ –º–∞–ª–æ. –î–æ–±–∞–≤—å –∫ —ç—Ç–æ–º—É –ø–æ–∫–∞–∑–Ω—É—é —Ö—Ä–∞–±—Ä–æ—Å—Ç—å –æ–±—ã—á–Ω–æ–≥–æ —Å–∫–µ–ª–ª–∏–≥—Ü–∞, –∏ –ø–æ–ª—É—á–∏—Ç—Å—è –≤–æ—Ç —ç—Ç–∞ —à—Ç—É–∫–∞. –î–≤—É—Ö–º–µ—Ç—Ä–æ–≤—ã–π —Ç–æ–ø–æ—Ä —Å –æ–≥—Ä–æ–º–Ω—ã–º –±–æ—Ä–æ–¥–æ–≤–∏–¥–Ω—ã–º –ª–µ–∑–≤–∏–µ–º, —É–∫—Ä–∞—à–µ–Ω–Ω—ã–º —Å–∫–µ–ª–ª–∏–≥—Å–∫–∏–º–∏ —Ä—É–Ω–∞–º.'),
    ('W168', 'core', 'wt_axe', '–û–±–µ–∑–≥–ª–∞–≤–ª–∏–≤–∞—Ç–µ–ª—å', 'Decapitator', NULL, 'TRUE', NULL, NULL, 0, 'humans', 'U', '10d6', 10, NULL, 20, 2, NULL, 'XL', 3, '4', NULL, ''),
    ('W169', 'dlc_sch_snail', 'wt_sword', '–°—Ç–∞–ª—å–Ω–æ–π –º–µ—á —à–∫–æ–ª—ã –£–ª–∏—Ç–∫–∏', 'Gastropod Steel Sword', 'TRUE', 'TRUE', NULL, NULL, -6, 'witchers', 'R', '8d6', 8, NULL, 20, 2, NULL, 'XL', 1, '5', NULL, ''),
    ('W170', 'dlc_sch_snail', 'wt_sword', '–°–µ—Ä–µ–±—Ä—è–Ω—ã–π –º–µ—á —à–∫–æ–ª—ã –£–ª–∏—Ç–∫–∏', 'Gastropod Silver Sword', 'TRUE', 'TRUE', NULL, NULL, -6, 'witchers', 'R', '5d6', 5, NULL, 15, 2, NULL, 'XL', 1, '3', NULL, '')
),
ins_i18n AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    -- Weapon names
    SELECT ck_id('witcher_cc.items.weapon.name.'||rd.w_id),
	       'items',
		   'weapon_names',
	       'ru',
		   rd.name_ru
	  FROM raw_data rd
	 WHERE nullif(rd.name_ru,'') is not null
	UNION ALL
    SELECT ck_id('witcher_cc.items.weapon.name.'||rd.w_id),
	       'items',
		   'weapon_names',
	       'en',
		   rd.name_en
	  FROM raw_data rd
	 WHERE nullif(rd.name_en,'') is not null
	UNION ALL
    -- Weapon descriptions
    SELECT ck_id('witcher_cc.items.weapon.description.'||rd.w_id),
	       'items',
		   'weapon_descriptions',
	       'ru',
		   rd.description_ru
	  FROM raw_data rd
	 WHERE nullif(rd.description_ru,'') is not null ) foo
)
INSERT INTO wcc_item_weapons
  SELECT w_id
       , source_id AS dlc_dlc_id
	   , ck_id('weapons.'||rd.class_id) AS weapon_class_id
	   , ck_id('witcher_cc.items.weapon.name.'||rd.w_id) AS name_id
	   , CASE WHEN rd.is_piercing = 'TRUE' THEN ck_id('damage_type.P') ELSE NULL END is_piercing
	   , CASE WHEN rd.is_slashing = 'TRUE' THEN ck_id('damage_type.S') ELSE NULL END is_slashing
	   , CASE WHEN rd.is_bludgeoning = 'TRUE' THEN ck_id('damage_type.B') ELSE NULL END is_bludgeoning
	   , CASE WHEN rd.is_elemental = 'TRUE' THEN ck_id('damage_type.E') ELSE NULL END is_elemental
	   , accuracy
	   , CASE 
	       WHEN rd.crafted_by_id = 'humans' THEN ck_id('crafted_by.humans')
	       WHEN rd.crafted_by_id = 'non-humans' THEN ck_id('crafted_by.non-humans')
	       WHEN rd.crafted_by_id = 'witchers' THEN ck_id('crafted_by.witchers')
	       ELSE NULL
	     END crafted_by_id
	   , CASE 
	       WHEN rd.availability_id = 'E' THEN ck_id('availability.E')
	       WHEN rd.availability_id = 'C' THEN ck_id('availability.C')
	       WHEN rd.availability_id = 'P' THEN ck_id('availability.P')
	       WHEN rd.availability_id = 'R' THEN ck_id('availability.R')
	       WHEN rd.availability_id = 'U' THEN ck_id('availability.U')
	       ELSE NULL
	     END availability_id
	   , nullif(rd.dmg,'') dmg
	   , damage_dices
	   , damage_modifier
	   , reliability
	   , hands
	   , nullif(rd.range,'') range
	   , CASE 
	       WHEN rd.concealment_id = 'S' THEN ck_id('concealment.S')
	       WHEN rd.concealment_id = 'M' THEN ck_id('concealment.T')
	       WHEN rd.concealment_id = 'L' THEN ck_id('concealment.L')
	       WHEN rd.concealment_id = 'XL' THEN ck_id('concealment.N/A')
	       ELSE NULL
	     END concealment_id
	   , coalesce(enhancements,0) enhancements
	   , CAST(REPLACE(rd.weight, ',', '.') AS numeric) weight
	   , CAST(REPLACE(rd.price, ',', '.') AS numeric) price
	   , ck_id('witcher_cc.items.weapon.description.'||rd.w_id) description_id
    FROM raw_data rd;
-- <<< END sql/items/005_wcc_item_weapons.sql

-- >>> BEGIN sql/items/006_wcc_item_armors.sql

\echo '006_wcc_item_armors.sql'
CREATE TABLE IF NOT EXISTS wcc_item_armors (
    a_id            varchar(10) PRIMARY KEY,          -- e.g. 'A002'
    dlc_dlc_id      varchar(64) NOT NULL REFERENCES wcc_dlcs(dlc_id), -- source_id (core, hb, dlc_*, exp_*)

    name_id         uuid NOT NULL,                    -- ck_id('witcher_cc.items.armor.name.'||a_id)

    -- Reused dictionary fields (see 000_wcc_items_dict.sql)
    body_part_id    uuid NULL,                        -- ck_id('bodypart.*')
    armor_class_id  uuid NULL,                        -- ck_id('armor_class.*')
    crafted_by_id   uuid NULL,                        -- ck_id('crafted_by.*')
    availability_id uuid NULL,                        -- ck_id('availability.*')

    reliability     integer NULL,
    stopping_power  integer NULL,
    enhancements    integer NOT NULL DEFAULT 0,

    encumbrance     integer NULL,
    weight          numeric(12,1) NULL,
    price           integer NULL,

    description_id  uuid NOT NULL                     -- ck_id('witcher_cc.items.armor.description.'||a_id)
);

COMMENT ON TABLE wcc_item_armors IS
  '–î–æ—Å–ø–µ—Ö–∏/—â–∏—Ç—ã. –ò—Å—Ç–æ—á–Ω–∏–∫ (DLC) —á–µ—Ä–µ–∑ wcc_dlcs, –ª–æ–∫–∞–ª–∏–∑—É–µ–º—ã–µ –ø–æ–ª—è —á–µ—Ä–µ–∑ i18n_text UUID (ck_id). –ß–∞—Å—Ç—å —Ç–µ–ª–∞/–∫–ª–∞—Å—Å/–ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å/–¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç—å ‚Äî –∏–∑ –æ–±—â–µ–≥–æ —Å–ª–æ–≤–∞—Ä—è (000_wcc_items_dict.sql).';

COMMENT ON COLUMN wcc_item_armors.dlc_dlc_id IS
  'FK –Ω–∞ wcc_dlcs.dlc_id (–∏—Å—Ç–æ—á–Ω–∏–∫/–ø–∞–∫–µ—Ç: core/hb/dlc_*/exp_*).';

COMMENT ON COLUMN wcc_item_armors.name_id IS
  'i18n UUID –¥–ª—è –Ω–∞–∑–≤–∞–Ω–∏—è –¥–æ—Å–ø–µ—Ö–∞. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''witcher_cc.items.armor.name.''||a_id).';

COMMENT ON COLUMN wcc_item_armors.description_id IS
  'i18n UUID –¥–ª—è –æ–ø–∏—Å–∞–Ω–∏—è –¥–æ—Å–ø–µ—Ö–∞. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''witcher_cc.items.armor.description.''||a_id).';

WITH raw_data (
  a_id, source_id, body_part, armor_class,
  name_ru, name_en,
  is_piercing, is_slashing, is_bludgeoning, is_elemental, is_poison, is_bleeding,
  crafted_by, availability,
  reliability, stopping_power, enhancements, encumbrance, weight, price,
  description_ru, description_en
) AS ( VALUES
  ('A002','core','bodypart.head','armor_class.light',
    $$–ö–∞–ø—é—à–æ–Ω –≤–∞—Ä–¥—ç–Ω—Å–∫–æ–≥–æ –ª—É—á–Ω–∏–∫–∞$$, $$Verden Archer‚Äôs Hood$$,
    NULL,NULL,NULL,NULL,NULL,NULL,
    'crafted_by.humans','availability.C',
    NULL,3,1,'0','0,5','100',
