ins_i18n AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    -- Recipe names
    SELECT ck_id('witcher_cc.items.recipe.name.'||rd.r_id),
           'items',
           'recipe_names',
           'ru',
           rd.name_ru
      FROM raw_data rd
     WHERE nullif(rd.name_ru,'') IS NOT NULL
    UNION ALL
    SELECT ck_id('witcher_cc.items.recipe.name.'||rd.r_id),
           'items',
           'recipe_names',
           'en',
           rd.name_en
      FROM raw_data rd
     WHERE nullif(rd.name_en,'') IS NOT NULL
    UNION ALL
    -- Recipe descriptions
    SELECT ck_id('witcher_cc.items.recipe.description.'||rd.r_id),
           'items',
           'recipe_descriptions',
           'ru',
           rd.description_ru
      FROM raw_data rd
     WHERE nullif(rd.description_ru,'') IS NOT NULL
    UNION ALL
    SELECT ck_id('witcher_cc.items.recipe.description.'||rd.r_id),
           'items',
           'recipe_descriptions',
           'en',
           rd.description_en
      FROM raw_data rd
     WHERE nullif(rd.description_en,'') IS NOT NULL
  ) foo
  ON CONFLICT (id, lang) DO NOTHING
)
INSERT INTO wcc_item_recipes (
  r_id, dlc_dlc_id, name_id,
  group_id, availability_id, craft_level_id,
  weight, minimal_ingredients_cost, price_potion, price_formula, complexity,
  time_craft_val, time_craft_unit_id,
  time_effect_val, time_effect_unit_id,
  toxicity,
  formula,
  description_id
)
SELECT rd.r_id
     , rd.source_id AS dlc_dlc_id
     , ck_id('witcher_cc.items.recipe.name.'||rd.r_id) AS name_id
     , ck_id(rd.group_key) AS group_id
     , ck_id(rd.availability_key) AS availability_id
     , CASE WHEN rd.craft_level_key IS NOT NULL AND rd.craft_level_key != '' THEN ck_id(rd.craft_level_key) ELSE NULL END AS craft_level_id
     , CAST(NULLIF(REPLACE(rd.weight, ',', '.'), '') AS numeric) AS weight
     , CAST(NULLIF(rd.minimal_ingredients_cost::text, '') AS integer) AS minimal_ingredients_cost
     , CAST(NULLIF(rd.price_potion::text, '') AS integer) AS price_potion
     , CAST(NULLIF(rd.price_formula::text, '') AS integer) AS price_formula
     , CAST(NULLIF(rd.complexity::text, '') AS integer) AS complexity
     , NULLIF(trim(rd.time_craft_val), '') AS time_craft_val
     , CASE WHEN rd.time_craft_unit_key IS NOT NULL AND rd.time_craft_unit_key != '' THEN ck_id(rd.time_craft_unit_key) ELSE NULL END AS time_craft_unit_id
     , NULLIF(trim(rd.time_effect_val), '') AS time_effect_val
     , CASE WHEN rd.time_effect_unit_key IS NOT NULL AND rd.time_effect_unit_key != '' THEN ck_id(rd.time_effect_unit_key) ELSE NULL END AS time_effect_unit_id
     , NULLIF(trim(rd.toxicity), '') AS toxicity
     , parse_formula_to_uuid_array(rd.formula_text) AS formula
     , ck_id('witcher_cc.items.recipe.description.'||rd.r_id) AS description_id
  FROM raw_data rd
ON CONFLICT (r_id) DO UPDATE
SET
  dlc_dlc_id = EXCLUDED.dlc_dlc_id,
  name_id = EXCLUDED.name_id,
  group_id = EXCLUDED.group_id,
  availability_id = EXCLUDED.availability_id,
  craft_level_id = EXCLUDED.craft_level_id,
  weight = EXCLUDED.weight,
  minimal_ingredients_cost = EXCLUDED.minimal_ingredients_cost,
  price_potion = EXCLUDED.price_potion,
  price_formula = EXCLUDED.price_formula,
  complexity = EXCLUDED.complexity,
  time_craft_val = EXCLUDED.time_craft_val,
  time_craft_unit_id = EXCLUDED.time_craft_unit_id,
  time_effect_val = EXCLUDED.time_effect_val,
  time_effect_unit_id = EXCLUDED.time_effect_unit_id,
  toxicity = EXCLUDED.toxicity,
  formula = EXCLUDED.formula,
  description_id = EXCLUDED.description_id;


-- <<< END sql/items/008_wcc_item_recipes.sql

-- >>> BEGIN sql/items/009_wcc_item_weapons_v.sql

\echo '009_wcc_item_weapons_v.sql'
-- Materialized view for shop UI (weapons catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.

DROP MATERIALIZED VIEW IF EXISTS wcc_item_weapons_v;

CREATE MATERIALIZED VIEW wcc_item_weapons_v AS
SELECT wiw.w_id
     , wiw.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , iic.text AS weapon_class
     , i.text AS weapon_name
     , wiw.dmg
     , coalesce(wiw.weight, 0) AS weight
     , coalesce(wiw.price, 0) AS price
     , coalesce(wiw.hands, 0) AS hands
     , coalesce(wiw.reliability, 0) AS reliability
     , iav.text AS availability
     , icb.text AS crafted_by
     , icon.text AS concealment
     , concat_ws(', ',
         CASE WHEN wiw.is_piercing IS NOT NULL THEN idp.text ELSE NULL END,
         CASE WHEN wiw.is_slashing IS NOT NULL THEN ids.text ELSE NULL END,
         CASE WHEN wiw.is_bludgeoning IS NOT NULL THEN idb.text ELSE NULL END,
         CASE WHEN wiw.is_elemental IS NOT NULL THEN ide.text ELSE NULL END
       ) AS dmg_types
     , ef.effect_names
     , ef.effect_descriptions
     , i.lang
  FROM wcc_item_weapons wiw
  JOIN i18n_text i ON i.id = wiw.name_id
  JOIN i18n_text iic ON iic.id = wiw.weapon_class_id AND iic.lang = i.lang
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = wiw.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = i.lang
  LEFT JOIN i18n_text iav ON iav.id = wiw.availability_id AND iav.lang = i.lang
  LEFT JOIN i18n_text icb ON icb.id = wiw.crafted_by_id AND icb.lang = i.lang
  LEFT JOIN i18n_text icon ON icon.id = wiw.concealment_id AND icon.lang = i.lang
  LEFT JOIN i18n_text idp ON idp.id = wiw.is_piercing AND idp.lang = i.lang
  LEFT JOIN i18n_text ids ON ids.id = wiw.is_slashing AND ids.lang = i.lang
  LEFT JOIN i18n_text idb ON idb.id = wiw.is_bludgeoning AND idb.lang = i.lang
  LEFT JOIN i18n_text ide ON ide.id = wiw.is_elemental AND ide.lang = i.lang
  LEFT JOIN (
    SELECT ite.item_id
         , ie.lang
         , string_agg(
             replace(coalesce(ie.text, ''), '<mod>', coalesce(ite.modifier::text, ''))
             || coalesce(nullif('[' || coalesce(iec.text, '') || ']', '[]'), ''),
             E'\n'
             ORDER BY ite.e_e_id
           ) AS effect_names
         , string_agg(CASE WHEN e.description_id is not null then
             replace(coalesce(ie.text, ''), '<mod>', coalesce(ite.modifier::text, ''))
             || ' -' || coalesce(nullif(' [' || coalesce(iec.text, '') || ']', ' []'), '')
			 || ' ' || replace(coalesce(ide.text, ''), '<mod>', coalesce(ite.modifier::text, ''))
			 end,
             E'\n'
             ORDER BY ite.e_e_id
           ) AS effect_descriptions
      FROM wcc_item_to_effects ite
      LEFT JOIN wcc_item_effects e ON e.e_id = ite.e_e_id
      LEFT JOIN i18n_text ie ON ie.id = e.name_id
      LEFT JOIN i18n_text ide ON ide.id = e.description_id AND ide.lang = ie.lang
      LEFT JOIN wcc_item_effect_conditions ec ON ec.ec_id = ite.ec_ec_id
      LEFT JOIN i18n_text iec ON iec.id = ec.description_id AND iec.lang = ie.lang
     WHERE ite.item_id LIKE 'W%'
     GROUP BY ite.item_id, ie.lang
  ) ef ON wiw.w_id = ef.item_id AND ef.lang = i.lang
 ORDER BY i.text;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_weapons_v_wid_lang_uidx ON wcc_item_weapons_v (w_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_weapons_v_lang_dlc_idx ON wcc_item_weapons_v (lang, dlc_id);



-- <<< END sql/items/009_wcc_item_weapons_v.sql

-- >>> BEGIN sql/items/010_wcc_item_armors_v.sql

\echo '010_wcc_item_armors_v.sql'
-- Materialized view for shop UI (armors catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.

DROP MATERIALIZED VIEW IF EXISTS wcc_item_armors_v;

CREATE MATERIALIZED VIEW wcc_item_armors_v AS
SELECT ia.a_id
     , ia.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , iname.text AS armor_name
     , ibp.text AS body_part
     , iac.text AS armor_class
     , coalesce(ia.stopping_power, 0) AS stopping_power
     , coalesce(ia.enhancements, 0) AS enhancements
     , coalesce(ia.encumbrance, 0) AS encumbrance
     , coalesce(ia.weight, 0) AS weight
     , coalesce(ia.price, 0) AS price
     , iav.text AS availability
     , icb.text AS crafted_by
     , ef.effect_names
     , ef.effect_descriptions
     , idesc.text AS armor_description
     , iname.lang
  FROM wcc_item_armors ia
  JOIN i18n_text iname ON iname.id = ia.name_id
  LEFT JOIN i18n_text idesc ON idesc.id = ia.description_id AND idesc.lang = iname.lang
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = ia.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = iname.lang
  LEFT JOIN i18n_text ibp ON ibp.id = ia.body_part_id AND ibp.lang = iname.lang
  LEFT JOIN i18n_text iac ON iac.id = ia.armor_class_id AND iac.lang = iname.lang
  LEFT JOIN i18n_text iav ON iav.id = ia.availability_id AND iav.lang = iname.lang
  LEFT JOIN i18n_text icb ON icb.id = ia.crafted_by_id AND icb.lang = iname.lang
  LEFT JOIN (
    SELECT ite.item_id
         , ie.lang
         , string_agg(
             replace(coalesce(ie.text, ''), '<mod>', coalesce(ite.modifier::text, ''))
             || coalesce(nullif('[' || coalesce(iec.text, '') || ']', '[]'), ''),
             E'\n'
             ORDER BY ite.e_e_id
           ) AS effect_names
         , string_agg(CASE WHEN e.description_id is not null then
             replace(coalesce(ie.text, ''), '<mod>', coalesce(ite.modifier::text, ''))
             || ' -' || coalesce(nullif(' [' || coalesce(iec.text, '') || ']', ' []'), '')
             || ' ' || replace(coalesce(ide.text, ''), '<mod>', coalesce(ite.modifier::text, ''))
			 end,
             E'\n'
             ORDER BY ite.e_e_id
           ) AS effect_descriptions
      FROM wcc_item_to_effects ite
      LEFT JOIN wcc_item_effects e ON e.e_id = ite.e_e_id
      LEFT JOIN i18n_text ie ON ie.id = e.name_id
      LEFT JOIN i18n_text ide ON ide.id = e.description_id AND ide.lang = ie.lang
      LEFT JOIN wcc_item_effect_conditions ec ON ec.ec_id = ite.ec_ec_id
      LEFT JOIN i18n_text iec ON iec.id = ec.description_id AND iec.lang = ie.lang
     WHERE ite.item_id LIKE 'A%'
     GROUP BY ite.item_id, ie.lang
  ) ef ON ia.a_id = ef.item_id AND ef.lang = iname.lang
 ORDER BY iname.text;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_armors_v_aid_lang_uidx ON wcc_item_armors_v (a_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_armors_v_lang_dlc_idx ON wcc_item_armors_v (lang, dlc_id);



-- <<< END sql/items/010_wcc_item_armors_v.sql

-- >>> BEGIN sql/items/011_wcc_item_mutagens.sql

\echo '011_wcc_item_mutagens.sql'
CREATE TABLE IF NOT EXISTS wcc_item_mutagens (
    m_id            varchar(10) PRIMARY KEY,          -- e.g. 'M001'
    dlc_dlc_id      varchar(64) NOT NULL REFERENCES wcc_dlcs(dlc_id), -- source_id (core, hb, dlc_*, exp_*)

    name_id         uuid NOT NULL,                    -- ck_id('witcher_cc.items.mutagen.name.'||m_id)

    -- Reused dictionary fields (see 001_wcc_items_dict.sql)
    color_id        uuid NULL,                        -- ck_id('mutagen.color.*')
    availability_id uuid NULL,                        -- ck_id('availability.*')

    effect_id       uuid NOT NULL,                    -- ck_id('witcher_cc.items.mutagen.effect.'||m_id)
    alchemy_dc      integer NOT NULL,
    minor_mutation_id uuid NOT NULL,                 -- ck_id('witcher_cc.items.mutagen.minor_mutation.'||m_id)
    price           integer NOT NULL DEFAULT 0
);

COMMENT ON TABLE wcc_item_mutagens IS
