\echo '012_wcc_item_potions.sql'
CREATE TABLE IF NOT EXISTS wcc_item_potions (
    p_id            varchar(10) PRIMARY KEY,          -- e.g. 'P001'
    dlc_dlc_id      varchar(64) NOT NULL REFERENCES wcc_dlcs(dlc_id), -- source_id (core, hb, dlc_*, exp_*)

    name_id         uuid NOT NULL,                    -- ck_id('witcher_cc.items.potion.name.'||p_id)

    -- Reused dictionary fields (see 001_wcc_items_dict.sql)
    group_id        uuid NULL,                        -- ck_id('reciples.group.*')
    availability_id uuid NULL,                        -- ck_id('availability.*')

    weight          numeric(12,1) NULL,
    price           integer NULL,                     -- price_potion from recipe
    time_effect_val text NULL,
    time_effect_unit_id uuid NULL,                   -- ck_id('time.unit.*')
    toxicity        text NULL,

    effect_id       uuid NOT NULL                     -- ck_id('witcher_cc.items.potion.effect.'||p_id) - same as description_id from recipe
);

COMMENT ON TABLE wcc_item_potions IS
  '–ì–æ—Ç–æ–≤—ã–µ –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–∏–µ —Å–æ—Å—Ç–∞–≤—ã (–∑–µ–ª—å—è, –º–∞—Å–ª–∞, –æ—Ç–≤–∞—Ä—ã –∏ —Ç.–¥.). –ò—Å—Ç–æ—á–Ω–∏–∫ (DLC) —á–µ—Ä–µ–∑ wcc_dlcs, –ª–æ–∫–∞–ª–∏–∑—É–µ–º—ã–µ –ø–æ–ª—è —á–µ—Ä–µ–∑ i18n_text UUID (ck_id). –ì—Ä—É–ø–ø–∞/–¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç—å/–µ–¥–∏–Ω–∏—Ü—ã –≤—Ä–µ–º–µ–Ω–∏ ‚Äî –∏–∑ –æ–±—â–µ–≥–æ —Å–ª–æ–≤–∞—Ä—è (001_wcc_items_dict.sql).';

COMMENT ON COLUMN wcc_item_potions.dlc_dlc_id IS
  'FK –Ω–∞ wcc_dlcs.dlc_id (–∏—Å—Ç–æ—á–Ω–∏–∫/–ø–∞–∫–µ—Ç: core/hb/dlc_*/exp_*).';

COMMENT ON COLUMN wcc_item_potions.name_id IS
  'i18n UUID –¥–ª—è –Ω–∞–∑–≤–∞–Ω–∏—è —Å–æ—Å—Ç–∞–≤–∞. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''witcher_cc.items.potion.name.''||p_id).';

COMMENT ON COLUMN wcc_item_potions.effect_id IS
  'i18n UUID –¥–ª—è –æ–ø–∏—Å–∞–Ω–∏—è —ç—Ñ—Ñ–µ–∫—Ç–∞ —Å–æ—Å—Ç–∞–≤–∞. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç—Å—è –¥–µ—Ç–µ—Ä–º–∏–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ: ck_id(''witcher_cc.items.potion.effect.''||p_id).';

-- Generate potions from recipes (excluding medicine group, all with price 0)
WITH ins_i18n AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    -- Potion names (copy from recipe names)
    SELECT ck_id('witcher_cc.items.potion.name.P' || substring(ir.r_id from 2)),
           'items',
           'potion_names',
           iname.lang,
           iname.text
      FROM wcc_item_recipes ir
      JOIN i18n_text iname ON iname.id = ir.name_id
     WHERE (ir.group_id != ck_id('reciples.group.medicine') OR ir.group_id IS NULL)
    UNION ALL
    -- Potion effects (copy from recipe descriptions)
    SELECT ck_id('witcher_cc.items.potion.effect.P' || substring(ir.r_id from 2)),
           'items',
           'potion_effects',
           idesc.lang,
           idesc.text
      FROM wcc_item_recipes ir
      JOIN i18n_text idesc ON idesc.id = ir.description_id
     WHERE (ir.group_id != ck_id('reciples.group.medicine') OR ir.group_id IS NULL)
  ) foo
  ON CONFLICT (id, lang) DO NOTHING
)
INSERT INTO wcc_item_potions (
  p_id, dlc_dlc_id, name_id,
  group_id, availability_id,
  weight, price,
  time_effect_val, time_effect_unit_id,
  toxicity,
  effect_id
)
SELECT 'P' || substring(ir.r_id from 2) AS p_id
     , ir.dlc_dlc_id
     , ck_id('witcher_cc.items.potion.name.P' || substring(ir.r_id from 2)) AS name_id
     , ir.group_id
     , ir.availability_id
     , ir.weight
     , 0 AS price
     , ir.time_effect_val
     , ir.time_effect_unit_id
     , ir.toxicity
     , ck_id('witcher_cc.items.potion.effect.P' || substring(ir.r_id from 2)) AS effect_id
  FROM wcc_item_recipes ir
 WHERE (ir.group_id != ck_id('reciples.group.medicine') OR ir.group_id IS NULL)
ON CONFLICT (p_id) DO UPDATE
SET
  dlc_dlc_id = EXCLUDED.dlc_dlc_id,
  name_id = EXCLUDED.name_id,
  group_id = EXCLUDED.group_id,
  availability_id = EXCLUDED.availability_id,
  weight = EXCLUDED.weight,
  price = EXCLUDED.price,
  time_effect_val = EXCLUDED.time_effect_val,
  time_effect_unit_id = EXCLUDED.time_effect_unit_id,
  toxicity = EXCLUDED.toxicity,
  effect_id = EXCLUDED.effect_id;


-- <<< END sql/items/012_wcc_item_potions.sql

-- >>> BEGIN sql/items/013_wcc_item_general_gear_v.sql

\echo '013_wcc_item_general_gear_v.sql'
-- Materialized view for shop UI (general gear catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.

DROP MATERIALIZED VIEW IF EXISTS wcc_item_general_gear_v;

CREATE MATERIALIZED VIEW wcc_item_general_gear_v AS
SELECT ig.t_id
     , ig.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , iname.text AS gear_name
     , igroup.text AS group_name
     , isubgroup.text AS subgroup_name
     , coalesce(ig.weight, 0) AS weight
     , coalesce(ig.price, 0) AS price
     , iav.text AS availability
     , iconc.text AS concealment
     , idesc.text AS gear_description
     , iname.lang
  FROM wcc_item_general_gear ig
  JOIN i18n_text iname ON iname.id = ig.name_id
  LEFT JOIN i18n_text idesc ON idesc.id = ig.description_id AND idesc.lang = iname.lang
  LEFT JOIN i18n_text isubgroup ON isubgroup.id = ig.subgroup_name_id AND isubgroup.lang = iname.lang
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = ig.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = iname.lang
  LEFT JOIN i18n_text igroup ON igroup.id = ig.group_key_id AND igroup.lang = iname.lang
  LEFT JOIN i18n_text iav ON iav.id = ig.availability_id AND iav.lang = iname.lang
  LEFT JOIN i18n_text iconc ON iconc.id = ig.concealment_id AND iconc.lang = iname.lang
 ORDER BY isubgroup.text, iname.text;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_general_gear_v_tid_lang_uidx ON wcc_item_general_gear_v (t_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_general_gear_v_lang_dlc_idx ON wcc_item_general_gear_v (lang, dlc_id);


-- <<< END sql/items/013_wcc_item_general_gear_v.sql

