ins_i18n AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    -- Ingredient names RU
    SELECT ck_id('witcher_cc.items.ingredient.name.'||rd.i_id),
           'items',
           'ingredient_names',
           'ru',
           rd.name_ru
      FROM raw_data rd
     WHERE nullif(rd.name_ru,'') IS NOT NULL
    UNION ALL
    -- Ingredient names EN
    SELECT ck_id('witcher_cc.items.ingredient.name.'||rd.i_id),
           'items',
           'ingredient_names',
           'en',
           rd.name_en
      FROM raw_data rd
     WHERE nullif(rd.name_en,'') IS NOT NULL
  ) foo
  ON CONFLICT (id, lang) DO NOTHING
)
INSERT INTO wcc_item_ingredients (
  i_id, dlc_dlc_id, name_id,
  group_id, availability_id, ingredient_id,
  harvesting_complexity, weight, price
)
SELECT rd.i_id
     , rd.source_id AS dlc_dlc_id
     , ck_id('witcher_cc.items.ingredient.name.'||rd.i_id) AS name_id
     , CASE WHEN rd.group_key IS NOT NULL THEN ck_id(rd.group_key) ELSE NULL END AS group_id
     , CASE WHEN rd.availability_key IS NOT NULL THEN ck_id(rd.availability_key) ELSE NULL END AS availability_id
     , CASE WHEN rd.ingredient_key IS NOT NULL THEN ck_id(rd.ingredient_key) ELSE NULL END AS ingredient_id
     , rd.harvesting_complexity
     , CAST(NULLIF(REPLACE(rd.weight, ',', '.'), '') AS numeric) AS weight
     , rd.price
  FROM raw_data rd
ON CONFLICT (i_id) DO UPDATE
SET
  dlc_dlc_id = EXCLUDED.dlc_dlc_id,
  name_id = EXCLUDED.name_id,
  group_id = EXCLUDED.group_id,
  availability_id = EXCLUDED.availability_id,
  ingredient_id = EXCLUDED.ingredient_id,
  harvesting_complexity = EXCLUDED.harvesting_complexity,
  weight = EXCLUDED.weight,
  price = EXCLUDED.price;




-- <<< END sql/items/014_wcc_item_ingredients.sql

-- >>> BEGIN sql/items/015_wcc_item_recipes_v.sql

\echo '015_wcc_item_recipes_v.sql'
-- Materialized view for shop UI (recipes catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.
-- Filters out medicine group (reciples.group.medicine).

DROP MATERIALIZED VIEW IF EXISTS wcc_item_recipes_v;

CREATE MATERIALIZED VIEW wcc_item_recipes_v AS
SELECT ir.r_id
     , ir.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , iname.text AS recipe_name
     , igroup.text AS recipe_group
     , icl.text AS craft_level
     , coalesce(ir.weight, 0) AS weight_potion
     , coalesce(ir.minimal_ingredients_cost, 0) AS minimal_ingredients_cost
     , coalesce(ir.price_potion, 0) AS price_potion
     , coalesce(ir.price_formula, 0) AS price_formula
     , coalesce(ir.complexity, 0) AS complexity
     , CASE 
         WHEN ir.time_craft_val IS NOT NULL AND ir.time_craft_unit_id IS NOT NULL THEN
           ir.time_craft_val || ' ' || coalesce(itcu.text, '')
         WHEN ir.time_craft_val IS NOT NULL THEN ir.time_craft_val
         ELSE NULL
       END AS time_craft
     , CASE 
         WHEN ir.time_effect_val IS NOT NULL AND ir.time_effect_unit_id IS NOT NULL THEN
           ir.time_effect_val || ' ' || coalesce(iteu.text, '')
         WHEN ir.time_effect_val IS NOT NULL THEN ir.time_effect_val
         ELSE NULL
       END AS time_effect
     , ir.toxicity
     , formula_ingredients.formula_text AS formula
     , iav.text AS availability
     , idesc.text AS recipe_description
     , iname.lang
  FROM wcc_item_recipes ir
  JOIN i18n_text iname ON iname.id = ir.name_id
  LEFT JOIN i18n_text idesc ON idesc.id = ir.description_id AND idesc.lang = iname.lang
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = ir.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = iname.lang
  LEFT JOIN i18n_text igroup ON igroup.id = ir.group_id AND igroup.lang = iname.lang
  LEFT JOIN i18n_text icl ON icl.id = ir.craft_level_id AND icl.lang = iname.lang
  LEFT JOIN i18n_text iav ON iav.id = ir.availability_id AND iav.lang = iname.lang
  LEFT JOIN i18n_text itcu ON itcu.id = ir.time_craft_unit_id AND itcu.lang = iname.lang
  LEFT JOIN i18n_text iteu ON iteu.id = ir.time_effect_unit_id AND iteu.lang = iname.lang
  LEFT JOIN (
    -- Expand formula array, join on i18n, then group back as readable list
    SELECT ir2.r_id
         , i18n.lang
         , string_agg(i18n.text, E',\n' ORDER BY pos) AS formula_text
      FROM wcc_item_recipes ir2
      CROSS JOIN LATERAL unnest(ir2.formula) WITH ORDINALITY AS formula_entry(formula_uuid, pos)
      JOIN i18n_text i18n ON i18n.id = formula_entry.formula_uuid AND i18n.entity = 'items' AND i18n.entity_field = 'dict'
     WHERE ir2.formula IS NOT NULL
     GROUP BY ir2.r_id, i18n.lang
  ) formula_ingredients ON ir.r_id = formula_ingredients.r_id AND formula_ingredients.lang = iname.lang
 WHERE ir.group_id != ck_id('reciples.group.medicine') OR ir.group_id IS NULL
 ORDER BY iname.text;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_recipes_v_rid_lang_uidx ON wcc_item_recipes_v (r_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_recipes_v_lang_dlc_idx ON wcc_item_recipes_v (lang, dlc_id);
CREATE INDEX IF NOT EXISTS wcc_item_recipes_v_lang_group_idx ON wcc_item_recipes_v (lang, recipe_group);


-- <<< END sql/items/015_wcc_item_recipes_v.sql

-- >>> BEGIN sql/items/016_wcc_item_ingredients_v.sql

\echo '016_wcc_item_ingredients_v.sql'
-- Materialized view for shop UI (ingredients catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.
-- Use filters in shop metadata to split into alchemy (alchemy_substance IS NOT NULL) 
-- and craft (alchemy_substance IS NULL) ingredients.

DROP MATERIALIZED VIEW IF EXISTS wcc_item_ingredients_v;

CREATE MATERIALIZED VIEW wcc_item_ingredients_v AS
SELECT ii.i_id
     , ii.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , iname.text AS ingredient_name
     , igrp.text AS ingredient_group
     , iav.text AS availability
     , iingr.text AS alchemy_substance
     , coalesce(ii.harvesting_complexity, 0) AS harvesting_complexity
     , coalesce(ii.weight, 0) AS weight
     , coalesce(ii.price, 0) AS price
     , iname.lang
  FROM wcc_item_ingredients ii
  JOIN i18n_text iname ON iname.id = ii.name_id
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = ii.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = iname.lang
  LEFT JOIN i18n_text igrp ON igrp.id = ii.group_id AND igrp.lang = iname.lang
  LEFT JOIN i18n_text iav ON iav.id = ii.availability_id AND iav.lang = iname.lang
  LEFT JOIN i18n_text iingr ON iingr.id = ii.ingredient_id AND iingr.lang = iname.lang
 ORDER BY iname.text;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_ingredients_v_iid_lang_uidx ON wcc_item_ingredients_v (i_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_ingredients_v_lang_dlc_idx ON wcc_item_ingredients_v (lang, dlc_id);




-- <<< END sql/items/016_wcc_item_ingredients_v.sql

-- >>> BEGIN sql/items/017_wcc_item_mutagens_v.sql

\echo '017_wcc_item_mutagens_v.sql'
-- Materialized view for shop UI (mutagens catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.

DROP MATERIALIZED VIEW IF EXISTS wcc_item_mutagens_v;

CREATE MATERIALIZED VIEW wcc_item_mutagens_v AS
SELECT im.m_id
     , im.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , iname.text AS mutagen_name
     , icolor.text AS mutagen_color
     , ieffect.text AS effect
     , im.alchemy_dc
     , iminor.text AS minor_mutation
     , coalesce(im.price, 0) AS price
     , iav.text AS availability
     , iname.lang
  FROM wcc_item_mutagens im
  JOIN i18n_text iname ON iname.id = im.name_id
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = im.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = iname.lang
  LEFT JOIN i18n_text icolor ON icolor.id = im.color_id AND icolor.lang = iname.lang
  LEFT JOIN i18n_text ieffect ON ieffect.id = im.effect_id AND ieffect.lang = iname.lang
  LEFT JOIN i18n_text iminor ON iminor.id = im.minor_mutation_id AND iminor.lang = iname.lang
  LEFT JOIN i18n_text iav ON iav.id = im.availability_id AND iav.lang = iname.lang
 ORDER BY iname.text;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_mutagens_v_mid_lang_uidx ON wcc_item_mutagens_v (m_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_mutagens_v_lang_dlc_idx ON wcc_item_mutagens_v (lang, dlc_id);
CREATE INDEX IF NOT EXISTS wcc_item_mutagens_v_lang_color_idx ON wcc_item_mutagens_v (lang, mutagen_color);


-- <<< END sql/items/017_wcc_item_mutagens_v.sql

-- >>> BEGIN sql/items/018_wcc_item_potions_v.sql

\echo '018_wcc_item_potions_v.sql'
-- Materialized view for shop UI (potions catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.

DROP MATERIALIZED VIEW IF EXISTS wcc_item_potions_v;

CREATE MATERIALIZED VIEW wcc_item_potions_v AS
SELECT ip.p_id
     , ip.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , iname.text AS potion_name
     , igroup.text AS potion_group
     , coalesce(ip.weight, 0) AS weight
     , coalesce(ip.price, 0) AS price
     , CASE 
         WHEN ip.time_effect_val IS NOT NULL AND ip.time_effect_unit_id IS NOT NULL THEN
           ip.time_effect_val || ' ' || coalesce(iteu.text, '')
         WHEN ip.time_effect_val IS NOT NULL THEN ip.time_effect_val
         ELSE NULL
       END AS time_effect
     , ip.toxicity
     , iav.text AS availability
     , ieffect.text AS effect
     , iname.lang
  FROM wcc_item_potions ip
  JOIN i18n_text iname ON iname.id = ip.name_id
  LEFT JOIN i18n_text ieffect ON ieffect.id = ip.effect_id AND ieffect.lang = iname.lang
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = ip.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = iname.lang
  LEFT JOIN i18n_text igroup ON igroup.id = ip.group_id AND igroup.lang = iname.lang
  LEFT JOIN i18n_text iav ON iav.id = ip.availability_id AND iav.lang = iname.lang
  LEFT JOIN i18n_text iteu ON iteu.id = ip.time_effect_unit_id AND iteu.lang = iname.lang
ORDER BY iname.text;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_potions_v_pid_lang_uidx ON wcc_item_potions_v (p_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_potions_v_lang_dlc_idx ON wcc_item_potions_v (lang, dlc_id);
CREATE INDEX IF NOT EXISTS wcc_item_potions_v_lang_group_idx ON wcc_item_potions_v (lang, potion_group);


