  ('WT011','–¢–æ—Ä–≥–æ–≤—ã–π —Ñ—É—Ä–≥–æ–Ω','Merchant Wagon','dlc_sh_wat','vehicles.subgroup.attached',NULL,-1,'Animal''s -3',1,3,60,400,500),
  ('WT012','–•–∏–∂–∏–Ω–∞ –ø–∞—Å—Ç—É—Ö–∞','Shepherd''s Hut','dlc_sh_wat','vehicles.subgroup.attached',NULL,-2,'Animal''s -5',4,2,80,600,700),
  ('WT013','–ë–æ–µ–≤–æ–π —Ñ—É—Ä–≥–æ–Ω','War Wagon','dlc_sh_wat','vehicles.subgroup.attached',NULL,-2,'Animal''s -5',0,4,100,800,700)
),
ins_i18n AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    -- Vehicle names
    SELECT ck_id('witcher_cc.items.vehicle.name.'||rd.wt_id),
           'items',
           'vehicle_names',
           'ru',
           rd.name_ru
      FROM raw_data rd
     WHERE nullif(rd.name_ru,'') IS NOT NULL
    UNION ALL
    SELECT ck_id('witcher_cc.items.vehicle.name.'||rd.wt_id),
           'items',
           'vehicle_names',
           'en',
           rd.name_en
      FROM raw_data rd
     WHERE nullif(rd.name_en,'') IS NOT NULL
  ) foo
  ON CONFLICT (id, lang) DO NOTHING
)
INSERT INTO wcc_item_vehicles (
  wt_id, dlc_dlc_id, name_id,
  subgroup_id,
  base, control_modifier, speed,
  occupancy, upgrade_slots,
  hp,
  weight, price
)
SELECT rd.wt_id
     , rd.source_id AS dlc_dlc_id
     , ck_id('witcher_cc.items.vehicle.name.'||rd.wt_id) AS name_id
     , CASE WHEN rd.subgroup_key IS NOT NULL THEN ck_id(rd.subgroup_key) ELSE NULL END AS subgroup_id
     , rd.base
     , rd.control_modifier
     , rd.speed
     , rd.occupancy
     , rd.upgrade_slots
     , rd.hp
     , rd.weight
     , rd.price
  FROM raw_data rd
ON CONFLICT (wt_id) DO UPDATE
SET
  dlc_dlc_id = EXCLUDED.dlc_dlc_id,
  name_id = EXCLUDED.name_id,
  subgroup_id = EXCLUDED.subgroup_id,
  base = EXCLUDED.base,
  control_modifier = EXCLUDED.control_modifier,
  speed = EXCLUDED.speed,
  occupancy = EXCLUDED.occupancy,
  upgrade_slots = EXCLUDED.upgrade_slots,
  hp = EXCLUDED.hp,
  weight = EXCLUDED.weight,
  price = EXCLUDED.price;


