  , ('B256', 'T129', '–ú–∞–≥–∏—á–µ—Å–∫–∏–π –ø—Ä–æ—Ç–µ–∑', 'Magical Prosthesis', 'dlc_wpaw', 'general_gear.group.transport', 'craft.level.master', 10, 8, 'time.unit.hour', 'I003 (3), I004 (2), I025 (4), I026 (4), I027 (2), I006 (3), I020 (1), I043 (1)', 375, 750, 500)
  , ('B257', 'T130', '–í–µ–¥—å–º–∞—á–∏–π –ø—Ä–æ—Ç–µ–∑', 'Witcher Prosthesis', 'dlc_wpaw', 'general_gear.group.transport', 'craft.level.master', 22, 9, 'time.unit.hour', 'I003 (2), I004 (2), I025 (2), I026 (6), I027 (3), I006 (2), I019 (1), I029 (1), I042 (1), I043 (2)', 600, 1200, 800)
  , ('B258', 'T131', '–ü—Ä–æ—Ç–µ–∑-–ø—Ä–æ–≤–æ–¥–Ω–∏–∫', 'Conduit Prosthesis', 'dlc_wpaw', 'general_gear.group.transport', 'craft.level.master', 24, 10, 'time.unit.hour', 'I003 (4), I004 (2), I025 (4), I026 (8), I027 (4), I034 (1), I006 (3), I020 (1), I085 (1), I043 (1), I013 (1)', 750, 1500, 1000)
),
ins_i18n_tech AS (
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT * FROM (
    -- T041: includes non-item operation in components
    SELECT ck_id('witcher_cc.items.blueprint.tech_val.T041_01'), 'items', 'blueprint_tech_val', 'ru', '–û–±—Ä–∞–±–æ—Ç–∫–∞ –≤ –º–µ—Å—Ç–µ —Å–∏–ª—ã (1—á) (1)'
    UNION ALL
    SELECT ck_id('witcher_cc.items.blueprint.tech_val.T041_01'), 'items', 'blueprint_tech_val', 'en', '–û–±—Ä–∞–±–æ—Ç–∫–∞ –≤ –º–µ—Å—Ç–µ —Å–∏–ª—ã (1—á) (1)'
    UNION ALL
    -- T044: free-form text (commas inside)
    SELECT ck_id('witcher_cc.items.blueprint.tech_val.T044_01'), 'items', 'blueprint_tech_val', 'ru', '3 —Ä–∞–∑–ª–∏—á–Ω—ã—Ö –º–∞—Ç–µ—Ä–∏–∞–ª–∞, –ò—Å–ø–æ–ª—å–∑—É–µ–º—ã–π –Ω–∞–≤—ã–∫: –ò—Å–∫—É—Å—Å—Ç–≤–æ (Fine Arts)'
    UNION ALL
    SELECT ck_id('witcher_cc.items.blueprint.tech_val.T044_01'), 'items', 'blueprint_tech_val', 'en', '3 —Ä–∞–∑–ª–∏—á–Ω—ã—Ö –º–∞—Ç–µ—Ä–∏–∞–ª–∞, –ò—Å–ø–æ–ª—å–∑—É–µ–º—ã–π –Ω–∞–≤—ã–∫: –ò—Å–∫—É—Å—Å—Ç–≤–æ (Fine Arts)'
    UNION ALL
    -- W156: "–ò–õ–ò" token
    SELECT ck_id('witcher_cc.items.blueprint.tech_val.W156_01'), 'items', 'blueprint_tech_val', 'ru', '–ò–õ–ò'
    UNION ALL
    SELECT ck_id('witcher_cc.items.blueprint.tech_val.W156_01'), 'items', 'blueprint_tech_val', 'en', 'OR'
  ) foo
  ON CONFLICT (id, lang) DO NOTHING
),
ins_i18n_blueprint_groups AS (
  -- Create generalized group labels like "–û—Ä—É–∂–∏–µ - –ê—Ä–±–∞–ª–µ—Ç—ã", "–ë—Ä–æ–Ω—è - –ù–æ–≥–∏"
  -- Stored under deterministic UUID: ck_id('blueprint_groups.'||ck_id(<group_key>)::text)
  INSERT INTO i18n_text (id, entity, entity_field, lang, text)
  SELECT
    ck_id('blueprint_groups.' || ck_id(gk.group_key)::text) AS id,
    'items' AS entity,
    'blueprint_groups' AS entity_field,
    gk.lang,
    (
      CASE
        WHEN gk.group_key LIKE 'weapons.%' THEN (CASE WHEN gk.lang = 'ru' THEN '–û—Ä—É–∂–∏–µ' ELSE 'Weapons' END)
        WHEN gk.group_key LIKE 'bodypart.%' THEN (CASE WHEN gk.lang = 'ru' THEN '–ë—Ä–æ–Ω—è' ELSE 'Armor' END)
        WHEN gk.group_key LIKE 'ingredients.%' THEN (CASE WHEN gk.lang = 'ru' THEN '–ò–Ω–≥—Ä–µ–¥–∏–µ–Ω—Ç—ã' ELSE 'Ingredients' END)
        WHEN gk.group_key LIKE 'upgrades.%' THEN (CASE WHEN gk.lang = 'ru' THEN '–£–ª—É—á—à–µ–Ω–∏—è' ELSE 'Upgrades' END)
        WHEN gk.group_key LIKE 'general_gear.group.%' THEN (CASE WHEN gk.lang = 'ru' THEN '–û–±—ã—á–Ω—ã–µ –≤–µ—â–∏' ELSE 'General Gear' END)
        WHEN gk.group_key LIKE 'reciples.group.%' THEN (CASE WHEN gk.lang = 'ru' THEN '–†–µ—Ü–µ–ø—Ç—ã' ELSE 'Recipes' END)
        ELSE (CASE WHEN gk.lang = 'ru' THEN '–î—Ä—É–≥–æ–µ' ELSE 'Other' END)
      END
      || ' - ' || gk.group_text
    ) AS text
  FROM (
    SELECT DISTINCT
      rd.group_key,
      l.lang,
      it.text AS group_text
    FROM raw_data rd
    CROSS JOIN (VALUES ('ru'::text), ('en'::text)) AS l(lang)
    JOIN i18n_text it
      ON it.id = ck_id(rd.group_key)
     AND it.lang = l.lang
     AND it.entity = 'items'
     AND it.entity_field = 'dict'
    WHERE rd.group_key IS NOT NULL
      AND rd.group_key <> ''
  ) gk
  ON CONFLICT (id, lang) DO NOTHING
)
INSERT INTO wcc_item_blueprints (
  b_id, item_id, dlc_dlc_id,
  name_id, name_ru, name_en,
  group_id, craft_level_id,
  difficulty_check, time_value, time_unit_id,
  components,
  price_components, price_blueprint, price_item
)
SELECT rd.b_id
     , rd.item_id
     , rd.dlc_id AS dlc_dlc_id
     , CASE
         WHEN right(rd.item_id, 1) = 'B' THEN NULL
         ELSE CASE
           WHEN rd.item_id ~ '^A[0-9]{3}$' THEN ck_id('witcher_cc.items.armor.name.'||rd.item_id)
           WHEN rd.item_id ~ '^I[0-9]{3}$' THEN ck_id('witcher_cc.items.ingredient.name.'||rd.item_id)
           WHEN rd.item_id ~ '^T[0-9]{3}$' THEN ck_id('witcher_cc.items.general_gear.name.'||rd.item_id)
           WHEN rd.item_id ~ '^U[0-9]{3}$' THEN ck_id('witcher_cc.items.upgrade.name.'||rd.item_id)
           WHEN rd.item_id ~ '^W[0-9]{3}$' THEN ck_id('witcher_cc.items.weapon.name.'||rd.item_id)
           WHEN rd.item_id ~ '^R[0-9]{3}$' THEN ck_id('witcher_cc.items.recipe.name.'||rd.item_id)
           ELSE NULL
         END
       END AS name_id
     , CASE WHEN right(rd.item_id, 1) = 'B' THEN nullif(rd.name_ru, '') ELSE NULL END AS name_ru
     , CASE WHEN right(rd.item_id, 1) = 'B' THEN nullif(rd.name_en, '') ELSE NULL END AS name_en
     , CASE WHEN rd.group_key IS NOT NULL THEN ck_id(rd.group_key) ELSE NULL END AS group_id
     , CASE WHEN rd.craft_level_key IS NOT NULL THEN ck_id(rd.craft_level_key) ELSE NULL END AS craft_level_id
     , rd.difficulty_check
     , rd.time_value
     , CASE WHEN rd.time_unit_key IS NOT NULL THEN ck_id(rd.time_unit_key) ELSE NULL END AS time_unit_id
     , CASE
         WHEN rd.components_raw IS NULL OR trim(rd.components_raw) = '' THEN NULL
         WHEN rd.item_id = 'T044' THEN jsonb_build_array(
           jsonb_build_object('id', ck_id('witcher_cc.items.blueprint.tech_val.T044_01')::text, 'qty', NULL)
         )
         ELSE (
           SELECT
             CASE
               WHEN jsonb_agg(comp.obj ORDER BY comp.pos) IS NULL THEN NULL
               ELSE jsonb_agg(comp.obj ORDER BY comp.pos)
             END
           FROM (
             SELECT
               pieces.pos,
               jsonb_build_object(
                 'id',
                 (
                   CASE
                     WHEN pieces.is_or THEN ck_id('witcher_cc.items.blueprint.tech_val.W156_01')
                     ELSE
                       COALESCE(
                         -- normal item-id mapping by prefix
                         CASE
                           WHEN pieces.code ~ '^A[0-9]{3}$' THEN ck_id('witcher_cc.items.armor.name.'||pieces.code)
                           WHEN pieces.code ~ '^I[0-9]{3}$' THEN ck_id('witcher_cc.items.ingredient.name.'||pieces.code)
                           WHEN pieces.code ~ '^T[0-9]{3}$' THEN ck_id('witcher_cc.items.general_gear.name.'||pieces.code)
                           WHEN pieces.code ~ '^U[0-9]{3}$' THEN ck_id('witcher_cc.items.upgrade.name.'||pieces.code)
                           WHEN pieces.code ~ '^W[0-9]{3}$' THEN ck_id('witcher_cc.items.weapon.name.'||pieces.code)
                           WHEN pieces.code ~ '^R[0-9]{3}$' THEN ck_id('witcher_cc.items.recipe.name.'||pieces.code)
                           ELSE NULL
                         END,
                         -- known exception: T041 tech op in components
                         CASE WHEN rd.item_id = 'T041' THEN ck_id('witcher_cc.items.blueprint.tech_val.T041_01') ELSE NULL END
                       )
                   END
                 )::text,
                 'qty',
                 CASE
                   WHEN pieces.is_or THEN NULL
                   WHEN rd.item_id = 'T041' AND (
                     pieces.code IS NULL OR pieces.code !~ '^[AITUWR][0-9]{3}$'
                   ) THEN NULL
                   ELSE pieces.qty
                 END
               ) AS obj
             FROM (
               SELECT
                 -- explode by commas first
                 CASE
                   WHEN rd.item_id = 'W156' AND position('–ò–õ–ò' IN part.part_raw) > 0 THEN (part.ord * 10) + u.subpos
                   ELSE part.ord * 10
                 END AS pos,
                 u.is_or,
                 u.qty,
                 u.code
               FROM (
                 SELECT token AS part_raw, ord
                 FROM regexp_split_to_table(rd.components_raw, '\s*,\s*') WITH ORDINALITY AS s(token, ord)
               ) part
               CROSS JOIN LATERAL (
                 -- W156: split "‚Ä¶ –ò–õ–ò ‚Ä¶" into 3 logical components; otherwise keep as is
                 SELECT 1 AS subpos,
                        false AS is_or,
                        (regexp_match(trim(split_part(part.part_raw, '–ò–õ–ò', 1)), '\((\d+)\)\s*$'))[1]::int AS qty,
                        trim(regexp_replace(trim(split_part(part.part_raw, '–ò–õ–ò', 1)), '\s*\([^)]*\)\s*$', '')) AS code
                  WHERE rd.item_id = 'W156'
                    AND position('–ò–õ–ò' IN part.part_raw) > 0
                    AND nullif(trim(split_part(part.part_raw, '–ò–õ–ò', 1)), '') IS NOT NULL
                 UNION ALL
                 SELECT 2 AS subpos,
                        true AS is_or,
                        NULL::int AS qty,
                        NULL::text AS code
                  WHERE rd.item_id = 'W156'
                    AND position('–ò–õ–ò' IN part.part_raw) > 0
                 UNION ALL
                 SELECT 3 AS subpos,
                        false AS is_or,
                        (regexp_match(trim(split_part(part.part_raw, '–ò–õ–ò', 2)), '\((\d+)\)\s*$'))[1]::int AS qty,
                        trim(regexp_replace(trim(split_part(part.part_raw, '–ò–õ–ò', 2)), '\s*\([^)]*\)\s*$', '')) AS code
                  WHERE rd.item_id = 'W156'
                    AND position('–ò–õ–ò' IN part.part_raw) > 0
                    AND nullif(trim(split_part(part.part_raw, '–ò–õ–ò', 2)), '') IS NOT NULL
                 UNION ALL
                 SELECT 0 AS subpos,
                        false AS is_or,
                        (regexp_match(trim(part.part_raw), '\((\d+)\)\s*$'))[1]::int AS qty,
                        trim(regexp_replace(trim(part.part_raw), '\s*\([^)]*\)\s*$', '')) AS code
                  WHERE NOT (rd.item_id = 'W156' AND position('–ò–õ–ò' IN part.part_raw) > 0)
               ) u
             ) pieces
             WHERE (pieces.is_or OR (pieces.code IS NOT NULL AND pieces.code <> ''))
           ) comp
           WHERE (comp.obj->>'id') IS NOT NULL
         )
       END AS components
     , rd.price_components
     , rd.price_blueprint
     , rd.price_item
  FROM raw_data rd
ON CONFLICT (b_id) DO UPDATE
SET
  item_id = EXCLUDED.item_id,
  dlc_dlc_id = EXCLUDED.dlc_dlc_id,
  name_id = EXCLUDED.name_id,
  name_ru = EXCLUDED.name_ru,
  name_en = EXCLUDED.name_en,
  group_id = EXCLUDED.group_id,
  craft_level_id = EXCLUDED.craft_level_id,
  difficulty_check = EXCLUDED.difficulty_check,
  time_value = EXCLUDED.time_value,
  time_unit_id = EXCLUDED.time_unit_id,
  components = EXCLUDED.components,
  price_components = EXCLUDED.price_components,
  price_blueprint = EXCLUDED.price_blueprint,
  price_item = EXCLUDED.price_item;



-- <<< END sql/items/025_wcc_item_blueprints.sql

-- >>> BEGIN sql/items/026_wcc_item_blueprints_v.sql

\echo '026_wcc_item_blueprints_v.sql'
-- Materialized view for shop UI (blueprints catalog)
-- Provides localized rows per language, plus dlc_id for filtering by allowedDlcs.

CREATE MATERIALIZED VIEW wcc_item_blueprints_v AS
WITH langs AS (
  SELECT 'ru'::text AS lang
  UNION ALL
  SELECT 'en'::text AS lang
),
components_expanded AS (
  SELECT b.b_id
       , l.lang
       , comp_elt.ord AS pos
       , (comp_elt.val->>'id')::uuid AS comp_name_id
       , NULLIF(comp_elt.val->>'qty', '')::int AS qty
    FROM wcc_item_blueprints b
    CROSS JOIN langs l
    CROSS JOIN LATERAL (
      SELECT val, ord
        FROM jsonb_array_elements(COALESCE(b.components, '[]'::jsonb)) WITH ORDINALITY AS t(val, ord)
    ) comp_elt
),
components_pretty AS (
  SELECT ce.b_id
       , ce.lang
       , string_agg(
           CASE
             WHEN ce.qty IS NOT NULL THEN (i18n.text || ' (' || ce.qty::text || ')')
             ELSE i18n.text
           END,
           E',\n' ORDER BY ce.pos
         ) AS components
    FROM components_expanded ce
    LEFT JOIN i18n_text i18n
      ON i18n.id = ce.comp_name_id
     AND i18n.lang = ce.lang
   GROUP BY ce.b_id, ce.lang
)
SELECT b.b_id
     , b.item_id
     , b.dlc_dlc_id AS dlc_id
     , idlcs.text AS dlc
     , COALESCE(iname.text, CASE l.lang WHEN 'ru' THEN b.name_ru ELSE b.name_en END) AS blueprint_name
     , COALESCE(ibg.text, igroup.text) AS blueprint_group
     , icl.text AS craft_level
     , b.difficulty_check
     , CASE
         WHEN b.time_value IS NOT NULL AND b.time_unit_id IS NOT NULL THEN
           b.time_value::text || ' ' || COALESCE(itcu.text, '')
         WHEN b.time_value IS NOT NULL THEN b.time_value::text
         ELSE NULL
       END AS time_craft
     , cp.components
     , COALESCE(b.price_components, 0) AS price_components
     , COALESCE(b.price_blueprint, 0) AS price
     , COALESCE(b.price_item, 0) AS price_item
     , l.lang
  FROM wcc_item_blueprints b
  CROSS JOIN langs l
  JOIN wcc_dlcs dlcs ON dlcs.dlc_id = b.dlc_dlc_id
  JOIN i18n_text idlcs ON idlcs.id = dlcs.name_id AND idlcs.lang = l.lang
  LEFT JOIN i18n_text iname ON iname.id = b.name_id AND iname.lang = l.lang
  LEFT JOIN i18n_text igroup ON igroup.id = b.group_id AND igroup.lang = l.lang
  LEFT JOIN i18n_text ibg ON ibg.id = ck_id('blueprint_groups.' || b.group_id::text) AND ibg.lang = l.lang
  LEFT JOIN i18n_text icl ON icl.id = b.craft_level_id AND icl.lang = l.lang
  LEFT JOIN i18n_text itcu ON itcu.id = b.time_unit_id AND itcu.lang = l.lang
  LEFT JOIN components_pretty cp ON cp.b_id = b.b_id AND cp.lang = l.lang
 ORDER BY blueprint_name;

-- Helpful indexes for shop queries
CREATE UNIQUE INDEX IF NOT EXISTS wcc_item_blueprints_v_bid_lang_uidx ON wcc_item_blueprints_v (b_id, lang);
CREATE INDEX IF NOT EXISTS wcc_item_blueprints_v_lang_dlc_idx ON wcc_item_blueprints_v (lang, dlc_id);
CREATE INDEX IF NOT EXISTS wcc_item_blueprints_v_lang_group_idx ON wcc_item_blueprints_v (lang, blueprint_group);



-- <<< END sql/items/026_wcc_item_blueprints_v.sql

-- >>> BEGIN sql/items/027_wcc_magic_spells.sql

\echo '027_wcc_magic_spells.sql'
-- Magic spells (and Witcher Signs) from temp TSV

CREATE TABLE IF NOT EXISTS wcc_magic_spells (
  ms_id              varchar(10) PRIMARY KEY,  -- e.g. 'MS001'
  dlc_dlc_id         varchar(64) NOT NULL REFERENCES wcc_dlcs(dlc_id),

  level_id           uuid NULL,                -- ck_id('level.*')
  element_id         uuid NULL,                -- ck_id('element.*')
  form_id            uuid NULL,                -- ck_id('magic.form.*')
  effect_time_unit_id uuid NULL,               -- ck_id('time.unit.*')

  name_id            uuid NOT NULL,            -- ck_id('witcher_cc.magic.spell.name.'||ms_id)
  effect_id          uuid NOT NULL,            -- ck_id('witcher_cc.magic.spell.effect.'||ms_id)

  distance           text NULL,
  zone_size          text NULL,
  stamina_cast       text NULL,
  stamina_keeping    text NULL,
  damage             text NULL,
  effect_time_value  text NULL,

  defense_ids        uuid[] NULL,              -- array of ck_id('skill.*') / ck_id('parameter.*') used for defense
  type               text NULL                 -- 'spell' for mage spells, 'sign' for witcher signs
);

-- Add type column if it doesn't exist (migration for existing tables)
DO E'\nBEGIN\n  IF NOT EXISTS (\n    SELECT 1 FROM information_schema.columns\n    WHERE table_name = ''wcc_magic_spells'' AND column_name = ''type''\n  ) THEN\n    ALTER TABLE wcc_magic_spells ADD COLUMN type text NULL;\n  END IF;\nEND ';

