  ins_branch_names AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
    SELECT DISTINCT
      ck_id(meta.su_su_id || '.wcc_skills.branch.' || 
        LOWER(REPLACE(REPLACE(REPLACE(raw_prof_skills.branch_name, ' ', '_'), '/', '_'), '&', 'and')) || '.name') AS id
         , meta.entity, 'branch_name', raw_prof_skills.lang, raw_prof_skills.branch_name
      FROM raw_prof_skills
      CROSS JOIN meta
      WHERE raw_prof_skills.branch_name IS NOT NULL
    ON CONFLICT (id, lang) DO NOTHING
  )
INSERT INTO wcc_skills (skill_aid, skill_id, skill_name_id, skill_desc_id, param_param_id, skill_type, professional_number, branch_number, branch_name_id, is_difficult)
SELECT DISTINCT
  raw_prof_skills.skill_aid,
  raw_prof_skills.skill_id,
  ck_id(meta.su_su_id || '.wcc_skills.' || raw_prof_skills.skill_id || '.name') AS skill_name_id,
  NULL::uuid AS skill_desc_id,
  raw_prof_skills.param_id,
  raw_prof_skills.skill_type,
  raw_prof_skills.prof_num::integer AS professional_number,
  raw_prof_skills.branch_num::integer AS branch_number,
  CASE 
    WHEN raw_prof_skills.branch_name IS NOT NULL THEN
      ck_id(meta.su_su_id || '.wcc_skills.branch.' || 
        LOWER(REPLACE(REPLACE(REPLACE(raw_prof_skills.branch_name, ' ', '_'), '/', '_'), '&', 'and')) || '.name')
    ELSE NULL
  END AS branch_name_id,
  raw_prof_skills.is_difficult
FROM raw_prof_skills
CROSS JOIN meta
WHERE raw_prof_skills.lang = 'ru'
ON CONFLICT (skill_aid) DO NOTHING;

-- –í—Å—Ç–∞–≤–∫–∞ –æ–ø–∏—Å–∞–Ω–∏–π –æ–ø—Ä–µ–¥–µ–ª—è—é—â–∏—Ö –Ω–∞–≤—ã–∫–æ–≤ (main skills)
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id, 'wcc_skills' AS entity),
  raw_main_skill_descriptions AS (
    SELECT 'ru' AS lang, skill_id, description
      FROM (VALUES
        ('busking', '–ë–∞—Ä–¥ –≤–µ—Å—å–º–∞ –ø–æ–ª–µ–∑–µ–Ω –≤ –≥—Ä—É–ø–ø–µ, –æ—Å–æ–±–µ–Ω–Ω–æ –∫–æ–≥–¥–∞ —É –≤–∞—Å –Ω–µ —Ö–≤–∞—Ç–∞–µ—Ç –¥–µ–Ω–µ–≥. –ë–∞—Ä–¥ –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å —á–∞—Å –≤—Ä–µ–º–µ–Ω–∏ –∏ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–£–ª–∏—á–Ω–æ–≥–æ –≤—ã—Å—Ç—É–ø–ª–µ–Ω–∏—è</strong> –≤ —Ü–µ–Ω—Ç—Ä–µ –±–ª–∏–∂–∞–π—à–µ–≥–æ –≥–æ—Ä–æ–¥–∞. –†–µ–∑—É–ª—å—Ç–∞—Ç –±—Ä–æ—Å–∫–∞ ‚Äî —ç—Ç–æ —Å—É–º–º–∞, –∫–æ—Ç–æ—Ä—É—é –±–∞—Ä–¥ –∑–∞—Ä–∞–±–æ—Ç–∞–ª –∑–∞ –≤—Ä–µ–º—è —É–ª–∏—á–Ω–æ–≥–æ –≤—ã—Å—Ç—É–ø–ª–µ–Ω–∏—è. –ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–æ–≤–∞–ª –º–æ–∂–µ—Ç —Å–Ω–∏–∑–∏—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç –±—Ä–æ—Å–∫–∞. –û—Ç—Ä–∏—Ü–∞—Ç–µ–ª—å–Ω—ã–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç –æ–∑–Ω–∞—á–∞–µ—Ç, —á—Ç–æ –±–∞—Ä–¥ –Ω–µ —Ç–æ–ª—å–∫–æ –Ω–µ –∑–∞—Ä–∞–±–æ—Ç–∞–ª –¥–µ–Ω–µ–≥, –Ω–æ –∏ –±—ã–ª –æ—Å–≤–∏—Å—Ç–∞–Ω –º–µ—Å—Ç–Ω—ã–º–∏, —á—Ç–æ –¥–∞—ë—Ç –µ–º—É —à—Ç—Ä–∞—Ñ -2 –∫ –•–∞—Ä–∏–∑–º–µ –ø—Ä–∏ –∫–æ–Ω—Ç–∞–∫—Ç–µ —Å–æ –≤—Å–µ–º–∏ –≤ —ç—Ç–æ–º –≥–æ—Ä–æ–¥–µ –Ω–∞ –æ—Å—Ç–∞—Ç–æ–∫ –¥–Ω—è.<br><br>–ü—Ä–∏ –ø–æ–ø—ã—Ç–∫–µ –∑–∞–≤–æ—Ä–æ–∂–∏—Ç—å –ø—É–±–ª–∏–∫—É –Ω–∞ –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –±–æ–ª—å—à–æ–π –ø–ª–æ—â–∞–¥–∏ —Å –º–Ω–æ–∂–µ—Å—Ç–≤–æ–º –ª—é–¥–µ–π —É—Å–ø–µ—à–Ω–æ –ø—Ä–æ–π–¥–µ–Ω–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞ —Å–æ –°–õ 15 –ø–æ–∑–≤–æ–ª—è–µ—Ç —Å–æ–∑–¥–∞—Ç—å –≤–æ–∫—Ä—É–≥ –±–∞—Ä–¥–∞ –ø–ª–æ—Ç–Ω—É—é —Ç–æ–ª–ø—É, –¥–ª—è –ø—Ä–æ—Ö–æ–¥–∞ —á–µ—Ä–µ–∑ –∫–æ—Ç–æ—Ä—É—é —Ç—Ä–µ–±—É–µ—Ç—Å—è –ø—Ä–æ–≤–µ—Ä–∫–∞ –°–∏–ª—ã –∏–ª–∏ –ê—Ç–ª–µ—Ç–∏–∫–∏ —Å–æ –°–õ 15. –ü–æ–º–Ω–∏—Ç–µ, —á—Ç–æ –≤—Ä–∞–≥–∏ –∏ –Ω–µ—Ä–∞–∑—É–º–Ω—ã–µ —Å—É—â–µ—Å—Ç–≤–∞ –ø–æ–ª—É—á–∞—é—Ç –±–æ–Ω—É—Å +10 –ø—Ä–æ—Ç–∏–≤ –∑–∞–≤–æ—Ä–∞–∂–∏–≤–∞–Ω–∏—è.'),
        ('witcher_training', '–ë–æ–ª—å—à–∏–Ω—Å—Ç–≤–æ –≤–µ–¥—å–º–∞–∫–æ–≤ –ø—Ä–æ–≤–æ–¥—è—Ç –¥–µ—Ç—Å—Ç–≤–æ –∏ —é–Ω–æ—Å—Ç—å –≤ –∫—Ä–µ–ø–æ—Å—Ç–∏, –∫–æ—Ä–ø—è –Ω–∞–¥ –ø—ã–ª—å–Ω—ã–º–∏ —Ç–æ–º–∞–º–∏ –∏ –ø—Ä–æ—Ö–æ–¥—è —á—É–¥–æ–≤–∏—â–Ω—ã–µ –±–æ–µ–≤—ã–µ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∫–∏. –ú–Ω–æ–≥–∏–µ –≥–æ–≤–æ—Ä—è—Ç, —á—Ç–æ –≥–ª–∞–≤–Ω–æ–µ –æ—Ä—É–∂–∏–µ –≤–µ–¥—å–º–∞–∫–∞ ‚Äî —ç—Ç–æ –∑–Ω–∞–Ω–∏—è –æ —á—É–¥–æ–≤–∏—â–∞—Ö –∏ —É–º–µ–Ω–∏–µ –Ω–∞–π—Ç–∏ –≤—ã—Ö–æ–¥ –∏–∑ –ª—é–±–æ–π —Å–∏—Ç—É–∞—Ü–∏–∏. –ù–∞—Ö–æ–¥—è—Å—å –≤ –æ–ø–∞—Å–Ω–æ–π —Å—Ä–µ–¥–µ –∏–ª–∏ –Ω–∞ –ø–µ—Ä–µ—Å–µ—á—ë–Ω–Ω–æ–π –º–µ—Å—Ç–Ω–æ—Å—Ç–∏, –≤–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç —Å–Ω–∏–∑–∏—Ç—å —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–µ —à—Ç—Ä–∞—Ñ—ã –Ω–∞ –ø–æ–ª–æ–≤–∏–Ω—É –∑–Ω–∞—á–µ–Ω–∏—è —Å–≤–æ–µ–≥–æ –Ω–∞–≤—ã–∫–∞ <strong>–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –≤–µ–¥—å–º–∞–∫–∞</strong> (–º–∏–Ω–∏–º—É–º 1). <strong>–ü–æ–¥–≥–æ—Ç–æ–≤–∫—É –≤–µ–¥—å–º–∞–∫–∞</strong> —Ç–∞–∫–∂–µ –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –≤ –ª—é–±–æ–π —Å–∏—Ç—É–∞—Ü–∏–∏, –≥–¥–µ –ø–æ–Ω–∞–¥–æ–±–∏–ª—Å—è –±—ã –Ω–∞–≤—ã–∫ <strong>–ú–æ–Ω—Å—Ç—Ä–æ–ª–æ–≥–∏—è</strong>.'),
        ('healing_hands', '–ö—Ç–æ —É–≥–æ–¥–Ω–æ –º–æ–∂–µ—Ç –ø–µ—Ä–µ–≤—è–∑–∞—Ç—å —Ä–∞–Ω—É, –Ω–æ —Ç–æ–ª—å–∫–æ —É –º–µ–¥–∏–∫–∞ –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –∑–Ω–∞–Ω–∏–π, —á—Ç–æ–±—ã –õ–µ—á–µ–Ω–∏–µ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏—Ö —Ä–∞–Ω–µ–Ω–∏–π –ü–æ–¥—Ä–æ–±–Ω–µ–µ –æ –ª–µ—á–µ–Ω–∏–∏ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏—Ö —Ä–∞–Ω–µ–Ω–∏–π —Å–º. –Ω–∞ —Å—Ç—Ä. 173. –ø—Ä–æ–≤–æ–¥–∏—Ç—å —Å–ª–æ–∂–Ω—ã–µ —Ö–∏—Ä—É—Ä–≥–∏—á–µ—Å–∫–∏–µ –æ–ø–µ—Ä–∞—Ü–∏–∏. –ú–µ–¥–∏–∫ —Å –Ω–∞–≤—ã–∫–æ–º <strong>–õ–µ—á–∞—â–µ–µ –ø—Ä–∏–∫–æ—Å–Ω–æ–≤–µ–Ω–∏–µ</strong> ‚Äî –µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—ã–π, –∫—Ç–æ —Å–ø–æ—Å–æ–±–µ–Ω –≤—ã–ª–µ—á–∏—Ç—å –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–µ —Ä–∞–Ω–µ–Ω–∏–µ. –î–ª—è –∏—Å—Ü–µ–ª–µ–Ω–∏—è –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–≥–æ —Ä–∞–Ω–µ–Ω–∏—è –º–µ–¥–∏–∫ –¥–æ–ª–∂–µ–Ω —É—Å–ø–µ—à–Ω–æ —Å–æ–≤–µ—Ä—à–∏—Ç—å –Ω–µ—Å–∫–æ–ª—å–∫–æ –ø—Ä–æ–≤–µ—Ä–æ–∫ <strong>–õ–µ—á–∞—â–µ–≥–æ –ø—Ä–∏–∫–æ—Å–Ω–æ–≤–µ–Ω–∏—è</strong> ‚Äî —á–∏—Å–ª–æ –∏—Ö –∑–∞–≤–∏—Å–∏—Ç –æ—Ç —Å–µ—Ä—å—ë–∑–Ω–æ—Å—Ç–∏ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–≥–æ —Ä–∞–Ω–µ–Ω–∏—è. –°–õ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Ç–∞–∫–∂–µ –∑–∞–≤–∏—Å–∏—Ç –æ—Ç —Å–µ—Ä—å—ë–∑–Ω–æ—Å—Ç–∏ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–≥–æ —Ä–∞–Ω–µ–Ω–∏—è. –ü–æ–º–∏–º–æ —ç—Ç–æ–≥–æ, <strong>–õ–µ—á–∞—â–µ–µ –ø—Ä–∏–∫–æ—Å–Ω–æ–≤–µ–Ω–∏–µ</strong> –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –≤–º–µ—Å—Ç–æ –ø—Ä–æ–≤–µ—Ä–∫–∏ –ü–µ—Ä–≤–æ–π –ø–æ–º–æ—â–∏.'),
        ('magical_training', '–î–ª—è —Ç–æ–≥–æ —á—Ç–æ–±—ã —Å—Ç–∞—Ç—å –ø–æ–ª–Ω–æ–ø—Ä–∞–≤–Ω—ã–º –º–∞–≥–æ–º, —Å–ø–æ—Å–æ–±–Ω—ã–π –∫ –º–∞–≥–∏–∏ –∞–¥–µ–ø—Ç –¥–æ–ª–∂–µ–Ω –ø—Ä–æ–π—Ç–∏ –æ–±—É—á–µ–Ω–∏–µ –≤ –æ–¥–Ω–æ–π –∏–∑ –º–∞–≥–∏—á–µ—Å–∫–∏—Ö –∞–∫–∞–¥–µ–º–∏–π. –ú–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ú–∞–≥–∏—á–µ—Å–∫–∏—Ö –ø–æ–∑–Ω–∞–Ω–∏–π</strong>, –µ—Å–ª–∏ –µ–º—É –ø–æ–ø–∞–¥—ë—Ç—Å—è –º–∞–≥–∏—á–µ—Å–∫–∏–π —Ñ–µ–Ω–æ–º–µ–Ω, –µ—Å–ª–∏ –æ–Ω —É–≤–∏–¥–∏—Ç –Ω–µ–∑–Ω–∞–∫–æ–º–æ–µ –∑–∞–∫–ª–∏–Ω–∞–Ω–∏–µ –∏–ª–∏ –∑–∞—Ö–æ—á–µ—Ç —É–∑–Ω–∞—Ç—å –æ—Ç–≤–µ—Ç –Ω–∞ –∫–∞–∫–æ–π-—Ç–æ —Ç–µ–æ—Ä–µ—Ç–∏—á–µ—Å–∫–∏–π –≤–æ–ø—Ä–æ—Å. –°–õ –ø—Ä–æ–≤–µ—Ä–∫–∏ –æ–ø—Ä–µ–¥–µ–ª—è–µ—Ç—Å—è –≤–µ–¥—É—â–∏–º. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–∞–≥ —É–∑–Ω–∞—ë—Ç –≤—Å—ë, —á—Ç–æ –∫–∞—Å–∞–µ—Ç—Å—è –¥–∞–Ω–Ω–æ–≥–æ –º–∞–≥–∏—á–µ—Å–∫–æ–≥–æ —Ñ–µ–Ω–æ–º–µ–Ω–∞. –ü—Ä–æ–≤–µ—Ä–∫–∞ <strong>–ú–∞–≥–∏—á–µ—Å–∫–∏—Ö –ø–æ–∑–Ω–∞–Ω–∏–π</strong> —Ç–∞–∫–∂–µ –º–æ–∂–µ—Ç –∑–∞–º–µ–Ω–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É –í–Ω–∏–º–∞–Ω–∏—è –¥–ª—è –æ–±–Ω–∞—Ä—É–∂–µ–Ω–∏—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–Ω–æ–π –º–∞–≥–∏–∏ –∏ –¥—É—Ö–æ–≤.'),
        ('tough_as_nails', '–ù–∞—Å—Ç–æ—è—â–∏–µ –≤–æ–∏–Ω—ã ‚Äî –±—É–¥—å —Ç–æ —Ç–µ–º–µ—Ä—Å–∫–∏–µ ¬´–°–∏–Ω–∏–µ –ø–æ–ª–æ—Å–∫–∏¬ª –∏–ª–∏ –Ω–∏–ª—å—Ñ–≥–∞–∞—Ä–¥—Ü—ã –∏–∑ –±—Ä–∏–≥–∞–¥—ã ¬´–ò–º–ø–µ—Ä–∞¬ª ‚Äî –Ω–∏–∫–æ–≥–¥–∞ –Ω–µ —Å–¥–∞—é—Ç—Å—è. –ö–æ–≥–¥–∞ –ü–ó –≤–æ–∏–Ω–∞ –æ–ø—É—Å–∫–∞–µ—Ç—Å—è –¥–æ 0 –∏–ª–∏ –Ω–∏–∂–µ, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É –Ω–∞–≤—ã–∫–∞ <strong>–ö—Ä–µ–ø—á–µ —Å—Ç–∞–ª–∏</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –∫–æ–ª–∏—á–µ—Å—Ç–≤—É –æ—Ç—Ä–∏—Ü–∞—Ç–µ–ª—å–Ω—ã—Ö –ü–ó —Ö 2, —á—Ç–æ–±—ã –ø—Ä–æ–¥–æ–ª–∂–∏—Ç—å —Å—Ä–∞–∂–∞—Ç—å—Å—è. –ü—Ä–∏ –ø—Ä–æ–≤–∞–ª–µ –≤–æ–∏–Ω –æ–∫–∞–∑—ã–≤–∞–µ—Ç—Å—è –ø—Ä–∏ —Å–º–µ—Ä—Ç–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –æ–Ω –º–æ–∂–µ—Ç –ø—Ä–æ–¥–æ–ª–∂–∞—Ç—å —Å—Ä–∞–∂–µ–Ω–∏–µ, –∫–∞–∫ –µ—Å–ª–∏ –±—ã –µ–≥–æ –ü–ó –±—ã–ª–∏ –Ω–∏–∂–µ –ø–æ—Ä–æ–≥–∞ —Ä–∞–Ω–µ–Ω–∏—è. –ü–æ–ª—É—á–∏–≤ —É—Ä–æ–Ω, –æ–Ω –≤–Ω–æ–≤—å –¥–æ–ª–∂–µ–Ω —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–æ –°–õ, –∑–∞–≤–∏—Å—è—â–µ–π –æ—Ç –µ–≥–æ –ü–ó.'),
        ('dedicated', '–í –±–æ–ª—å—à–∏–Ω—Å—Ç–≤–µ —Ü–µ—Ä–∫–≤–µ–π –º–∏—Ä–∞ —Ä–∞–¥—ã –ø–æ—Å–µ—Ç–∏—Ç–µ–ª—è–º. –°–ª—É–∂–∏—Ç–µ–ª–∏ —Ö—Ä–∞–º–æ–≤ –ø–æ–º–æ–≥–∞—é—Ç –º–µ—Å—Ç–Ω—ã–º –∂–∏—Ç–µ–ª—è–º –∏ —Å —Ä–∞–¥–æ—Å—Ç—å—é –ø—Ä–∏–Ω–∏–º–∞—é—Ç –Ω–æ–≤–æ–æ–±—Ä–∞—â—ë–Ω–Ω—ã—Ö –≤ —Å–≤–æ—é –≤–µ—Ä—É. –ñ—Ä–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É –Ω–∞–≤—ã–∫–∞ <strong>–ü–æ—Å–≤—è—â—ë–Ω–Ω—ã–π</strong> (–°–õ –æ–ø—Ä–µ–¥–µ–ª—è–µ—Ç –≤–µ–¥—É—â–∏–π) –≤ —Ö—Ä–∞–º–µ —Å–≤–æ–µ–π —Ä–µ–ª–∏–≥–∏–∏, —á—Ç–æ–±—ã –ø–æ–ª—É—á–∏—Ç—å –±–µ—Å–ø–ª–∞—Ç–Ω—ã–π –∫—Ä–æ–≤, –∏—Å—Ü–µ–ª–µ–Ω–∏–µ –∏ –ø—Ä–æ—á–∏–µ —É—Å–ª—É–≥–∏ –Ω–∞ —É—Å–º–æ—Ç—Ä–µ–Ω–∏–µ –≤–µ–¥—É—â–µ–≥–æ. –ù–∞–≤—ã–∫ <strong>–ü–æ—Å–≤—è—â—ë–Ω–Ω—ã–π</strong> —Ç–∞–∫–∂–µ –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø—Ä–∏ –æ–±—â–µ–Ω–∏–∏ —Å –µ–¥–∏–Ω–æ–≤–µ—Ä—Ü–∞–º–∏, –Ω–æ –ø–æ–ª—É—á–∏—Ç–µ –≤—ã –∫—É–¥–∞ –º–µ–Ω—å—à–µ, —á–µ–º –≤ —Ü–µ—Ä–∫–≤–∏. <strong>–ü–æ—Å–≤—è—â—ë–Ω–Ω—ã–π</strong> –Ω–µ –¥–µ–π—Å—Ç–≤—É–µ—Ç –ø—Ä–∏ –æ–±—â–µ–Ω–∏–∏ —Å —Ç–µ–º–∏, –∫—Ç–æ –∏—Å–ø–æ–≤–µ–¥—É–µ—Ç –¥—Ä—É–≥—É—é –≤–µ—Ä—É.'),
        ('professional_paranoia', '–í—Å–µ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫–∏, –±—É–¥—å —Ç–æ —É–±–∏–π—Ü—ã, –≤–æ—Ä—ã, —Ñ–∞–ª—å—à–∏–≤–æ–º–æ–Ω–µ—Ç—á–∏–∫–∏ –∏–ª–∏ –∫–æ–Ω—Ç—Ä–∞–±–∞–Ω–¥–∏—Å—Ç—ã, –æ–±–ª–∞–¥–∞—é—Ç –æ–±–æ—Å—Ç—Ä—ë–Ω–Ω—ã–º —á—É—Ç—å—ë–º –Ω–∞ –æ–ø–∞—Å–Ω–æ—Å—Ç—å ‚Äî —Ñ–∞–∫—Ç–∏—á–µ—Å–∫–∏ –ø—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω–æ–π –ø–∞—Ä–∞–Ω–æ–π–µ–π, –±–ª–∞–≥–æ–¥–∞—Ä—è –∫–æ—Ç–æ—Ä–æ–π –æ–Ω–∏ –∏–∑–±–µ–≥–∞—é—Ç –ø–æ–∏–º–∫–∏. –ö–æ–≥–¥–∞ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –æ–∫–∞–∑—ã–≤–∞–µ—Ç—Å—è –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö 10 –º–µ—Ç—Ä–æ–≤ –æ—Ç –ª–æ–≤—É—à–∫–∏ (–≤–∫–ª—é—á–∞—è —ç–∫—Å–ø–µ—Ä–∏–º–µ–Ω—Ç–∞–ª—å–Ω—ã–µ –ª–æ–≤—É—à–∫–∏, –ª–æ–≤—É—à–∫–∏ –≤–æ–∏–Ω–∞ –∏ –∑–∞—Å–∞–¥—ã), –æ–Ω –º–æ–∂–µ—Ç –Ω–µ–º–µ–¥–ª–µ–Ω–Ω–æ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ü—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω–æ–π –ø–∞—Ä–∞–Ω–æ–π–∏</strong> –ª–∏–±–æ –ø—Ä–æ—Ç–∏–≤ –°–õ –æ–±–Ω–∞—Ä—É–∂–µ–Ω–∏—è –ª–æ–≤—É—à–∫–∏, –ª–∏–±–æ –ø—Ä–æ—Ç–∏–≤ –°–∫—Ä—ã—Ç–Ω–æ—Å—Ç–∏ –∑–∞—Å–∞–¥—ã, –ª–∏–±–æ –ø—Ä–æ—Ç–∏–≤ –∑–∞–¥–∞–Ω–Ω–æ–π –≤–µ–¥—É—â–∏–º –°–õ. –î–∞–∂–µ –µ—Å–ª–∏ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –Ω–µ –∑–∞–º–µ—Ç–∏—Ç –ª–æ–≤—É—à–∫–∏, —á—É—Ç—å—ë –≤—Å—ë —Ä–∞–≤–Ω–æ –µ–º—É –ø–æ–¥—Å–∫–∞–∂–µ—Ç, —á—Ç–æ —Ç—É—Ç —á—Ç–æ-—Ç–æ –Ω–µ —Ç–∞–∫.'),
        ('quick_fix', '–£–º–µ–ª—ã–π —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ —Å–ø–æ—Å–æ–±–µ–Ω –Ω–∞—Å–∫–æ—Ä–æ –ø–æ–¥–ª–∞—Ç–∞—Ç—å –æ—Ä—É–∂–∏–µ –∏–ª–∏ –±—Ä–æ–Ω—é, —á—Ç–æ–±—ã –∏—Ö –≤–ª–∞–¥–µ–ª–µ—Ü –º–æ–≥ –ø—Ä–æ–¥–æ–ª–∂–∞—Ç—å —Å—Ä–∞–∂–∞—Ç—å—Å—è. –†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ —Å–≤—è–∂–µ—Ç –≤–º–µ—Å—Ç–µ –æ–±—Ä—ã–≤–∫–∏ –ª–æ–ø–Ω—É–≤—à–µ–π —Ç–µ—Ç–∏–≤—ã, –∑–∞–æ—Å—Ç—Ä–∏—Ç –∫—Ä–∞–π —Å–ª–æ–º–∞–Ω–Ω–æ–≥–æ –∫–ª–∏–Ω–∫–∞ –∏–ª–∏ –ø—Ä–∏–∫–æ–ª–æ—Ç–∏—Ç –º–µ—Ç–∞–ª–ª–∏—á–µ—Å–∫—É—é –ø–ª–∞—Å—Ç–∏–Ω—É –ø–æ–≤–µ—Ä—Ö —Ç—Ä–µ—Å–Ω—É–≤—à–µ–≥–æ —â–∏—Ç–∞. –†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å —Ö–æ–¥ –∏ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ë—ã—Å—Ç—Ä–æ–≥–æ —Ä–µ–º–æ–Ω—Ç–∞</strong> —Å–æ —Å–ª–æ–∂–Ω–æ—Å—Ç—å—é, —Ä–∞–≤–Ω–æ–π –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω–æ–≥–æ –ø—Ä–µ–¥–º–µ—Ç–∞ –º–∏–Ω—É—Å 3, —á—Ç–æ–±—ã –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç—å 1/2 –ø—Ä–æ—á–Ω–æ—Å—Ç–∏ –±—Ä–æ–Ω–∏ –∏–ª–∏ 1/2 –Ω–∞–¥—ë–∂–Ω–æ—Å—Ç–∏ —Å–ª–æ–º–∞–Ω–Ω–æ–≥–æ –æ—Ä—É–∂–∏—è –∏–ª–∏ —â–∏—Ç–∞. –ü–æ–∫–∞ –æ—Ä—É–∂–∏–µ –ø–æ—Å–ª–µ <strong>–ë—ã—Å—Ç—Ä–æ–≥–æ —Ä–µ–º–æ–Ω—Ç–∞</strong> –Ω–µ –ø–æ—á–∏–Ω—è—Ç –≤ –∫—É–∑–Ω–∏—Ü–µ, –æ–Ω–æ –Ω–∞–Ω–æ—Å–∏—Ç –ø–æ–ª–æ–≤–∏–Ω—É –æ–±—ã—á–Ω–æ–≥–æ —É—Ä–æ–Ω–∞.<br><br><strong>–°–ª–∏—à–∫–æ–º –º–Ω–æ–≥–æ –ø–æ–ª–æ–º–æ–∫</strong><br>–†–∞–Ω–µ–µ –ø–æ–¥–ª–∞—Ç–∞–Ω–Ω–æ–µ –æ—Ä—É–∂–∏–µ, —â–∏—Ç –∏–ª–∏ –±—Ä–æ–Ω—é –ø–æ—Å–ª–µ –ø–æ–≤—Ç–æ—Ä–Ω–æ–π –ø–æ–ª–æ–º–∫–∏ –º–æ–∂–Ω–æ –ø–æ–¥–ª–∞—Ç–∞—Ç—å –µ—â—ë —Ç–æ–ª—å–∫–æ –æ–¥–∏–Ω —Ä–∞–∑. –í–æ –≤—Ç–æ—Ä–æ–π —Ä–∞–∑ <strong>–ë—ã—Å—Ç—Ä—ã–π —Ä–µ–º–æ–Ω—Ç</strong> –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç –ª–∏—à—å 1/4 –∑–Ω–∞—á–µ–Ω–∏—è –Ω–∞–¥—ë–∂–Ω–æ—Å—Ç–∏/–ø—Ä–æ—á–Ω–æ—Å—Ç–∏ (—Å –æ–∫—Ä—É–≥–ª–µ–Ω–∏–µ–º –≤–Ω–∏–∑).'),
        ('well_traveled', '–û–±—ã—á–Ω—ã–π —Ç–æ—Ä–≥–æ–≤–µ—Ü –∑–∞—Ä–∞–±–∞—Ç—ã–≤–∞–µ—Ç –Ω–∞ –∂–∏–∑–Ω—å —Ç–µ–º, —á—Ç–æ –ø—Ä–æ–¥–∞—ë—Ç —Ç–æ–≤–∞—Ä –ø—Ä–∏—Ö–æ–¥—è—â–∏–º –∫ –Ω–µ–º—É –ø–æ–∫—É–ø–∞—Ç–µ–ª—è–º. –°—Ç—Ä–∞–Ω—Å—Ç–≤—É—é—â–∏–π –∂–µ —Ç–æ—Ä–≥–æ–≤–µ—Ü —Å–∞–º –ø—Ä–∏—Ö–æ–¥–∏—Ç –∫ –ø–æ–∫—É–ø–∞—Ç–µ–ª—é. –û–Ω –µ–∑–¥–∏—Ç –ø–æ –º–∏—Ä—É –∏ —É–∑–Ω–∞—ë—Ç –æ–±–æ –≤—Å—ë–º, —á—Ç–æ —Ç–∞–º –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç. –¢–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç –≤ –ª—é–±–æ–π –º–æ–º–µ–Ω—Ç –ø–æ —Å–≤–æ–µ–º—É –∂–µ–ª–∞–Ω–∏—é —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É –Ω–∞–≤—ã–∫–∞ <strong>–ë—ã–≤–∞–ª—ã–π –ø—É—Ç–µ—à–µ—Å—Ç–≤–µ–Ω–Ω–∏–∫</strong>, —á—Ç–æ–±—ã —É–∑–Ω–∞—Ç—å –æ–¥–∏–Ω —Ñ–∞–∫—Ç –æ–± –æ–ø—Ä–µ–¥–µ–ª—ë–Ω–Ω–æ–º –ø—Ä–µ–¥–º–µ—Ç–µ, –∫—É–ª—å—Ç—É—Ä–µ –∏–ª–∏ –æ–±–ª–∞—Å—Ç–∏. –°–õ –ø—Ä–æ–≤–µ—Ä–∫–∏ –æ–ø—Ä–µ–¥–µ–ª—è–µ—Ç –≤–µ–¥—É—â–∏–π. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ç–æ—Ä–≥–æ–≤–µ—Ü –ø–æ–ª—É—á–∞–µ—Ç –æ—Ç–≤–µ—Ç –Ω–∞ –≤–æ–ø—Ä–æ—Å, –≤—Å–ø–æ–º–Ω–∏–≤ —Ç–µ –≤—Ä–µ–º–µ–Ω–∞, –∫–æ–≥–¥–∞ –æ–Ω –≤ –ø—Ä–æ—à–ª—ã–π —Ä–∞–∑ –±—ã–ª –≤ —ç—Ç–æ–º –º–µ—Å—Ç–µ.')
      ) AS raw_data_ru(skill_id, description)
    UNION ALL
    SELECT 'en' AS lang, skill_id, description
      FROM (VALUES
        ('busking', 'A Bard is a wonderful thing to have around, especially when the party''s low on money. A Bard can take an hour and make a Busking roll in the nearest town center. The total of this roll is the amount of money raked in by the Bard while they perform on the street. A fumble can lower the roll, and a negative value means that not only do you fail to make any coin but you are also harrassed by the locals for your poor performance, resulting in a ‚àí2 to Charisma with anyone in the town for the rest of the day.<br><br>When raising a crowd in a large area full of people, a DC:15 is sufficient to create a crowd around the Bard dense enough to require a DC:15 Physique or Athletics check to pass through. Also keep in mind that enemies and non-sentient creatures gain a +10 to resist.'),
        ('witcher_training', 'Most of a Witcher''s early life is spent within the walls of their keep, studying huge, dusty tomes and going through hellish combat training. Many have argued that the Witcher''s greatest weapon is their knowledge of monsters and their adaptability in any situation. When in a hostile environment or difficult terrain, a Witcher can lessen the penalties by half their <strong>Witcher Training</strong> value (minimum 1). <strong>Witcher Training</strong> can also be used in any situation that you would normally use Monster Lore for.'),
        ('healing_hands', 'Anyone can apply some ointment and wrap a bandage around a cut, but a Doctor has true medical training which allows them to perform complex surgeries. A Doctor with <strong>Healing Hands</strong> is the only person who can heal a critical wound. To heal critical wounds a doctor must make a number of successful <strong>Healing Hands</strong> rolls based on the severity of the critical wound. The DC of the roll is based on the severity of the critical wound as well. <strong>Healing Hands</strong> can also be used for any First Aid task.'),
        ('magical_training', 'To qualify as a Mage, a magically adept person must pass through the halls of one of the world''s magical academies and learn the fundamentals of the magical arts. A Mage can roll <strong>Magical Training</strong> whenever they encounter a magical phenomenon, an unknown spell, or a question of magical theory. The DC is set by the GM, and a success allows the Mage to recall everything there is to know about the phenomenon. <strong>Magical Training</strong> can also be rolled as a form of Awareness that detects magic that is in use, or specters.'),
        ('tough_as_nails', 'True Men At Arms like the Blue Stripes of Temeria and the Impera Brigade of Nilfgaard are hardened soldiers who never give in or surrender. When a Man At Arms falls to or below 0 Health, they can roll <strong>Tough As Nails</strong> at a DC equal to the number of negative Health times 2 to keep fighting. If they fail, they fall into death state as per usual. If they succeed they can keep fighting as if they were only at their Wound Threshold. Any damage forces them to make another roll against a DC based on their Health.'),
        ('dedicated', 'The churches of the world are often warm and inviting places, helping their communities and welcoming new converts. A Priest can roll <strong>Initiate of the Gods</strong> at a DC set by the GM at churches of the same faith to get free lodging, healing, and other services at the GM''s discretion. <strong>Initiate of the Gods</strong> also works when dealing with members of the same faith, though they will likely be able to offer less than a fully supplied church. Keep in mind that <strong>Initiate of the Gods</strong> doesn''t work with members of other faiths.'),
        ('professional_paranoia', 'Whether they''re an assassin, a thief, a counterfeitter, or a smuggler, criminals all share a practiced paranoia that keeps them out of trouble. Whenever a Criminal comes within 10m of a trap (this includes experimental traps, Man at Arms booby traps, and ambushes) they immediately can make a <strong>Practiced Paranoia</strong> roll at either the DC to spot the trap, the ambushing party''s Stealth roll, or a DC set by the GM. Even if they don''t succeed in spotting the trap, they are still aware that something is wrong.'),
        ('quick_fix', 'A skilled craftsman can patch a weapon or armor well enough to keep it working and keep its wearer/wielder in the fight, whether that be by tying a bowstring back together, sharpening the edge of a broken blade, or nailing a plate over a cracked shield. By taking a turn to roll <strong>Patch Job</strong> at a DC equal to the item''s Crafting DC-3 a Craftsman can restore a broken shield or armor to half its full SP or restore a broken weapon to half its durability. Until fixed at a forge, a patched weapon does half its normal damage.<br><br><strong>Too Many Patches</strong><br>A weapon, shield, or armor which has already been patched once can only be patched again 1 more time, and this patch only brings it to 1/4th SP/Durability (rounding down).'),
        ('well_traveled', 'Your average merchant makes a living from trade, and that trade brings in customers from all around. But a traveling merchant goes to their customers, wandering the roads of the world and learning from its people. A Merchant can make a <strong>Well Traveled</strong> roll any time they want to know a fact about a specific item, culture, or area. The DC is set by the GM, and if the roll is successful the Merchant remembers the answer to that question, calling on memories of the last time they traveled through the applicable area.')
      ) AS raw_data_en(skill_id, description)
  ),
  ins_main_skill_descriptions AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
    SELECT ck_id(meta.su_su_id || '.wcc_skills.' || raw_main_skill_descriptions.skill_id || '.description') AS id
         , meta.entity, 'description', raw_main_skill_descriptions.lang, raw_main_skill_descriptions.description
      FROM raw_main_skill_descriptions
      CROSS JOIN meta
    ON CONFLICT (id, lang) DO NOTHING
  )
UPDATE wcc_skills
SET skill_desc_id = ck_id('witcher_cc.wcc_skills.' || skill_id || '.description')
WHERE skill_type = 'main'
  AND skill_desc_id IS NULL
  AND EXISTS (
    SELECT 1 FROM i18n_text 
    WHERE id = ck_id('witcher_cc.wcc_skills.' || wcc_skills.skill_id || '.description')
  );

-- –í—Å—Ç–∞–≤–∫–∞ –æ–ø–∏—Å–∞–Ω–∏–π –ø—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω—ã—Ö –Ω–∞–≤—ã–∫–æ–≤ (professional skills)
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id, 'wcc_skills' AS entity),
  raw_prof_skill_descriptions AS (
    SELECT 'ru' AS lang, skill_id, description
      FROM (VALUES
        -- Bard skills
        ('return_act', '–ü–µ—Ä–µ–¥ –ø—Ä–æ–≤–µ—Ä–∫–æ–π <strong>–£–ª–∏—á–Ω–æ–≥–æ –≤—ã—Å—Ç—É–ø–ª–µ–Ω–∏—è</strong> –±–∞—Ä–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ü–æ–≤—Ç–æ—Ä–Ω–æ–≥–æ –≤—ã—Å—Ç—É–ø–ª–µ–Ω–∏—è</strong> —Å–æ –°–õ, —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω–æ–π –≤–µ–¥—É—â–∏–º, —á—Ç–æ–±—ã –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å, –≤—ã—Å—Ç—É–ø–∞–ª –ª–∏ –æ–Ω –≤ —ç—Ç–æ–º –≥–æ—Ä–æ–¥–µ —Ä–∞–Ω—å—à–µ. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –±–∞—Ä–¥ —É–∂–µ –∑–∞–≤–æ–µ–≤–∞–ª –ø–æ–ø—É–ª—è—Ä–Ω–æ—Å—Ç—å –≤ —ç—Ç–æ–º –≥–æ—Ä–æ–¥–µ. –í —Ç–∞–∫–æ–º —Å–ª—É—á–∞–µ –¥–æ—Ö–æ–¥ —Å –µ–≥–æ <strong>–£–ª–∏—á–Ω–æ–≥–æ –≤—ã—Å—Ç—É–ø–ª–µ–Ω–∏—è</strong> —É–¥–≤–∞–∏–≤–∞–µ—Ç—Å—è, –∞ —Å–∞–º –±–∞—Ä–¥ –ø–æ–ª—É—á–∞–µ—Ç –±–æ–Ω—É—Å +2 –∫ –•–∞—Ä–∏–∑–º–µ –ø—Ä–∏ –æ–±—â–µ–Ω–∏–∏ —Å–æ –≤—Å–µ–º–∏, –∫—Ç–æ –ø—Ä–∏—à—ë–ª –Ω–∞ –≤—ã—Å—Ç—É–ø–ª–µ–Ω–∏–µ.'),
        ('raise_a_crowd', '–í—ã—Å—Ç—É–ø–∞—è –≤ —Ç–µ—á–µ–Ω–∏–µ –ø–æ–ª–Ω–æ–≥–æ —Ä–∞—É–Ω–¥–∞, –≤—ã –º–æ–∂–µ—Ç–µ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ó–∞–≤–æ—Ä–æ–∂–∏—Ç—å –ø—É–±–ª–∏–∫—É</strong>, —á—Ç–æ–±—ã –ø—Ä–∏–≤–ª–µ—á—å –≤–Ω–∏–º–∞–Ω–∏–µ –≤—Å–µ—Ö –≤ —Ä–∞–¥–∏—É—Å–µ 20 –º–µ—Ç—Ä–æ–≤. –õ—é–±–æ–π –ø–µ—Ä—Å–æ–Ω–∞–∂, —á–µ–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç –ø—Ä–æ–≤–µ—Ä–∫–∏ –°–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—è —É–±–µ–∂–¥–µ–Ω–∏—é –±—É–¥–µ—Ç –Ω–∏–∂–µ –≤–∞—à–µ–≥–æ –∏–∑–Ω–∞—á–∞–ª—å–Ω–æ–≥–æ, –º–æ–∂–µ—Ç —Ç–æ–ª—å–∫–æ —Å—Ç–æ—è—Ç—å –∏ –Ω–∞–±–ª—é–¥–∞—Ç—å, –ø–æ–∫–∞ –Ω–µ –≤—ã–±—Ä–æ—Å–∏—Ç –±–æ–ª–µ–µ –≤—ã—Å–æ–∫–∏–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç. –ê—Ç–∞–∫–æ–≤–∞–Ω–Ω—ã–µ —Ü–µ–ª–∏ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –ø–µ—Ä–µ—Å—Ç–∞—é—Ç –±—ã—Ç—å –∑–∞–≤–æ—Ä–æ–∂—ë–Ω–Ω—ã–º–∏.'),
        ('good_friend', '–û–¥–∏–Ω —Ä–∞–∑ –∑–∞ –∏–≥—Ä–æ–≤—É—é –ø–∞—Ä—Ç–∏—é –±–∞—Ä–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–î–æ–±—Ä—ã–π –¥—Ä—É–≥</strong>, —á—Ç–æ–±—ã –Ω–∞–π—Ç–∏ –¥—Ä—É–≥–∞, –∫–æ—Ç–æ—Ä—ã–π –ø–æ–º–æ–≥ –±—ã –µ–º—É. –†–µ–∑—É–ª—å—Ç–∞—Ç –±—Ä–æ—Å–∫–∞ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–∏—Ç—å –º–µ–∂–¥—É 3 –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º–∏, —É–∫–∞–∑–∞–Ω–Ω—ã–º–∏ –≤ —Ç–∞–±–ª–∏—Ü–µ ¬´–î–æ–±—Ä—ã–π –¥—Ä—É–≥¬ª –Ω–∞ –ø–æ–ª—è—Ö. –î—Ä—É–≥ –ø–æ —Å—Ç–∞—Ä–æ–π –ø–∞–º—è—Ç–∏ –æ–∫–∞–∂–µ—Ç –≤–∞–º –æ–¥–Ω—É —É—Å–ª—É–≥—É –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö —Ä–∞–∑—É–º–Ω–æ–≥–æ, –ø–æ—Å–ª–µ —á–µ–≥–æ –Ω–µ –±—É–¥–µ—Ç –±–æ–ª—å—à–µ –ø–æ–º–æ–≥–∞—Ç—å –±–µ—Å–ø–ª–∞—Ç–Ω–æ –∏ –µ–≥–æ –Ω—É–∂–Ω–æ –±—É–¥–µ—Ç —É–≥–æ–≤–∞—Ä–∏–≤–∞—Ç—å.'),
        ('fade', '–ë–∞—Ä–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ù–µ–∑–∞–º–µ—Ç–Ω–æ—Å—Ç–∏</strong> –ø—Ä–æ—Ç–∏–≤ –í–Ω–∏–º–∞–Ω–∏—è –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö —Ü–µ–ª–µ–π, —á—Ç–æ–±—ã —Å–ª–∏—Ç—å—Å—è —Å —Ç–æ–ª–ø–æ–π. –≠—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –ø–æ–∑–≤–æ–ª—è–µ—Ç –±–∞—Ä–¥—É –ø—Ä—è—Ç–∞—Ç—å—Å—è –¥–∞–∂–µ —Ç–∞–º, –≥–¥–µ –Ω–µ—Ç –ø–æ–¥—Ö–æ–¥—è—â–∏—Ö —É–∫—Ä—ã—Ç–∏–π, ‚Äî –±–∞—Ä–¥ –ø–æ–ø—Ä–æ—Å—Ç—É –≤–∫–ª–∏–Ω–∏–≤–∞–µ—Ç—Å—è –≤ —Ä–∞–∑–≥–æ–≤–æ—Ä, –ø–µ—Ä–µ–∫–ª—é—á–∞–µ—Ç –≤–Ω–∏–º–∞–Ω–∏–µ –æ–∫—Ä—É–∂–∞—é—â–∏—Ö –Ω–∞ –¥—Ä—É–≥–æ–π –ø—Ä–µ–¥–º–µ—Ç –∏ —Ç–æ–º—É –ø–æ–¥–æ–±–Ω–æ–µ. –≠—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç –≤ —Ç–æ–º —Å–ª—É—á–∞–µ, –µ—Å–ª–∏ –±–∞—Ä–¥ –æ–¥–µ—Ç –≤–æ —á—Ç–æ-—Ç–æ –æ—á–µ–Ω—å –±—Ä–æ—Å–∫–æ–µ.'),
        ('spread_the_word', '–ü–æ—Å–ª–µ —É—Å–ø–µ—à–Ω–æ–≥–æ –±—Ä–æ—Å–∫–∞ –û–±–º–∞–Ω–∞ –ø—Ä–æ—Ç–∏–≤ —Ü–µ–ª–∏ –±–∞—Ä–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –≤—Å—Ç—Ä–µ—á–Ω—ã–π –±—Ä–æ—Å–æ–∫ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü—É—Å—Ç–∏—Ç—å —Å–ª—É—Ö</strong> –ø—Ä–æ—Ç–∏–≤ –°–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—è —É–±–µ–∂–¥–µ–Ω–∏—é —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –±–∞—Ä–¥–∞ —Ü–µ–ª—å —Ä–∞—Å–ø—Ä–æ—Å—Ç—Ä–∞–Ω—è–µ—Ç —Ä–∞—Å—Å–∫–∞–∑–∞–Ω–Ω—É—é –∏–º –ª–æ–∂—å –≤ —Å–≤–æ—ë–º –ø–æ—Å–µ–ª–µ–Ω–∏–∏ –∏–ª–∏ –≥—Ä—É–ø–ø–µ, —á—Ç–æ –¥–∞—ë—Ç –±–∞—Ä–¥—É –±–æ–Ω—É—Å +2 –∫ –û–±–º–∞–Ω—É –ø—Ä–∏ –ø–æ–ø—ã—Ç–∫–µ —Ä–∞—Å—Å–∫–∞–∑–∞—Ç—å —Ç—É –∂–µ –ª–æ–∂—å –∫–æ–º—É-—Ç–æ –µ—â—ë.'),
        ('acclimatize', '–ù–∞—Ö–æ–¥—è—Å—å –≤ –ø–æ—Å–µ–ª–µ–Ω–∏–∏, –±–∞—Ä–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–°–æ–π—Ç–∏ –∑–∞ —Å–≤–æ–µ–≥–æ</strong> (—Å–º. —Ç–∞–±–ª–∏—Ü—É –Ω–∞ –ø–æ–ª—è—Ö). –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –±–∞—Ä–¥ —É–∑–Ω–∞—ë—Ç, –∫–∞–∫ –≤—ã–¥–∞—Ç—å —Å–µ–±—è –∑–∞ –º–µ—Å—Ç–Ω–æ–≥–æ, –∏ –µ–≥–æ –±–æ–ª—å—à–µ –Ω–µ —Å—á–∏—Ç–∞—é—Ç —á—É–∂–∏–º. –û–Ω –ø–æ–ª—É—á–∞–µ—Ç –±–æ–Ω—É—Å +2 –∫ –•–∞—Ä–∏–∑–º–µ –∏ –£–±–µ–∂–¥–µ–Ω–∏—é –ø—Ä–∏ –æ–±—â–µ–Ω–∏–∏ —Å –º–µ—Å—Ç–Ω—ã–º–∏. –ü—Ä–∏ —ç—Ç–æ–º –∫ –Ω–µ–º—É –Ω–µ –±—É–¥—É—Ç –æ—Ç–Ω–æ—Å–∏—Ç—å—Å—è —Å –ø–æ–¥–æ–∑—Ä–µ–Ω–∏–µ–º –∏–ª–∏ –ø–æ–¥–≤–µ—Ä–≥–∞—Ç—å —Ç—Ä–∞–≤–ª–µ, –∫–∞–∫ —á—É–∂–∞–∫–∞.'),
        ('poison_the_well', '–ö–æ–≥–¥–∞ –±–∞—Ä–¥ –ø—ã—Ç–∞–µ—Ç—Å—è –ø–æ–≤–ª–∏—è—Ç—å –Ω–∞ –æ–¥–Ω–æ–≥–æ –∏–ª–∏ –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–æ–≤, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ö–æ–≤–∞—Ä—Å—Ç–≤–∞</strong> –ø—Ä–æ—Ç–∏–≤ –≠–º–ø —Ö 3 —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –±–∞—Ä–¥ –¥–µ–ª–∞–µ—Ç –µ—Ö–∏–¥–Ω–æ–µ –∑–∞–º–µ—á–∞–Ω–∏–µ, –∫–æ—Ç–æ—Ä–æ–µ –¥–∞—ë—Ç —à—Ç—Ä–∞—Ñ -1 –∫ –°–æ–±–ª–∞–∑–Ω–µ–Ω–∏—é, –£–±–µ–∂–¥–µ–Ω–∏—é, –õ–∏–¥–µ—Ä—Å—Ç–≤—É, –ó–∞–ø—É–≥–∏–≤–∞–Ω–∏—é –∏–ª–∏ –•–∞—Ä–∏–∑–º–µ —Ü–µ–ª–∏ –∑–∞ –∫–∞–∂–¥—ã–π –ø—É–Ω–∫—Ç —Å–≤—ã—à–µ –°–õ.'),
        ('needling', '–ë–∞—Ä–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –≤—Å—Ç—Ä–µ—á–Ω—É—é –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü–æ–¥–∫–æ–ª–∫–∞</strong> –ø—Ä–æ—Ç–∏–≤ –°–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—è —É–±–µ–∂–¥–µ–Ω–∏—é —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –±–∞—Ä–¥ –¥—Ä–∞–∑–Ω–∏—Ç —Ü–µ–ª—å, –æ—Å—ã–ø–∞–µ—Ç –µ—ë —É–≥—Ä–æ–∑–∞–º–∏ –∏ —Ä—É–≥–∞—Ç–µ–ª—å—Å—Ç–≤–∞–º–∏ –¥–æ —Ç–µ—Ö –ø–æ—Ä, –ø–æ–∫–∞ —Ü–µ–ª—å –Ω–µ –Ω–∞–ø–∞–¥—ë—Ç. –¶–µ–ª—å –ø–æ–ª—É—á–∞–µ—Ç —à—Ç—Ä–∞—Ñ –∫ –∞—Ç–∞–∫–µ –∏ –∑–∞—â–∏—Ç–µ, —Ä–∞–≤–Ω—ã–π –ø–æ–ª–æ–≤–∏–Ω–µ –∑–Ω–∞—á–µ–Ω–∏—è <strong>–ü–æ–¥–∫–æ–ª–∫–∏</strong> –±–∞—Ä–¥–∞ –∏ –¥–ª—è—â–∏–π—Å—è –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–∞—É–Ω–¥–æ–≤, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü–æ–¥–∫–æ–ª–∫–∞</strong>.'),
        ('et_tu_brute', '–ë–∞—Ä–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ò —Ç—ã, –ë—Ä—É—Ç</strong> –ø—Ä–æ—Ç–∏–≤ –í–æ–ª–∏ —Ö–ó —Ü–µ–ª–∏, —á—Ç–æ–±—ã –Ω–∞—Å—Ç—Ä–æ–∏—Ç—å —Ü–µ–ª—å –ø—Ä–æ—Ç–∏–≤ –æ–¥–Ω–æ–≥–æ —Å–æ—é–∑–Ω–∏–∫–∞. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –ª–æ–∂—å –∏–ª–∏ –ø–æ–ª—É–ø—Ä–∞–≤–¥–∞, —Å–∫–∞–∑–∞–Ω–Ω–∞—è –±–∞—Ä–¥–æ–º, –∑–∞—Å—Ç–∞–≤–ª—è–µ—Ç —Ü–µ–ª—å –æ—Ç–Ω–æ—Å–∏—Ç—å—Å—è –∫ —Å–≤–æ–µ–º—É —Å–æ—é–∑–Ω–∏–∫—É —Å –ø–æ–¥–æ–∑—Ä–µ–Ω–∏–µ–º –∏ –≤—Ä–∞–∂–¥–µ–±–Ω–æ—Å—Ç—å—é –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–ò —Ç—ã, –ë—Ä—É—Ç</strong>, –∏–ª–∏ –ø–æ–∫–∞ —Ü–µ–ª—å –Ω–µ —Å–æ–≤–µ—Ä—à–∏—Ç –ø—Ä–æ–≤–µ—Ä–∫—É –°–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—è —É–±–µ–∂–¥–µ–Ω–∏—é, —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∫–æ—Ç–æ—Ä–æ–π –≤—ã—à–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ <strong>–ò —Ç—ã, –ë—Ä—É—Ç</strong>.'),
        -- Witcher skills
        ('meditation', '–í–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç –≤–æ–π—Ç–∏ –≤ –º–µ–¥–∏—Ç–∞—Ç–∏–≤–Ω—ã–π —Ç—Ä–∞–Ω—Å, —á—Ç–æ –ø–æ–∑–≤–æ–ª—è–µ—Ç –µ–º—É –ø–æ–ª—É—á–∏—Ç—å –≤—Å–µ –ø—Ä–µ–∏–º—É—â–µ—Å—Ç–≤–∞ —Å–Ω–∞, –Ω–æ –ø—Ä–∏ —ç—Ç–æ–º —Å–æ—Ö—Ä–∞–Ω—è—Ç—å –±–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å. –í–æ –≤—Ä–µ–º—è –º–µ–¥–∏—Ç–∞—Ü–∏–∏ –≤–µ–¥—å–º–∞–∫ —Å—á–∏—Ç–∞–µ—Ç—Å—è –Ω–∞—Ö–æ–¥—è—â–∏–º—Å—è –≤ —Å–æ–∑–Ω–∞–Ω–∏–∏ –¥–ª—è —Ç–æ–≥–æ, —á—Ç–æ–±—ã –∑–∞–º–µ—Ç–∏—Ç—å —á—Ç–æ-–ª–∏–±–æ –≤ —Ä–∞–¥–∏—É—Å–µ –≤ –º–µ—Ç—Ä–∞—Ö, —Ä–∞–≤–Ω–æ–º —É–¥–≤–æ–µ–Ω–Ω–æ–º—É –∑–Ω–∞—á–µ–Ω–∏—é –µ–≥–æ <strong>–ú–µ–¥–∏—Ç–∞—Ü–∏–∏</strong>.'),
        ('magical_source', '–ü–æ –º–µ—Ä–µ —Ç–æ–≥–æ –∫–∞–∫ –≤–µ–¥—å–º–∞–∫ –≤—Å—ë –±–æ–ª—å—à–µ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç –∑–Ω–∞–∫–∏, –µ–≥–æ —Ç–µ–ª–æ –ø–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ –ø—Ä–∏–≤—ã–∫–∞–µ—Ç –∫ —Ç–µ—á–µ–Ω–∏—é –º–∞–≥–∏—á–µ—Å–∫–æ–π —ç–Ω–µ—Ä–≥–∏–∏. –ö–∞–∂–¥—ã–µ 2 –æ—á–∫–∞, –≤–ª–æ–∂–µ–Ω–Ω—ã–µ –≤ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å <strong>–ú–∞–≥–∏—á–µ—Å–∫–∏–π –∏—Å—Ç–æ—á–Ω–∏–∫</strong>, –ø–æ–≤—ã—à–∞—é—Ç –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –≤–µ–¥—å–º–∞–∫–∞ –Ω–∞ 1. –ö–æ–≥–¥–∞ —ç—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –¥–æ—Å—Ç–∏–≥–∞–µ—Ç 10 —É—Ä–æ–≤–Ω—è, –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –≤–µ–¥—å–º–∞–∫–∞ —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è —Ä–∞–≤–Ω–æ 7. –≠—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å —Ä–∞–∑–≤–∏–≤–∞–µ—Ç—Å—è –∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ –ø—Ä–æ—á–∏–º –Ω–∞–≤—ã–∫–∞–º.'),
        ('heliotrope', '–ö–æ–≥–¥–∞ –≤–µ–¥—å–º–∞–∫ —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è —Ü–µ–ª—å—é –∑–∞–∫–ª–∏–Ω–∞–Ω–∏—è, –∏–Ω–≤–æ–∫–∞—Ü–∏–∏ –∏–ª–∏ –ø–æ—Ä—á–∏, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ì–µ–ª–∏–æ—Ç—Ä–æ–ø</strong>, —á—Ç–æ–±—ã –ø–æ–ø—ã—Ç–∞—Ç—å—Å—è –æ—Ç–º–µ–Ω–∏—Ç—å —ç—Ñ—Ñ–µ–∫—Ç. –û–Ω –¥–æ–ª–∂–µ–Ω –≤—ã–∫–∏–Ω—É—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç, –∫–æ—Ç–æ—Ä—ã–π –±–æ–ª—å—à–µ –ª–∏–±–æ —Ä–∞–≤–µ–Ω —Ä–µ–∑—É–ª—å—Ç–∞—Ç—É –µ–≥–æ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞, –∞ —Ç–∞–∫–∂–µ –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –í—ã–Ω–æ—Å–ª–∏–≤–æ—Å—Ç–∏, —Ä–∞–≤–Ω–æ–µ –ø–æ–ª–æ–≤–∏–Ω–µ –í—ã–Ω–æ—Å–ª–∏–≤–æ—Å—Ç–∏, –∑–∞—Ç—Ä–∞—á–µ–Ω–Ω–æ–π –Ω–∞ —Å–æ—Ç–≤–æ—Ä–µ–Ω–∏–µ –º–∞–≥–∏–∏.'),
        ('iron_stomach', '–ó–∞ –≥–æ–¥—ã —É–ø–æ—Ç—Ä–µ–±–ª–µ–Ω–∏—è —è–¥–æ–≤–∏—Ç—ã—Ö –≤–µ–¥—å–º–∞—á—å–∏—Ö —ç–ª–∏–∫—Å–∏—Ä–æ–≤ –≤–µ–¥—å–º–∞–∫–∏ –ø—Ä–∏–≤—ã–∫–∞—é—Ç –∫ —Ç–æ–∫—Å–∏–Ω–∞–º. –í–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç –≤—ã–¥–µ—Ä–∂–∞—Ç—å –æ—Ç–≤–∞—Ä—ã –∏ —ç–ª–∏–∫—Å–∏—Ä—ã —Å—É–º–º–∞—Ä–Ω–æ–π —Ç–æ–∫—Å–∏—á–Ω–æ—Å—Ç—å—é –Ω–∞ 5% –±–æ–ª—å—à–µ –∑–∞ –∫–∞–∂–¥—ã–µ 2 –æ—á–∫–∞, –≤–ª–æ–∂–µ–Ω–Ω—ã–µ –≤ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å <strong>–ö—Ä–µ–ø–∫–∏–π –∂–µ–ª—É–¥–æ–∫</strong>. –≠—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å —Ä–∞–∑–≤–∏–≤–∞–µ—Ç—Å—è –∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ –ø—Ä–æ—á–∏–º –Ω–∞–≤—ã–∫–∞–º. –ù–∞ 10 —É—Ä–æ–≤–Ω–µ –º–∞–∫—Å–∏–º–∞–ª—å–Ω–∞—è —Ç–æ–∫—Å–∏—á–Ω–æ—Å—Ç—å –¥–ª—è –≤–µ–¥—å–º–∞–∫–∞ —Ä–∞–≤–Ω–∞ 150%.'),
        ('frenzy', '–ë—É–¥—É—á–∏ –æ—Ç—Ä–∞–≤–ª–µ–Ω–Ω—ã–º, –≤–µ–¥—å–º–∞–∫ –≤–ø–∞–¥–∞–µ—Ç –≤ —è—Ä–æ—Å—Ç—å –∏ –Ω–∞–Ω–æ—Å–∏—Ç –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ 1 —É—Ä–æ–Ω –≤ –±–ª–∏–∂–Ω–µ–º –±–æ—é –∑–∞ –∫–∞–∂–¥—ã–π —É—Ä–æ–≤–µ–Ω—å <strong>–Ø—Ä–æ—Å—Ç–∏</strong>. –í —ç—Ç–æ–º —Å–æ—Å—Ç–æ—è–Ω–∏–∏ –µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω–∞—è —Ü–µ–ª—å –≤–µ–¥—å–º–∞–∫–∞ ‚Äî –¥–æ–±—Ä–∞—Ç—å—Å—è –¥–æ –±–µ–∑–æ–ø–∞—Å–Ω–æ–≥–æ –º–µ—Å—Ç–∞ –∏–ª–∏ —É–±–∏—Ç—å –æ—Ç—Ä–∞–≤–∏—Ç–µ–ª—è. –î–µ–π—Å—Ç–≤–∏–µ <strong>–Ø—Ä–æ—Å—Ç–∏</strong> –∑–∞–∫–∞–Ω—á–∏–≤–∞–µ—Ç—Å—è –æ–¥–Ω–æ–≤—Ä–µ–º–µ–Ω–Ω–æ —Å –¥–µ–π—Å—Ç–≤–∏–µ–º —è–¥–∞. –í–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç –ø–æ–ø—ã—Ç–∞—Ç—å—Å—è –∏–∑–±–∞–≤–∏—Ç—å—Å—è –æ—Ç <strong>–Ø—Ä–æ—Å—Ç–∏</strong> —Ä–∞–Ω—å—à–µ, —Å–æ–≤–µ—Ä—à–∏–≤ –ø—Ä–æ–≤–µ—Ä–∫—É –°—Ç–æ–π–∫–æ—Å—Ç–∏ —Å–æ –°–õ 15.'),
        ('transmutation', '–ü—Ä–∏–Ω–∏–º–∞—è –æ—Ç–≤–∞—Ä, –≤–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–¢—Ä–∞–Ω—Å–º—É—Ç–∞—Ü–∏–∏</strong> —Å–æ –°–õ 18. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ç–µ–ª–æ –≤–µ–¥—å–º–∞–∫–∞ –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –≤ —Å–µ–±—è –Ω–µ—Å–∫–æ–ª—å–∫–æ –±–æ–ª—å—à–µ –º—É—Ç–∞–≥–µ–Ω–∞, —á–µ–º –æ–±—ã—á–Ω–æ, —á—Ç–æ –ø–æ–∑–≤–æ–ª—è–µ—Ç –ø–æ–ª—É—á–∏—Ç—å –±–æ–Ω—É—Å –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –ø—Ä–∏–Ω—è—Ç–æ–≥–æ –æ—Ç–≤–∞—Ä–∞ (—Å–º. —Ç–∞–±–ª–∏—Ü—É –Ω–∞ –ø–æ–ª—è—Ö). –î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å –¥–µ–π—Å—Ç–≤–∏—è –æ—Ç–≤–∞—Ä–∞ —É–º–µ–Ω—å—à–∞–µ—Ç—Å—è –≤–¥–≤–æ–µ. –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –º—É—Ç–∞—Ü–∏–∏ —Å–ª–∏—à–∫–æ–º –º–∞–ª—ã, —á—Ç–æ–±—ã –∏—Ö –∑–∞–º–µ—Ç–∏—Ç—å.'),
        ('parry_arrows', '–í–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —ç—Ç–æ–π —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ —Å–æ —à—Ç—Ä–∞—Ñ–æ–º -3, —á—Ç–æ–±—ã –æ—Ç–±–∏—Ç—å –ª–µ—Ç—è—â–∏–π —Ñ–∏–∑–∏—á–µ—Å–∫–∏–π —Å–Ω–∞—Ä—è–¥. –ü—Ä–∏ –æ—Ç–±–∏–≤–∞–Ω–∏–∏ –≤–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç –≤—ã–±—Ä–∞—Ç—å —Ü–µ–ª—å –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö 10 –º. –≠—Ç–∞ —Ü–µ–ª—å –¥–æ–ª–∂–Ω–∞ —Å–æ–≤–µ—Ä—à–∏—Ç—å –¥–µ–π—Å—Ç–≤–∏–µ –∑–∞—â–∏—Ç—ã –ø—Ä–æ—Ç–∏–≤ –±—Ä–æ—Å–∫–∞ <strong>–û—Ç–±–∏–≤–∞–Ω–∏—è —Å—Ç—Ä–µ–ª</strong> –≤–µ–¥—å–º–∞–∫–∞, –∏–ª–∏ –æ–Ω–∞ –±—É–¥–µ—Ç –æ—à–µ–ª–æ–º–ª–µ–Ω–∞ –∏–∑-–∑–∞ –ø–æ–ø–∞–¥–∞–Ω–∏—è –æ—Ç–±–∏—Ç–æ–≥–æ —Å–Ω–∞—Ä—è–¥–∞.<br><br><b>–û—Ç–±–∏–≤–∞–Ω–∏–µ –±–æ–º–±.</b> –ë–æ–º–±—ã –∏ –¥—Ä—É–≥–∏–µ –∞—Ç–∞–∫–∏, –ø–æ—Ä–∞–∂–∞—é—â–∏–µ –∑–æ–Ω—É, –≤–∑—Ä—ã–≤–∞—é—Ç—Å—è –ø–æ—Å–ª–µ –æ—Ç–±–∏–≤–∞–Ω–∏—è. –ï—Å–ª–∏ –≤—Ç–æ—Ä–∞—è —Ü–µ–ª—å —É–∫–ª–æ–Ω—è–µ—Ç—Å—è –æ—Ç –∞—Ç–∞–∫–∏, —Å–æ–≤–µ—Ä—à–∏—Ç–µ –±—Ä–æ—Å–æ–∫ –ø–æ —Ç–∞–±–ª–∏—Ü–µ —Ä–∞–∑–±—Ä–æ—Å–∞ (—Å–º. —Å—Ç—Ä. 152), —á—Ç–æ–±—ã –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å, –∫—É–¥–∞ –ø–æ–ø–∞–¥—ë—Ç —Å–Ω–∞—Ä—è–¥.'),
        ('quick_strike', '–ó–∞–∫–æ–Ω—á–∏–≤ —Å–≤–æ–π —Ö–æ–¥, –≤–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å 5 –æ—á–∫–æ–≤ –í—ã–Ω –∏ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ë—ã—Å—Ç—Ä–æ–≥–æ —É–¥–∞—Ä–∞</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –†–µ–∞ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞ —Ö–ó. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –≤–µ–¥—å–º–∞–∫ —Å–æ–≤–µ—Ä—à–∞–µ—Ç –µ—â—ë –æ–¥–Ω—É –∞—Ç–∞–∫—É –≤ —ç—Ç–æ—Ç —Ä–∞—É–Ω–¥ –ø—Ä–æ—Ç–∏–≤ —ç—Ç–æ–≥–æ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞, –∫–æ—Ç–æ—Ä–∞—è –º–æ–∂–µ—Ç –≤–∫–ª—é—á–∞—Ç—å –≤ —Å–µ–±—è —Ä–∞–∑–æ—Ä—É–∂–µ–Ω–∏–µ, –ø–æ–¥—Å–µ—á–∫—É –∏ –ø—Ä–æ—á–∏–µ –∞—Ç–∞–∫–∏.'),
        ('whirl', '–ü–æ—Ç—Ä–∞—Ç–∏–≤ 5 –æ—á–∫–æ–≤ –í—ã–Ω –∑–∞ —Ä–∞—É–Ω–¥, –≤–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç –∑–∞–∫—Ä—É—Ç–∏—Ç—å—Å—è –≤ <strong>–í–∏—Ö—Ä–µ</strong>, —Å–æ–≤–µ—Ä—à–∞—è –∫–∞–∂–¥—ã–π —Ö–æ–¥ –ø–æ –æ–¥–Ω–æ–π –∞—Ç–∞–∫–µ –ø—Ä–æ—Ç–∏–≤ –≤—Å–µ—Ö, –∫—Ç–æ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö –¥–∏—Å—Ç–∞–Ω—Ü–∏–∏ –µ–≥–æ –º–µ—á–∞. –ü—Ä–æ–≤–µ—Ä–∫–∞ <strong>–í–∏—Ö—Ä—è</strong> —Å—á–∏—Ç–∞–µ—Ç—Å—è –ø—Ä–æ–≤–µ—Ä–∫–æ–π –∞—Ç–∞–∫–∏. –ù–∞—Ö–æ–¥—è—Å—å –≤ <strong>–í–∏—Ö—Ä–µ</strong>, –≤–µ–¥—å–º–∞–∫ –º–æ–∂–µ—Ç —Ç–æ–ª—å–∫–æ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞—Ç—å –µ–≥–æ, —É–∫–ª–æ–Ω—è—Ç—å—Å—è –∏ –ø–µ—Ä–µ–¥–≤–∏–≥–∞—Ç—å—Å—è –Ω–∞ 2 –º–µ—Ç—Ä–∞ –∑–∞ —Ä–∞—É–Ω–¥. –õ—é–±–æ–µ –¥—Ä—É–≥–æ–µ –¥–µ–π—Å—Ç–≤–∏–µ –∏–ª–∏ –ø–æ–ª—É—á–µ–Ω–Ω—ã–π —É–¥–∞—Ä –ø—Ä–µ–∫—Ä–∞—â–∞—é—Ç <strong>–í–∏—Ö—Ä—å</strong>.'),
        -- Doctor skills
        ('diagnose', '–ü—Ä–∏ –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ –æ—Å–º–æ—Ç—Ä–µ—Ç—å —Ä–∞–Ω–µ–Ω–æ–µ —Å—É—â–µ—Å—Ç–≤–æ –º–µ–¥–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–î–∏–∞–≥–Ω–æ–∑–∞</strong> —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –æ–Ω –æ–±–Ω–∞—Ä—É–∂–∏–≤–∞–µ—Ç –≤—Å–µ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ —Ä–∞–Ω–µ–Ω–∏—è —Ü–µ–ª–∏ –∏ —É–∑–Ω–∞—ë—Ç, —Å–∫–æ–ª—å–∫–æ –ø—É–Ω–∫—Ç–æ–≤ –∑–¥–æ—Ä–æ–≤—å—è —É –Ω–µ—ë –æ—Å—Ç–∞–ª–æ—Å—å. –≠—Ç–æ —Ç–∞–∫–∂–µ –¥–∞—ë—Ç –±–æ–Ω—É—Å +2 –∫–æ –≤—Å–µ–º –ø—Ä–æ–≤–µ—Ä–∫–∞–º <strong>–õ–µ—á–∞—â–µ–≥–æ –ø—Ä–∏–∫–æ—Å–Ω–æ–≤–µ–Ω–∏—è</strong> –¥–ª—è –ª–µ—á–µ–Ω–∏—è —ç—Ç–∏—Ö —Ä–∞–Ω.'),
        ('analysis', '–ü–µ—Ä–µ–¥ –ø—Ä–æ–≤–µ—Ä–∫–æ–π <strong>–õ–µ—á–∞—â–µ–≥–æ –ø—Ä–∏–∫–æ—Å–Ω–æ–≤–µ–Ω–∏—è</strong> –º–µ–¥–∏–∫ –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å —Ö–æ–¥ –∏ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–û—Å–º–æ—Ç—Ä–∞</strong> —Å–æ –°–õ, –∑–∞–≤–∏—Å—è—â–µ–π –æ—Ç —Å–µ—Ä—å—ë–∑–Ω–æ—Å—Ç–∏ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–≥–æ —Ä–∞–Ω–µ–Ω–∏—è. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–µ–¥–∏–∫ –ø–æ–Ω–∏–º–∞–µ—Ç –ø—Ä–∏—Ä–æ–¥—É —Ä–∞–Ω–µ–Ω–∏—è –∏ –∑–∞ –∫–∞–∂–¥—ã–µ 2 –ø—É–Ω–∫—Ç–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Å–≤—ã—à–µ –°–õ (–º–∏–Ω–∏–º—É–º 1) —Ö–∏—Ä—É—Ä–≥–∏—á–µ—Å–∫–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è –∑–∞–π–º—ë—Ç –Ω–∞ 1 —Ö–æ–¥ –º–µ–Ω—å—à–µ.'),
        ('effective_surgery', '–ü–µ—Ä–µ–¥ —Ç–µ–º –∫–∞–∫ –Ω–∞—á–∞—Ç—å –ª–µ—á–∏—Ç—å –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–µ —Ä–∞–Ω–µ–Ω–∏–µ, –º–µ–¥–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–≠—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ–π —Ö–∏—Ä—É—Ä–≥–∏–∏</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –ø—Ä–æ–≤–µ—Ä–∫–∏ <strong>–õ–µ—á–∞—â–µ–≥–æ –ø—Ä–∏–∫–æ—Å–Ω–æ–≤–µ–Ω–∏—è</strong>, –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ–π –¥–ª—è –ª–µ—á–µ–Ω–∏—è –¥–∞–Ω–Ω–æ–≥–æ —Ä–∞–Ω–µ–Ω–∏—è. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–µ–¥–∏–∫ –∑–∞—à–∏–≤–∞–µ—Ç —Ä–∞–Ω—ã —Å—Ç–æ–ª—å –∏—Å–∫—É—Å–Ω–æ, —á—Ç–æ –æ–Ω–∏ –∏—Å—Ü–µ–ª—è—é—Ç—Å—è –≤ –¥–≤–∞ —Ä–∞–∑–∞ –±—ã—Å—Ç—Ä–µ–µ. –≠—Ç—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –ø—Ä–∏ –ª–µ—á–µ–Ω–∏–∏ –∫–∞–∫ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏—Ö —Ä–∞–Ω–µ–Ω–∏–π, —Ç–∞–∫ –∏ –æ–±—ã—á–Ω—ã—Ö.'),
        ('healing_tent', '–ü–∞–ª–∞—Ç–∫–∞ –ª–µ–∫–∞—Ä—è –ø–æ–∑–≤–æ–ª—è–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º, —á—Ç–æ–±—ã —Å–æ–∑–¥–∞—Ç—å —É–∫—Ä—ã—Ç–∏–µ —Å –æ–ø—Ç–∏–º–∞–ª—å–Ω—ã–º–∏ —É—Å–ª–æ–≤–∏—è–º–∏ –¥–ª—è –ª–µ—á–µ–Ω–∏—è. –≠—Ç–æ —Ç—Ä–µ–±—É–µ—Ç 1 —á–∞—Å–∞, –Ω–æ –¥–æ–±–∞–≤–ª—è–µ—Ç –±–æ–Ω—É—Å +3 –∫ —Å–æ–≤–µ—Ä—à—ë–Ω–Ω—ã–º –≤–Ω—É—Ç—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–∞–º <strong>–õ–µ—á–∞—â–µ–≥–æ –ø—Ä–∏–∫–æ—Å–Ω–æ–≤–µ–Ω–∏—è</strong>/–ü–µ—Ä–≤–æ–π –ø–æ–º–æ—â–∏ –∏ +2 –∫ —Å–∫–æ—Ä–æ—Å—Ç–∏ –∏—Å—Ü–µ–ª–µ–Ω–∏—è –ª—é–±–æ–≥–æ, –∫—Ç–æ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –≤ –ø–∞–ª–∞—Ç–∫–µ, –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–ü–∞–ª–∞—Ç–∫–∏ –ª–µ–∫–∞—Ä—è</strong>.'),
        ('improvised_medicine', '–ú–µ–¥–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ü–æ–¥—Ä—É—á–Ω—ã—Ö —Å—Ä–µ–¥—Å—Ç–≤</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –æ–ø—Ä–µ–¥–µ–ª—ë–Ω–Ω–æ–≥–æ –ª–µ—á–∞—â–µ–≥–æ –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–æ–≥–æ —Å–æ—Å—Ç–∞–≤–∞, —á—Ç–æ–±—ã –∑–∞–º–µ–Ω–∏—Ç—å –µ–≥–æ —á–µ–º-—Ç–æ, —á—Ç–æ —É –Ω–µ–≥–æ –µ—Å—Ç—å –≤ –Ω–∞–ª–∏—á–∏–∏. –ü—Ä–æ–≤–µ—Ä–∫–∞ –∑–∞–Ω–∏–º–∞–µ—Ç 1 —Ä–∞—É–Ω–¥, –∏ –µ—ë –º–æ–∂–Ω–æ –ø–æ–≤—Ç–æ—Ä–∏—Ç—å –ø—Ä–∏ –ø—Ä–æ–≤–∞–ª–µ. <strong>–ü–æ–¥—Ä—É—á–Ω—ã–µ —Å—Ä–µ–¥—Å—Ç–≤–∞</strong> –≤–µ—Å—å–º–∞ —Å–ø–µ—Ü–∏—Ñ–∏—á–Ω—ã –∏ –¥–µ–π—Å—Ç–≤—É—é—Ç —Ç–æ–ª—å–∫–æ –Ω–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω—É—é —Ä–∞–Ω—É.'),
        ('herbal_remedy', '–°–º–µ—à–∞–≤ –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–∏–µ —Å—É–±—Å—Ç–∞–Ω—Ü–∏–∏, –º–µ–¥–∏–∫ –º–æ–∂–µ—Ç —Å–æ–∑–¥–∞—Ç—å —Ä–∞—Å—Ç–∏—Ç–µ–ª—å–Ω–æ–µ –ª–µ–∫–∞—Ä—Å—Ç–≤–æ, –∫–æ—Ç–æ—Ä–æ–µ –¥–∞—ë—Ç –±–æ–Ω—É—Å—ã/—ç—Ñ—Ñ–µ–∫—Ç—ã –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Å–æ—Å—Ç–∞–≤–∞ (—Å–º. —Ç–∞–±–ª–∏—Ü—É –†–∞—Å—Ç–∏—Ç–µ–ª—å–Ω—ã–µ –ª–µ–∫–∞—Ä—Å—Ç–≤–∞ –Ω–∞ –ø–æ–ª—è—Ö). –ö–∞–∂–¥–æ–µ –ª–µ–∫–∞—Ä—Å—Ç–≤–æ —Ö—Ä–∞–Ω–∏—Ç—Å—è –º–∞–∫—Å–∏–º—É–º 3 –¥–Ω—è, –ø–æ—Å–ª–µ –∏—Å—Ç–µ—á–µ–Ω–∏—è —ç—Ç–æ–≥–æ —Å—Ä–æ–∫–∞ –µ–≥–æ –Ω–µ–ª—å–∑—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å. –ß—Ç–æ–±—ã –ø–æ–ª—É—á–∏—Ç—å –±–æ–Ω—É—Å, –ª–µ–∫–∞—Ä—Å—Ç–≤–æ —Å–ª–µ–¥—É–µ—Ç —Å–∂–µ—á—å –∏–ª–∏ —Ä–∞–∑–∂–µ–≤–∞—Ç—å; –µ–≥–æ —Ö–≤–∞—Ç–∞–µ—Ç —Ç–æ–ª—å–∫–æ –Ω–∞ –æ–¥–Ω–æ –ø—Ä–∏–º–µ–Ω–µ–Ω–∏–µ. –°–æ–∑–¥–∞–Ω–∏–µ –ª–µ–∫–∞—Ä—Å—Ç–≤–∞ –∑–∞–Ω–∏–º–∞–µ—Ç 1 —Ö–æ–¥.'),
        ('bloody_wound', '–ù–∞–Ω–æ—Å—è —É—Ä–æ–Ω –∫–ª–∏–Ω–∫–æ–≤—ã–º –æ—Ä—É–∂–∏–µ–º, –º–µ–¥–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ö—Ä–æ–≤–∞–≤–∞—è —Ä–∞–Ω–∞</strong> —Å–æ –°–õ 15. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –ø–æ—Å–ª–µ —ç—Ç–æ–π –∞—Ç–∞–∫–∏ —Ü–µ–ª—å –Ω–∞—á–∏–Ω–∞–µ—Ç –∏—Å—Ç–µ–∫–∞—Ç—å –∫—Ä–æ–≤—å—é —Å–æ —Å–∫–æ—Ä–æ—Å—Ç—å—é 1 —É—Ä–æ–Ω –∑–∞ –∫–∞–∂–¥—ã–µ 2 –ø—É–Ω–∫—Ç–∞ —Å–≤—ã—à–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω–æ–π –°–õ –∑–∞ —Ä–∞—É–Ω–¥. –ö—Ä–æ–≤–æ—Ç–µ—á–µ–Ω–∏–µ –º–æ–∂–Ω–æ –æ—Å—Ç–∞–Ω–æ–≤–∏—Ç—å —Ç–æ–ª—å–∫–æ –ø—Ä–æ–≤–µ—Ä–∫–æ–π –ü–µ—Ä–≤–æ–π –ø–æ–º–æ—â–∏ —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç—É –ø—Ä–æ–≤–µ—Ä–∫–∏ <strong>–ö—Ä–æ–≤–∞–≤–æ–π —Ä–∞–Ω—ã</strong>.'),
        ('practical_butchery', '–ú–µ–¥–∏–∫–º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∞—è —Ä–µ–∑–Ω—è</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –¢–µ–ª —Ö 3 –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞, —á—Ç–æ–±—ã –æ–±—ã—á–Ω—ã–µ –∏ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ —Ä–∞–Ω–µ–Ω–∏—è –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞ –∏—Å—Ü–µ–ª—è–ª–∏—Å—å –≤ –¥–≤–∞ —Ä–∞–∑–∞ –º–µ–¥–ª–µ–Ω–Ω–µ–µ. –î—Ä—É–≥–∏–µ –º–µ–¥–∏–∫–∏ –º–æ–≥—É—Ç –Ω–µ–π—Ç—Ä–∞–ª–∏–∑–æ–≤–∞—Ç—å —ç—Ç–æ—Ç —ç—Ñ—Ñ–µ–∫—Ç –ø—Ä–∏ –ø–æ–º–æ—â–∏ <strong>–≠—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ–π —Ö–∏—Ä—É—Ä–≥–∏–∏</strong> –∏ –ø—Ä–µ–¥–º–µ—Ç–æ–≤, –ø–æ–≤—ã—à–∞—é—â–∏—Ö —Å–∫–æ—Ä–æ—Å—Ç—å –∏—Å—Ü–µ–ª–µ–Ω–∏—è –æ–±—ã—á–Ω—ã—Ö –∏ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏—Ö —Ä–∞–Ω.'),
        ('crippling_wound', '–ú–µ–¥–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ö–∞–ª–µ—á–∞—â–∞—è —Ä–∞–Ω–∞</strong> –ø—Ä–æ—Ç–∏–≤ –∑–∞—â–∏—Ç—ã —Ü–µ–ª–∏. –≠—Ç–∞ –∞—Ç–∞–∫–∞ –¥–∞—ë—Ç —à—Ç—Ä–∞—Ñ -6 –∫ –ø–æ–ø–∞–¥–∞–Ω–∏—é, –Ω–æ –ø—Ä–∏ —É—Å–ø–µ—Ö–µ —Å–Ω–∏–∂–∞–µ—Ç –†–µ–∞–∫—Ü–∏—é, –¢–µ–ª–æ—Å–ª–æ–∂–µ–Ω–∏–µ –∏–ª–∏ –°–∫–æ—Ä–æ—Å—Ç—å —Ü–µ–ª–∏ –Ω–∞ 1 –ø—É–Ω–∫—Ç –∑–∞ –∫–∞–∂–¥—ã–µ 3 –ø—É–Ω–∫—Ç–∞ —Å–≤—ã—à–µ –±—Ä–æ—Å–∫–∞ –∑–∞—â–∏—Ç—ã. –®—Ç—Ä–∞—Ñ –º–æ–∂–Ω–æ —Å–Ω—è—Ç—å, —Ç–æ–ª—å–∫–æ —Å–æ–≤–µ—Ä—à–∏–≤ –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–≠—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ–π —Ö–∏—Ä—É—Ä–≥–∏–∏</strong> —Å —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–º –≤—ã—à–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ –∞—Ç–∞–∫–∏ –º–µ–¥–∏–∫–∞.'),
        -- Mage skills
        ('scheming', '–ú–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–°—Ç—Ä–æ–∏—Ç—å –∫–æ–∑–Ω–∏</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –ò–Ω—Ç—Ö–ó —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–∞–≥ –ø–æ–ª—É—á–∞–µ—Ç –±–æ–Ω—É—Å +3 –∫ –û–±–º–∞–Ω—É, –°–æ–±–ª–∞–∑–Ω–µ–Ω–∏—é, –ó–∞–ø—É–≥–∏–≤–∞–Ω–∏—é –∏–ª–∏ –£–±–µ–∂–¥–µ–Ω–∏—é –ø—Ä–æ—Ç–∏–≤ —ç—Ç–æ–π —Ü–µ–ª–∏ –±–ª–∞–≥–æ–¥–∞—Ä—è –∑–Ω–∞–Ω–∏—è–º –æ –µ—ë —Å–∏–ª—å–Ω—ã—Ö –∏ —Å–ª–∞–±—ã—Ö —Å—Ç–æ—Ä–æ–Ω–∞—Ö. –ë–æ–Ω—É—Å –¥–µ–π—Å—Ç–≤—É–µ—Ç –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–°—Ç—Ä–æ–∏—Ç—å –∫–æ–∑–Ω–∏</strong>.'),
        ('grape_vine', '–ü–æ—Ç—Ä–∞—Ç–∏–≤ —á–∞—Å –≤—Ä–µ–º–µ–Ω–∏, –º–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–°–ø–ª–µ—Ç–µ–Ω</strong> –ø—Ä–æ—Ç–∏–≤ –≠–º–ø—Ö–ó —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–∞–≥ —É—Å–ø–µ—à–Ω–æ —Ä–∞—Å–ø—É—Å–∫–∞–µ—Ç —Å–ª—É—Ö–∏ –æ —Ü–µ–ª–∏ –ø–æ –≤—Å–µ–º—É –ø–æ—Å–µ–ª–µ–Ω–∏—é, —á—Ç–æ —Å–Ω–∏–∂–∞–µ—Ç —Ä–µ–ø—É—Ç–∞—Ü–∏—é —Ü–µ–ª–∏ –Ω–∞ –ø–æ–ª–æ–≤–∏–Ω—É –∑–Ω–∞—á–µ–Ω–∏—è <strong>–°–ø–ª–µ—Ç–µ–Ω</strong> –º–∞–≥–∞ –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é —ç—Ç–æ–π —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏.'),
        ('assets', '–û–¥–∏–Ω —Ä–∞–∑ –∑–∞ –∏–≥—Ä—É –º–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ü–æ–ª–µ–∑–Ω—ã—Ö —Å–≤—è–∑–µ–π</strong>, —á—Ç–æ–±—ã –≤—Å–ø–æ–º–Ω–∏—Ç—å –æ –∫–æ–º—Ç–æ, –∫—Ç–æ –º–æ–≥ –±—ã –±—ã—Ç—å –ø–æ–ª–µ–∑–µ–Ω. –†–µ–∑—É–ª—å—Ç–∞—Ç –ø—Ä–æ–≤–µ—Ä–∫–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–∏—Ç—å –º–µ–∂–¥—É —á–µ—Ç—ã—Ä—å–º—è –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º–∏, —É–∫–∞–∑–∞–Ω–Ω—ã–º–∏ –≤ —Ç–∞–±–ª–∏—Ü–µ –Ω–∞ –ø–æ–ª—è—Ö, —á—Ç–æ–±—ã –ø–æ–Ω—è—Ç—å, –∫—Ç–æ —ç—Ç–æ—Ç –∑–Ω–∞–∫–æ–º—ã–π. –¢–æ, –∫–∞–∫ –∞–≥–µ–Ω—Ç –±—É–¥–µ—Ç –ø–æ–º–æ–≥–∞—Ç—å –º–∞–≥—É, –∑–∞–≤–∏—Å–∏—Ç –æ—Ç –∏—Ö –æ—Ç–Ω–æ—à–µ–Ω–∏–π.'),
        ('reverse_engineer', '–ü–æ—Ç—Ä–∞—Ç–∏–≤ —á–∞—Å –Ω–∞ –∏–∑—É—á–µ–Ω–∏–µ –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–æ–≥–æ —Å–æ—Å—Ç–∞–≤–∞, –º–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ê–Ω–∞–ª–∏–∑–∞</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è —ç—Ç–æ–≥–æ –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–æ–≥–æ —Å–æ—Å—Ç–∞–≤–∞ + 3. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–∞–≥ –≤—ã–≤–æ–¥–∏—Ç –∏ –∑–∞–ø–∏—Å—ã–≤–∞–µ—Ç —Ñ–æ—Ä–º—É–ª—É —ç—Ç–æ–≥–æ —Å–æ—Å—Ç–∞–≤–∞. –°–õ —Å–æ–∑–¥–∞–Ω–∏—è –ø—Ä–µ–¥–º–µ—Ç–∞ –ø–æ –≤–æ—Å—Å–æ–∑–¥–∞–Ω–Ω–æ–π —Ñ–æ—Ä–º—É–ª–µ –Ω–∞ 3 –ø—É–Ω–∫—Ç–∞ –≤—ã—à–µ, –Ω–æ –≤ –∏—Ç–æ–≥–µ –º–∞–≥ –ø–æ–ª—É—á–∞–µ—Ç –∂–µ–ª–∞–µ–º—ã–π –ø—Ä–µ–¥–º–µ—Ç.'),
        ('distillation', '–ú–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–î–∏—Å—Ç–∏–ª–ª—è—Ü–∏–∏</strong> –≤–º–µ—Å—Ç–æ –ê–ª—Ö–∏–º–∏–∏ –ø—Ä–∏ –∏–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏–∏ –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–æ–≥–æ —Å–æ—Å—Ç–∞–≤–∞. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–∞–≥ —Å–æ–∑–¥–∞—ë—Ç –ø–æ—Ä—Ü–∏—é —Å–æ—Å—Ç–∞–≤–∞, –¥–µ–π—Å—Ç–≤—É—é—â—É—é –≤ –ø–æ–ª—Ç–æ—Ä–∞ —Ä–∞–∑–∞ —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–µ–µ –æ–±—ã—á–Ω–æ–π –ø–æ—Ä—Ü–∏–∏ ‚Äî —ç—Ç–æ –æ—Ç–Ω–æ—Å–∏—Ç—Å—è –∫ –¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏, —É—Ä–æ–Ω—É –∏–ª–∏ –°–õ —Å–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—è –Ω–∞ –≤—ã–±–æ—Ä –º–∞–≥–∞. –û–∫—Ä—É–≥–ª–µ–Ω–∏–µ —ç—Ñ—Ñ–µ–∫—Ç–∞ –≤—Å–µ–≥–¥–∞ –∏–¥—ë—Ç –≤–Ω–∏–∑.'),
        ('mutate', '–ú–∞–≥ –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å –ø–æ–ª–Ω—ã–π –¥–µ–Ω—å –∏ –≤—Å—é —Å–≤–æ—é –í—ã–Ω–æ—Å–ª–∏–≤–æ—Å—Ç—å –Ω–∞ –ø—Ä–æ–≤–µ–¥–µ–Ω–∏–µ —ç–∫—Å–ø–µ—Ä–∏–º–µ–Ω—Ç–æ–≤ –Ω–∞–¥ —Ü–µ–ª—å—é, —á—Ç–æ–±—ã —Å–æ–≤–µ—Ä—à–∏—Ç—å –±—Ä–æ—Å–æ–∫ <strong>–ú—É—Ç–∞—Ü–∏–∏</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π (28 -(–¢–µ–ª+ –í–æ–ª—è —Ü–µ–ª–∏)/ 2), –∏ –º—É—Ç–∞—Ü–∏–µ–π –∏–∑–º–µ–Ω–∏—Ç—å —Ü–µ–ª—å. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ü–µ–ª—å –ø–æ–ª—É—á–∞–µ—Ç –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –º—É—Ç–∞–≥–µ–Ω —Å –ø–æ–¥—Ö–æ–¥—è—â–µ–π –º–∞–ª–æ–π –º—É—Ç–∞—Ü–∏–µ–π. –ü—Ä–∏ –ø—Ä–æ–≤–∞–ª–µ —Ü–µ–ª—å –æ–∫–∞–∑—ã–≤–∞–µ—Ç—Å—è –ø—Ä–∏ —Å–º–µ—Ä—Ç–∏ –∏ –ø–æ–ª—É—á–∞–µ—Ç –∫—Ä—É–ø–Ω—É—é –º—É—Ç–∞—Ü–∏—é.<br><br>–ú—É—Ç–∏—Ä–æ–≤–∞—Ç—å –æ–¥–Ω–æ–≥–æ –ø–µ—Ä—Å–æ–Ω–∞–∂–∞ –º–æ–∂–Ω–æ –º–∞–∫—Å–∏–º—É–º –¥–≤–∞ —Ä–∞–∑–∞. –ù–æ–≤—ã–µ –º—É—Ç–∞—Ü–∏–∏ –ø—Ä–∏–º–µ–Ω—è—é—Ç—Å—è –≤–∑–∞–º–µ–Ω –∏–º–µ—é—â–∏—Ö—Å—è.'),
        ('in_touch', '–ü–æ –º–µ—Ä–µ —Ç–æ–≥–æ –∫–∞–∫ –º–∞–≥ –≤—Å—ë –±–æ–ª—å—à–µ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç –º–∞–≥–∏—é, –µ–≥–æ —Ç–µ–ª–æ –ø–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ –ø—Ä–∏–≤—ã–∫–∞–µ—Ç –∫ —Ç–µ—á–µ–Ω–∏—é –º–∞–≥–∏—á–µ—Å–∫–æ–π —ç–Ω–µ—Ä–≥–∏–∏. –ö–∞–∂–¥–æ–µ –æ—á–∫–æ, –≤–ª–æ–∂–µ–Ω–Ω–æ–µ –≤ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å <strong>–£–∫—Ä–µ–ø–ª–µ–Ω–∏–µ —Å–≤—è–∑–∏</strong>, –ø–æ–≤—ã—à–∞–µ—Ç –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –º–∞–≥–∞ –Ω–∞ 2. –ö–æ–≥–¥–∞ —ç—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –¥–æ—Å—Ç–∏–≥–∞–µ—Ç 10 —É—Ä–æ–≤–Ω—è, –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –º–∞–≥–∞ —Ä–∞–≤–Ω–æ 25. –≠—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å —Ä–∞–∑–≤–∏–≤–∞–µ—Ç—Å—è –∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ –ø—Ä–æ—á–∏–º –Ω–∞–≤—ã–∫–∞–º.'),
        ('immutable', '–ú–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–£—Å—Ç–æ–π—á–∏–≤–æ—Å—Ç–∏ –∫ –¥–≤–∏–º–µ—Ä–∏—Ç—É</strong> —Å–æ –°–õ 16 –≤ –ª—é–±–æ–π –º–æ–º–µ–Ω—Ç, –∫–æ–≥–¥–∞ –Ω–∞ –Ω–µ–≥–æ –æ–±—ã—á–Ω–æ –º–æ–∂–µ—Ç –≤–æ–∑–¥–µ–π—Å—Ç–≤–æ–≤–∞—Ç—å –¥–≤–∏–º–µ—Ä–∏—Ç. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–∞–≥ —Å–ø–æ—Å–æ–±–µ–Ω –ø—Ä–æ—Ç–∏–≤–æ—Å—Ç–æ—è—Ç—å —ç—Ñ—Ñ–µ–∫—Ç—É –¥–≤–∏–º–µ—Ä–∏—Ç–∞: —É –Ω–µ–≥–æ –∫—Ä—É–∂–∏—Ç—Å—è –≥–æ–ª–æ–≤–∞ –∏ –æ–Ω –∏—Å–ø—ã—Ç—ã–≤–∞–µ—Ç –¥–∏—Å–∫–æ–º—Ñ–æ—Ä—Ç, –Ω–æ —Å–æ—Ö—Ä–∞–Ω—è–µ—Ç –ø–æ–ª–æ–≤–∏–Ω—É –≠–Ω–µ—Ä–≥–∏–∏ –∏ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å —Å–æ—Ç–≤–æ—Ä—è—Ç—å –∑–∞–∫–ª–∏–Ω–∞–Ω–∏—è.'),
        ('expanded_magic', '–ú–∞–≥ –º–æ–∂–µ—Ç –æ–±—Ä–µ—Å—Ç–∏ –æ–≥—Ä–æ–º–Ω–æ–µ –º–æ–≥—É—â–µ—Å—Ç–≤–æ, –ø—Ä–æ–≤–æ–¥—è –º–∞–≥–∏—á–µ—Å–∫—É—é —ç–Ω–µ—Ä–≥–∏—é —á–µ—Ä–µ–∑ —Ä–∞–∑–Ω—ã–µ —Ñ–æ–∫—É—Å–∏—Ä—É—é—â–∏–µ –º–∞–≥–∏—á–µ—Å–∫–∏–µ –ø—Ä–µ–¥–º–µ—Ç—ã. –ú–∞–≥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–£—Å–∏–ª–µ–Ω–∏—è –º–∞–≥–∏–∏</strong> —Å–æ –°–õ 16 –ø–µ—Ä–µ–¥ —Å–æ—Ç–≤–æ—Ä–µ–Ω–∏–µ–º –∑–∞–∫–ª–∏–Ω–∞–Ω–∏—è –∏–ª–∏ –ø—Ä–æ–≤–µ–¥–µ–Ω–∏–µ–º —Ä–∏—Ç—É–∞–ª–∞. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –º–∞–≥ –º–æ–∂–µ—Ç –ø—Ä–æ–≤–µ—Å—Ç–∏ –º–∞–≥–∏—á–µ—Å–∫—É—é —ç–Ω–µ—Ä–≥–∏—é —á–µ—Ä–µ–∑ –ª—é–±—ã–µ 2 —Ñ–æ–∫—É—Å–∏—Ä—É—é—â–∏—Ö –ø—Ä–µ–¥–º–µ—Ç–∞ –ø–æ —Å–≤–æ–µ–º—É –≤—ã–±–æ—Ä—É, —Å–Ω–∏–∂–∞—è –∑–∞—Ç—Ä–∞—Ç—ã –í—ã–Ω–æ—Å–ª–∏–≤–æ—Å—Ç–∏ –≤–¥–≤–æ–µ.'),
        -- Man At Arms skills
        ('extreme_range', '–°–æ–≤–µ—Ä—à–∞—è –¥–∏—Å—Ç–∞–Ω—Ü–∏–æ–Ω–Ω—É—é –∞—Ç–∞–∫—É, –∫–æ—Ç–æ—Ä–∞—è –ø–æ–ª—É—á–∏–ª–∞ –±—ã —à—Ç—Ä–∞—Ñ –∑–∞ –¥–∏—Å—Ç–∞–Ω—Ü–∏—é, –≤–æ–∏–Ω –º–æ–∂–µ—Ç —É–º–µ–Ω—å—à–∏—Ç—å —à—Ç—Ä–∞—Ñ –Ω–∞ –ø–æ–ª–æ–≤–∏–Ω—É <strong>–ú–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–π –¥–∏—Å—Ç–∞–Ω—Ü–∏–∏</strong>. –û–Ω —Ç–∞–∫–∂–µ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ú–∞–∫—Å–∏–º–∞–ª—å–Ω–∞—è –¥–∏—Å—Ç–∞–Ω—Ü–∏—è</strong> —Å–æ –°–õ 16, —á—Ç–æ–±—ã –∞—Ç–∞–∫–æ–≤–∞—Ç—å —Ü–µ–ª—å –Ω–∞ —Ä–∞—Å—Å—Ç–æ—è–Ω–∏–∏ –¥–æ 3 –¥–∏—Å—Ç–∞–Ω—Ü–∏–π —Å–≤–æ–µ–≥–æ –æ—Ä—É–∂–∏—è —Å–æ —à—Ç—Ä–∞—Ñ–æ–º -10. –≠—Ç–æ—Ç —à—Ç—Ä–∞—Ñ –º–æ–∂–Ω–æ —É–º–µ–Ω—å—à–∏—Ç—å, –ø—Ä–∏–º–µ–Ω–∏–≤ –¥–∞–Ω–Ω—É—é —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å.'),
        ('twin_shot', '–°–æ–≤–µ—Ä—à–∞—è –¥–∏—Å—Ç–∞–Ω—Ü–∏–æ–Ω–Ω—É—é –∞—Ç–∞–∫—É –∏–∑ –ª—É–∫–∞ –∏–ª–∏ –º–µ—Ç–∞—Ç–µ–ª—å–Ω—ã–º –æ—Ä—É–∂–∏–µ–º, –≤–æ–∏–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–î–≤–æ–π–Ω–æ–π –≤—ã—Å—Ç—Ä–µ–ª</strong> –≤–º–µ—Å—Ç–æ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–µ–≥–æ –æ—Ä—É–∂–∏—é –Ω–∞–≤—ã–∫–∞. –ü—Ä–∏ –ø–æ–ø–∞–¥–∞–Ω–∏–∏ –≤–æ–∏–Ω –≤—ã–ø—É—Å–∫–∞–µ—Ç –≤ —Ü–µ–ª—å –¥–≤–∞ —Å–Ω–∞—Ä—è–¥–∞, –ø–æ–≤—Ä–µ–∂–¥–∞—è –¥–≤–µ —Å–ª—É—á–∞–π–Ω—ã–µ —á–∞—Å—Ç–∏ —Ç–µ–ª–∞. –î–∞–∂–µ –µ—Å–ª–∏ –∞—Ç–∞–∫–∞ –±—ã–ª–∞ –ø—Ä–∏—Ü–µ–ª—å–Ω–æ–π, –≤—Ç–æ—Ä–æ–π —Å–Ω–∞—Ä—è–¥ –ø–æ–ø–∞–¥—ë—Ç –≤ —Å–ª—É—á–∞–π–Ω—É—é —á–∞—Å—Ç—å —Ç–µ–ª–∞.'),
        ('precise_aim', '–ï—Å–ª–∏ –≤–æ–∏–Ω —Å–æ–≤–µ—Ä—à–∞–µ—Ç –∫—Ä–∏—Ç–∏—á–µ—Å–∫—É—é –∞—Ç–∞–∫—É –¥–∏—Å—Ç–∞–Ω—Ü–∏–æ–Ω–Ω—ã–º –æ—Ä—É–∂–∏–µ–º, –æ–Ω –º–æ–∂–µ—Ç –Ω–µ–º–µ–¥–ª–µ–Ω–Ω–æ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–¢–æ—á–Ω–æ–≥–æ –ø—Ä–∏—Ü–µ–ª–∞</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –õ–≤–∫ —Ö 3 —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –≤–æ–∏–Ω –¥–æ–±–∞–≤–ª—è–µ—Ç –∑–Ω–∞—á–µ–Ω–∏–µ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–¢–æ—á–Ω—ã–π –ø—Ä–∏—Ü–µ–ª</strong> –∫ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–º—É –±—Ä–æ—Å–∫—É. –≠—Ç–∏ –æ—á–∫–∏ –≤–ª–∏—è—é—Ç —Ç–æ–ª—å–∫–æ –Ω–∞ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –ø–æ–ª–æ–∂–µ–Ω–∏—è –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–≥–æ —Ä–∞–Ω–µ–Ω–∏—è.'),
        ('bloodhound', '–ü—Ä–∏ –≤—ã—Å–ª–µ–∂–∏–≤–∞–Ω–∏–∏ —Ü–µ–ª–∏ –≤–æ–∏–Ω –¥–æ–±–∞–≤–ª—è–µ—Ç –∑–Ω–∞—á–µ–Ω–∏–µ <strong>–ò—â–µ–π–∫–∏</strong> –∫ –ø—Ä–æ–≤–µ—Ä–∫–∞–º –í—ã–∂–∏–≤–∞–Ω–∏—è –≤ –¥–∏–∫–æ–π –ø—Ä–∏—Ä–æ–¥–µ, —á—Ç–æ–±—ã –Ω–∞–π—Ç–∏ —Å–ª–µ–¥ –∏–ª–∏ –ø—Ä–æ–π—Ç–∏ –ø–æ –Ω–µ–º—É. –ï—Å–ª–∏ –≤–æ–∏–Ω —Ç–µ—Ä—è–µ—Ç —Å–ª–µ–¥ –≤–æ –≤—Ä–µ–º—è –≤—ã—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è —Å –ø–æ–º–æ—â—å—é —ç—Ç–æ–π —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ò—â–µ–π–∫–∏</strong> —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º, —á—Ç–æ–±—ã –Ω–µ–º–µ–¥–ª–µ–Ω–Ω–æ –≤–Ω–æ–≤—å –Ω–∞–π—Ç–∏ —Å–ª–µ–¥.'),
        ('warrior_trap', '–í–æ–∏–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–õ–æ–≤—É—à–∫–∞ –≤–æ–∏–Ω–∞</strong>, —á—Ç–æ–±—ã —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å —Å–∞–º–æ–¥–µ–ª—å–Ω—É—é –ª–æ–≤—É—à–∫—É –≤ –æ–ø—Ä–µ–¥–µ–ª—ë–Ω–Ω–æ–π –∑–æ–Ω–µ. –í–∏–¥ –ª–æ–≤—É—à–∫–∏ –æ–ø—Ä–µ–¥–µ–ª–∏—Ç–µ –ø–æ —Ç–∞–±–ª–∏—Ü–µ ¬´–õ–æ–≤—É—à–∫–∏ –≤–æ–∏–Ω–∞¬ª. –í–æ–∏–Ω –º–æ–∂–µ—Ç —Å–æ–∑–¥–∞—Ç—å –ª–æ–≤—É—à–∫—É —Ç–æ–ª—å–∫–æ –æ–¥–Ω–æ–≥–æ –≤–∏–¥–∞ –∑–∞ —Ä–∞–∑. –£ –∫–∞–∂–¥–æ–π –ª–æ–≤—É—à–∫–∏ –µ—Å—Ç—å —Ä–∞—Å—Ç—è–∂–∫–∞ —Ä–∞–¥–∏—É—Å–æ–º 2 –º–µ—Ç—Ä–∞, –¥–ª—è –µ—ë –æ–±–Ω–∞—Ä—É–∂–µ–Ω–∏—è —Ç—Ä–µ–±—É–µ—Ç—Å—è —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É –í–Ω–∏–º–∞–Ω–∏—è —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –ø—Ä–æ–≤–µ—Ä–∫–µ <strong>–õ–æ–≤—É—à–∫–∏ –≤–æ–∏–Ω–∞</strong>.'),
        ('tactical_advantage', '–í–º–µ—Å—Ç–æ –ø–µ—Ä–µ–º–µ—â–µ–Ω–∏—è –≤–æ–∏–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–¢–∞–∫—Ç–∏—á–µ—Å–∫–æ–≥–æ –ø—Ä–µ–∏–º—É—â–µ—Å—Ç–≤–∞</strong>, —á—Ç–æ–±—ã –æ—Ü–µ–Ω–∏—Ç—å –≥—Ä—É–ø–ø—É –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–æ–≤. –í–æ–∏–Ω –ø–æ–ª—É—á–∞–µ—Ç –±–æ–Ω—É—Å +3 –∫ –∞—Ç–∞–∫–µ –∏ –∑–∞—â–∏—Ç–µ –Ω–∞ –æ–¥–∏–Ω —Ä–∞—É–Ω–¥ –ø—Ä–æ—Ç–∏–≤ –≤—Å–µ—Ö –≤—Ä–∞–≥–æ–≤ –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö 10 –º–µ—Ç—Ä–æ–≤, —á—å—è –õ–≤–∫—Ö–ó –º–µ–Ω—å—à–µ, —á–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –ø—Ä–æ–≤–µ—Ä–∫–∏. –¢–∞–∫–∂–µ —ç—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –ø–æ–∑–≤–æ–ª—è–µ—Ç –ø–æ–Ω—è—Ç—å, —á—Ç–æ —Å–æ–±–∏—Ä–∞–µ—Ç—Å—è –¥–µ–ª–∞—Ç—å –∫–∞–∂–¥—ã–π –∏–∑ –≤—Ä–∞–≥–æ–≤, –Ω–∞ –∫–æ—Ç–æ—Ä—ã—Ö –æ–Ω–∞ –¥–µ–π—Å—Ç–≤—É–µ—Ç.'),
        ('fury', '–í–æ–∏–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ù–µ–∏—Å—Ç–æ–≤—Å—Ç–≤–∞</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –µ–≥–æ –≠–º–ø—Ö–ó. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –≤–æ–∏–Ω —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è –Ω–µ–≤–æ—Å–ø—Ä–∏–∏–º—á–∏–≤ –∫ —É–∂–∞—Å—É, –≤–ª–∏—è—é—â–∏–º –Ω–∞ —ç–º–æ—Ü–∏–∏ –∑–∞–∫–ª–∏–Ω–∞–Ω–∏—è–º –∏ –°–ª–æ–≤–µ—Å–Ω–æ–π –¥—É—ç–ª–∏ –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–∞—É–Ω–¥–æ–≤, —Ä–∞–≤–Ω–æ–µ —É–¥–≤–æ–µ–Ω–Ω–æ–º—É –∑–Ω–∞—á–µ–Ω–∏—é <strong>–ù–µ–∏—Å—Ç–æ–≤—Å—Ç–≤–∞</strong>. –í —ç—Ç–æ –≤—Ä–µ–º—è —è—Ä–æ—Å—Ç—å –∑–∞—Å—Ç–∏–ª–∞–µ—Ç —Ä–∞–∑—É–º –≤–æ–∏–Ω–∞ –∏ –æ–Ω –ø–æ–ª–Ω–æ—Å—Ç—å—é –æ—Ç–¥–∞—ë—Ç—Å—è –≤–æ –≤–ª–∞—Å—Ç—å –∏–Ω—Å—Ç–∏–Ω–∫—Ç–æ–≤.'),
        ('two_handed', '–ü–æ—Ç—Ä–∞—Ç–∏–≤ 10 –æ—á–∫–æ–≤ –í—ã–Ω –∏ —Å–æ–≤–µ—Ä—à–∏–≤ –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–î–≤—É—Ä—É—á–Ω–∏–∫</strong> —Å–æ —à—Ç—Ä–∞—Ñ–æ–º -3 –ø—Ä–æ—Ç–∏–≤ –∑–∞—â–∏—Ç—ã –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞, –≤–æ–∏–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –æ–¥–Ω—É –∞—Ç–∞–∫—É, –∫–æ—Ç–æ—Ä–∞—è –Ω–∞–Ω–æ—Å–∏—Ç –¥–≤–æ–π–Ω–æ–π —É—Ä–æ–Ω –∏ —Å—á–∏—Ç–∞–µ—Ç—Å—è –ø—Ä–æ–±–∏–≤–∞—é—â–µ–π –±—Ä–æ–Ω—é. –ï—Å–ª–∏ –µ–≥–æ –æ—Ä—É–∂–∏–µ —É–∂–µ –ø—Ä–æ–±–∏–≤–∞—é—â–µ–µ –±—Ä–æ–Ω—é, –æ–Ω–æ —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è —É–ª—É—á—à–µ–Ω–Ω—ã–º –ø—Ä–æ–±–∏–≤–∞—é—â–∏–º –±—Ä–æ–Ω—é. –£–ª—É—á—à–µ–Ω–Ω–æ–µ –ø—Ä–æ–±–∏–≤–∞—é—â–µ–µ –±—Ä–æ–Ω—é –æ—Ä—É–∂–∏–µ —Å —ç—Ç–æ–π —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å—é –Ω–∞–Ω–æ—Å–∏—Ç 3d6 –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ–≥–æ —É—Ä–æ–Ω–∞.'),
        ('ignore_pain', '–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–∞–∑ –∑–∞ –∏–≥—Ä–æ–≤—É—é –ø–∞—Ä—Ç–∏—é, —Ä–∞–≤–Ω–æ–µ –¢–µ–ª –≤–æ–∏–Ω–∞, –æ–Ω –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å 10 –æ—á–∫–æ–≤ –í—ã–Ω, —á—Ç–æ–±—ã –Ω–µ–º–µ–¥–ª–µ–Ω–Ω–æ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ò–≥–Ω–æ—Ä–∏—Ä–æ–≤–∞—Ç—å —É–¥–∞—Ä</strong>, –∫–æ–≥–¥–∞ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫ –Ω–∞–Ω–æ—Å–∏—Ç –µ–º—É –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–µ —Ä–∞–Ω–µ–Ω–∏–µ. –ï—Å–ª–∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç –ø—Ä–æ–≤–µ—Ä–∫–∏ –≤—ã—à–µ –ø—Ä–æ–≤–µ—Ä–∫–∏ –∞—Ç–∞–∫–∏ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞, –≤–æ–∏–Ω –æ—Ç–º–µ–Ω—è–µ—Ç –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–µ —Ä–∞–Ω–µ–Ω–∏–µ, –∫–∞–∫ –µ—Å–ª–∏ –±—ã –∞—Ç–∞–∫–∞ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–∞ –Ω–µ –±—ã–ª–∞ –∫—Ä–∏—Ç–∏—á–µ—Å–∫–æ–π.'),
        -- Priest skills
        ('divine_power', '–£–∫—Ä–µ–ø–ª—è—è —Å–≤—è–∑—å —Å –±–æ–∂–µ—Å—Ç–≤–æ–º, –∂—Ä–µ—Ü –º–æ–∂–µ—Ç –ø–æ–≤—ã—Å–∏—Ç—å —Å–≤–æ—ë –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –Ω–∞ 1 –∑–∞ –∫–∞–∂–¥—ã–π —É—Ä–æ–≤–µ–Ω—å <strong>–ë–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω–æ–π —Å–∏–ª—ã</strong>. –¢–∞–∫–∏–º –æ–±—Ä–∞–∑–æ–º, –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –∂—Ä–µ—Ü–∞ –Ω–∞ 10 —É—Ä–æ–≤–Ω–µ –±—É–¥–µ—Ç —Ä–∞–≤–Ω–æ 12. –≠—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å —Ä–∞–∑–≤–∏–≤–∞–µ—Ç—Å—è –∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ –ø—Ä–æ—á–∏–º –Ω–∞–≤—ã–∫–∞–º –∏ —Å—É–º–º–∏—Ä—É–µ—Ç—Å—è —Å <strong>–ï–¥–∏–Ω–µ–Ω–∏–µ–º —Å –ø—Ä–∏—Ä–æ–¥–æ–π</strong>. –ó–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –≤ —ç—Ç–æ–º —Å–ª—É—á–∞–µ –æ–±—â–µ–µ'),
        ('divine_authority', '–î–ª—è –∫—Ä–µ—Å—Ç—å—è–Ω –∏ –ø—Ä–æ—Å—Ç–æ–≥–æ –ª—é–¥–∞ –∂—Ä–µ—Ü ‚Äî –ø—Ä–æ–≤–æ–¥–Ω–∏–∫ –≤–æ–ª–∏ –±–æ–≥–æ–≤. –ñ—Ä–µ—Ü –º–æ–∂–µ—Ç –¥–æ–±–∞–≤–∏—Ç—å –∑–Ω–∞—á–µ–Ω–∏–µ <strong>–ë–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω–æ–≥–æ –∞–≤—Ç–æ—Ä–∏—Ç–µ—Ç–∞</strong> –∫ —Å–≤–æ–∏–º –ø—Ä–æ–≤–µ—Ä–∫–∞–º –õ–∏–¥–µ—Ä—Å—Ç–≤–∞, –µ—Å–ª–∏ –æ–Ω –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –≤ –æ–±–ª–∞—Å—Ç–∏, –≥–¥–µ –∏—Å–ø–æ–≤–µ–¥—É—é—Ç —Ç—É –∂–µ —Ä–µ–ª–∏–≥–∏—é. –ï—Å–ª–∏ –∂—Ä–µ—Ü –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –∑–∞ –ø—Ä–µ–¥–µ–ª–∞–º–∏ —Ç–∞–∫–æ–π –æ–±–ª–∞—Å—Ç–∏, —Ç–æ –æ–Ω –¥–æ–±–∞–≤–ª—è–µ—Ç –ø–æ–ª–æ–≤–∏–Ω—É –∑–Ω–∞—á–µ–Ω–∏—è —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏.'),
        ('foresight', '–ü–æ —Ä–µ—à–µ–Ω–∏—é –≤–µ–¥—É—â–µ–≥–æ –∂—Ä–µ—Ü –º–æ–∂–µ—Ç –ø–æ–ª—É—á–∏—Ç—å –≤–∏–¥–µ–Ω–∏–µ –±—É–¥—É—â–µ–≥–æ, –Ω–∞ 3 —Ä–∞—É–Ω–¥–∞ –≤–ø–∞–≤ –≤ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –∫–∞—Ç–∞—Ç–æ–Ω–∏–∏. –ü–æ—Å–ª–µ —ç—Ç–æ–≥–æ –∂—Ä–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ü—Ä–µ–¥–≤–∏–¥–µ–Ω–∏—è</strong> —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º, —á—Ç–æ–±—ã —Ä–∞—Å—à–∏—Ñ—Ä–æ–≤–∞—Ç—å –ø–æ–ª—É—á–µ–Ω–Ω—ã–µ –≤–∏–¥–µ–Ω–∏—è, –∫–æ—Ç–æ—Ä—ã–µ –ø—Ä–µ–¥—Å—Ç–∞–≤–ª—è—é—Ç —Å–æ–±–æ–π —Å–º–µ—Å—å —Å–∏–º–≤–æ–ª–æ–≤ –∏ –º–µ—Ç–∞—Ñ–æ—Ä.'),
        ('one_with_nature', '–£–∫—Ä–µ–ø–ª—è—è —Å–≤—è–∑—å —Å –ø—Ä–∏—Ä–æ–¥–æ–π, –∂—Ä–µ—Ü –º–æ–∂–µ—Ç –ø–æ–≤—ã—Å–∏—Ç—å —Å–≤–æ—ë –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –Ω–∞ 1 –∑–∞ –∫–∞–∂–¥—ã–π —É—Ä–æ–≤–µ–Ω—å <strong>–ï–¥–∏–Ω–µ–Ω–∏—è —Å –ø—Ä–∏—Ä–æ–¥–æ–π</strong>. –¢–∞–∫–∏–º –æ–±—Ä–∞–∑–æ–º, –∑–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –∂—Ä–µ—Ü–∞ –Ω–∞ 10 —É—Ä–æ–≤–Ω–µ –±—É–¥–µ—Ç —Ä–∞–≤–Ω–æ 12. –≠—Ç–∞ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å —Ä–∞–∑–≤–∏–≤–∞–µ—Ç—Å—è –∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ –ø—Ä–æ—á–∏–º –Ω–∞–≤—ã–∫–∞–º –∏ —Å—É–º–º–∏—Ä—É–µ—Ç—Å—è —Å <strong>–ë–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω–æ–π —Å–∏–ª–æ–π</strong>. –ó–Ω–∞—á–µ–Ω–∏–µ –≠–Ω–µ—Ä–≥–∏–∏ –≤ —ç—Ç–æ–º —Å–ª—É—á–∞–µ –æ–±—â–µ–µ.'),
        ('nature_s_signs', '–ù–∞—Ö–æ–¥—è—Å—å —Å—Ä–µ–¥–∏ –ø—Ä–∏—Ä–æ–¥—ã, –¥—Ä—É–∏–¥ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ó–Ω–∞–∫–∏ –ø—Ä–∏—Ä–æ–¥—ã</strong> —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –¥—Ä—É–∏–¥ –ø–æ –∑–Ω–∞–∫–∞–º —É–∑–Ω–∞—ë—Ç, –∫—Ç–æ –≤ —ç—Ç–æ–º –º–µ—Å—Ç–µ –±—ã–ª –∏ —á—Ç–æ –¥–µ–ª–∞–ª. –≠—Ç–∞ –ø—Ä–æ–≤–µ—Ä–∫–∞ –¥–∞—ë—Ç —Ç–æ–ª—å–∫–æ –ª–æ–∫–∞–ª—å–Ω—É—é –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –∏ –Ω–µ –ø–æ–∑–≤–æ–ª—è–µ—Ç –æ—Ç—Å–ª–µ–∂–∏–≤–∞—Ç—å.'),
        ('nature_s_ally', '–î—Ä—É–∏–¥ –¥–æ–±–∞–≤–ª—è–µ—Ç —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å <strong>–°–æ—é–∑–Ω–∏–∫ –ø—Ä–∏—Ä–æ–¥—ã</strong> –∫ –ª—é–±—ã–º –ø—Ä–æ–≤–µ—Ä–∫–∞–º –í—ã–∂–∏–≤–∞–Ω–∏—è –≤ –¥–∏–∫–æ–π –ø—Ä–∏—Ä–æ–¥–µ –¥–ª—è –æ–±—Ä–∞—â–µ–Ω–∏—è —Å –∂–∏–≤–æ—Ç–Ω—ã–º–∏. –î—Ä—É–∏–¥ —Ç–∞–∫–∂–µ –º–æ–∂–µ—Ç —Å–¥—Ä—É–∂–∏—Ç—å—Å—è —Å –∂–∏–≤–æ—Ç–Ω—ã–º, –ø–æ—Ç—Ä–∞—Ç–∏–≤ –ø–æ–ª–Ω—ã–π —Ä–∞—É–Ω–¥ –∏ —Å–æ–≤–µ—Ä—à–∏–≤ –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–°–æ—é–∑–Ω–∏–∫–∞ –ø—Ä–∏—Ä–æ–¥—ã</strong>. –ó–≤–µ—Ä—å –∏–ª–∏ –∏–Ω–æ–µ –∂–∏–≤–æ—Ç–Ω–æ–µ —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è —Å–æ—é–∑–Ω–∏–∫–æ–º –¥—Ä—É–∏–¥–∞ –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —á–∞—Å–æ–≤, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–°–æ—é–∑–Ω–∏–∫ –ø—Ä–∏—Ä–æ–¥—ã</strong>. –î–∞–Ω–Ω–∞—è —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –Ω–µ –¥–µ–π—Å—Ç–≤—É–µ—Ç –Ω–∞ —á—É–¥–æ–≤–∏—â.'),
        ('bloody_rituals', '–ü—Ä–æ–≤–æ–¥—è —Ä–∏—Ç—É–∞–ª, –∂—Ä–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ö—Ä–æ–≤–∞–≤—ã–µ —Ä–∏—Ç—É–∞–ª—ã</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ —Ä–∏—Ç—É–∞–ª–∞. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –∂—Ä–µ—Ü –ø—Ä–æ–≤–æ–¥–∏—Ç —Ä–∏—Ç—É–∞–ª –±–µ–∑ –Ω–µ–æ–±—Ö–æ–¥–∏–º—ã—Ö –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–∏—Ö —Å—É–±—Å—Ç–∞–Ω—Ü–∏–π, –∂–µ—Ä—Ç–≤—É—è –ø—Ä–∏ —ç—Ç–æ–º 5 –ü–ó –≤ –≤–∏–¥–µ –∫—Ä–æ–≤–∏ –∑–∞ –∫–∞–∂–¥—É—é –Ω–µ–¥–æ—Å—Ç–∞—é—â—É—é —Å—É–±—Å—Ç–∞–Ω—Ü–∏—é. –≠—Ç–æ –º–æ–∂–µ—Ç –±—ã—Ç—å –∏ —á—É–∂–∞—è –∫—Ä–æ–≤—å, –Ω–æ —Ç–æ–ª—å–∫–æ –ø—Ä–æ–ª–∏—Ç–∞—è –≤–æ –≤—Ä–µ–º—è –¥–∞–Ω–Ω–æ–≥–æ —Ä–∏—Ç—É–∞–ª–∞.'),
        ('zeal', '–ñ—Ä–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–†–≤–µ–Ω–∏—è</strong> –ø—Ä–æ—Ç–∏–≤ —Ç–µ–∫—É—â–µ–≥–æ –∑–Ω–∞—á–µ–Ω–∏—è –ò–Ω—Ç—Ö–ó —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Å–ª–æ–≤–∞ –∂—Ä–µ—Ü–∞ –æ–±–æ–¥—Ä—è—é—Ç —Ü–µ–ª—å, —á—Ç–æ –¥–∞—ë—Ç –µ–π 1d6 –≤—Ä–µ–º–µ–Ω–Ω—ã—Ö –ü–ó –∑–∞ –∫–∞–∂–¥—ã–π –ø—É–Ω–∫—Ç —Å–≤–µ—Ä—Ö –°–õ (–º–∞–∫—Å–∏–º—É–º 5). –≠—Ç–æ—Ç —ç—Ñ—Ñ–µ–∫—Ç –¥–ª–∏—Ç—Å—è –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–∞—É–Ω–¥–æ–≤, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–†–≤–µ–Ω–∏—è</strong> —Ö 2, –∏ –Ω–∞ –æ–¥–Ω—É —Ü–µ–ª—å –µ–≥–æ –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Ç–æ–ª—å–∫–æ —Ä–∞–∑ –≤ –¥–µ–Ω—å.'),
        ('holy_fire', '–ñ—Ä–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–°–ª–æ–≤–æ –±–æ–∂—å–µ</strong>, —á—Ç–æ–±—ã —É–±–µ–¥–∏—Ç—å —Å–ª—É—à–∞—Ç–µ–ª–µ–π, —á—Ç–æ –µ–≥–æ —É—Å—Ç–∞–º–∏ –≥–æ–≤–æ—Ä–∏—Ç –±–æ–∂–µ—Å—Ç–≤–æ. –õ—é–±–æ–π, –∫—Ç–æ –ø—Ä–æ–≤–∞–ª–∏—Ç –ø—Ä–æ–≤–µ—Ä–∫—É –°–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—è —É–±–µ–∂–¥–µ–Ω–∏—é, –±—É–¥–µ—Ç —Å—á–∏—Ç–∞—Ç—å –∂—Ä–µ—Ü–∞ –º–µ—Å—Å–∏–µ–π –∏ —Å–ª–µ–¥–æ–≤–∞—Ç—å –∑–∞ –Ω–∏–º. –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª–µ–π –∂—Ä–µ—Ü–∞ —Ä–∞–≤–Ω–æ –∑–Ω–∞—á–µ–Ω–∏—é –µ–≥–æ <strong>–°–ª–æ–≤–∞ –±–æ–∂—å–µ–≥–æ</strong>. –ï—Å–ª–∏ —É –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª–µ–π –Ω–µ—Ç –±–ª–æ–∫–æ–≤ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤, –∏—Å–ø–æ–ª—å–∑—É–π—Ç–µ –¥–ª—è –Ω–∏—Ö –ø–∞—Ä–∞–º–µ—Ç—Ä—ã —Ä–∞–∑–±–æ–π–Ω–∏–∫–æ–≤.<br><br>–ö–æ–≥–¥–∞ –≤–∞—à –ø–µ—Ä—Å–æ–Ω–∞–∂ –æ—Ç–¥–∞—ë—Ç —Å–≤–æ–∏–º –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª—è–º –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–æ —Å—Ç—Ä–∞–Ω–Ω—ã–π –∏–ª–∏ –Ω–µ–µ—Å—Ç–µ—Å—Ç–≤–µ–Ω–Ω—ã–π –¥–ª—è –Ω–µ–≥–æ –ø—Ä–∏–∫–∞–∑, —Å–æ–≤–µ—Ä—à–∏—Ç–µ –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–°–ª–æ–≤–∞ –±–æ–∂—å–µ–≥–æ</strong> —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º. –í—ã –º–æ–∂–µ—Ç–µ –ø—Ä–æ–≤–∞–ª–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É 3 —Ä–∞–∑–∞, –ø–æ—Å–ª–µ —á–µ–≥–æ –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª–∏ –ø–æ–∫–∏–Ω—É—Ç –≤–∞—à–µ–≥–æ –ø–µ—Ä—Å–æ–Ω–∞–∂–∞. –ï—Å–ª–∏ –ø—Ä–∏ –ø–æ—Å–ª–µ–¥–Ω–µ–π –ø—Ä–æ–≤–µ—Ä–∫–µ –≤—ã–ø–∞–¥–∞–µ—Ç 1, —Ç–æ –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª–∏ –Ω–∞–ø–∞–¥—É—Ç –Ω–∞ –≤–∞—Å –∏–ª–∏ –æ–±—ä—è–≤—è—Ç –µ—Ä–µ—Ç–∏–∫–æ–º.'),
        -- Criminal skills
        ('case_joint', '–ü—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å —á–∞—Å, —á—Ç–æ–±—ã –ø–æ–±—Ä–æ–¥–∏—Ç—å –ø–æ —É–ª–∏—Ü–∞–º –ø–æ—Å–µ–ª–µ–Ω–∏—è –∏ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü—Ä–∏—Å–º–æ—Ç—Ä–µ—Ç—å—Å—è</strong> —Å–æ –°–õ, —É–∫–∞–∑–∞–Ω–Ω–æ–π –≤ —Ç–∞–±–ª–∏—Ü–µ –Ω–∞ –ø–æ–ª—è—Ö. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –∑–∞–ø–æ–º–∏–Ω–∞–µ—Ç –º–∞—Ä—à—Ä—É—Ç—ã –ø–∞—Ç—Ä—É–ª–µ–π, —Ä–∞—Å–ø–æ–ª–æ–∂–µ–Ω–∏–µ —É–ª–∏—Ü –∏ —É–∫—Ä—ã—Ç–∏–π, —á—Ç–æ –¥–∞—ë—Ç –µ–º—É –±–æ–Ω—É—Å +2 –∫ –°–∫—Ä—ã—Ç–Ω–æ—Å—Ç–∏ –≤ —ç—Ç–æ–º —Ä–∞–π–æ–Ω–µ –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–ü—Ä–∏—Å–º–æ—Ç—Ä–µ—Ç—å—Å—è</strong>.'),
        ('repeat_lockpick', '–ö–æ–≥–¥–∞ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ —É—Å–ø–µ—à–Ω–æ –≤—Å–∫—Ä—ã–≤–∞–µ—Ç –∑–∞–º–æ–∫, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ü–æ–≤—Ç–æ—Ä–Ω–æ–≥–æ –≤–∑–ª–æ–º–∞</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –í–∑–ª–æ–º–∞ –∑–∞–º–∫–æ–≤ (–¥–ª—è –¥–∞–Ω–Ω–æ–≥–æ –∑–∞–º–∫–∞), —á—Ç–æ–±—ã –∑–∞–ø–æ–º–Ω–∏—Ç—å –ø–æ–ª–æ–∂–µ–Ω–∏–µ —à—Ç–∏—Ñ—Ç–æ–≤. –≠—Ç–æ –ø–æ–∑–≤–æ–ª–∏—Ç –µ–º—É –æ—Ç–∫—Ä—ã—Ç—å —Ç–æ—Ç –∂–µ –∑–∞–º–æ–∫ –±–µ–∑ –ø—Ä–æ–≤–µ—Ä–∫–∏ –Ω–∞–≤—ã–∫–∞ –í–∑–ª–æ–º–∞ –∑–∞–º–∫–æ–≤. –ü—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç –∑–∞–ø–æ–º–Ω–∏—Ç—å —Å—Ç–æ–ª—å–∫–æ –∑–∞–º–∫–æ–≤, —Å–∫–æ–ª—å–∫–æ —É –Ω–µ–≥–æ –æ—á–∫–æ–≤ –ò–Ω—Ç. –í—Å–µ–≥–¥–∞ –º–æ–∂–Ω–æ –∑–∞–ø–æ–º–Ω–∏—Ç—å –Ω–æ–≤—ã–π –∑–∞–º–æ–∫, –∑–∞–±—ã–≤ —Å—Ç–∞—Ä—ã–π.'),
        ('lay_low', '–û–¥–∏–Ω —Ä–∞–∑ –∑–∞ –∏–≥—Ä–æ–≤—É—é –ø–∞—Ä—Ç–∏—é –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ó–∞–ª–µ—á—å –Ω–∞ –¥–Ω–æ</strong>, —á—Ç–æ–±—ã –Ω–∞–π—Ç–∏ —Ç–∞–π–Ω–æ–µ —É–±–µ–∂–∏—â–µ, –≥–¥–µ –æ–Ω –º–æ–∂–µ—Ç —Å–ø—Ä—è—Ç–∞—Ç—å—Å—è –Ω–∞ –∫–∞–∫–æ–µ-—Ç–æ –≤—Ä–µ–º—è. –†–µ–∑—É–ª—å—Ç–∞—Ç –ø—Ä–æ–≤–µ—Ä–∫–∏ <strong>–ó–∞–ª–µ—á—å –Ω–∞ –¥–Ω–æ</strong> —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–∏—Ç–µ –º–µ–∂–¥—É —Ç—Ä–µ–º—è –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º–∏ –ø–æ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–µ–π —Ç–∞–±–ª–∏—Ü–µ –Ω–∞ –ø–æ–ª—è—Ö. –¢–∞–π–Ω–æ–µ —É–±–µ–∂–∏—â–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç, –ø–æ–∫–∞ –µ–≥–æ –Ω–µ —É–Ω–∏—á—Ç–æ–∂–∞—Ç, –∏ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –≤—Å–µ–≥–¥–∞ –º–æ–∂–µ—Ç –≤ –Ω–µ–≥–æ –≤–µ—Ä–Ω—É—Ç—å—Å—è.'),
        ('vulnerability', '–ü—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –≤—Å—Ç—Ä–µ—á–Ω—É—é –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–£—è–∑–≤–∏–º–æ—Å—Ç–∏</strong> –ø—Ä–æ—Ç–∏–≤ –Ω–∞–≤—ã–∫–∞ –û–±–º–∞–Ω–∞ —Ä–∞–∑—É–º–Ω–æ–π —Ü–µ–ª–∏, —á—Ç–æ–±—ã –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å —Å–∞–º—É—é –¥–æ—Ä–æ–≥—É—é –¥–ª—è —Ü–µ–ª–∏ –≤–µ—â—å –∏–ª–∏ –ª–∏—á–Ω–æ—Å—Ç—å. –≠—Ç–æ —Ç–∞–∫–∂–µ –¥–∞—ë—Ç –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫—É –±–æ–Ω—É—Å +1 –∫ –ó–∞–ø—É–≥–∏–≤–∞–Ω–∏—é –∑–∞ –∫–∞–∂–¥—ã–µ 2 –ø—É–Ω–∫—Ç–∞ —Å–≤—ã—à–µ –û–±–º–∞–Ω–∞ —Ü–µ–ª–∏. –≠—Ç–æ—Ç –±–æ–Ω—É—Å –¥–µ–π—Å—Ç–≤—É–µ—Ç –¥–æ —Ç–µ—Ö –ø–æ—Ä, –ø–æ–∫–∞ —É—è–∑–≤–∏–º–æ—Å—Ç—å —Ü–µ–ª–∏ –Ω–µ –∏–∑–º–µ–Ω–∏—Ç—Å—è.'),
        ('take_note', '–ü—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–í–∑—è—Ç—å –Ω–∞ –∑–∞–º–µ—Ç–∫—É</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –≠–º–ø —Ö 3 —Ü–µ–ª–∏, —á—Ç–æ–±—ã –æ—Å—Ç–∞–≤–∏—Ç—å –º–µ—Ç–∫—É –Ω–∞ –µ—ë –¥–≤–µ—Ä–∏ –∏–ª–∏ —á—Ç–æ-—Ç–æ –ø–æ–¥–æ–±–Ω–æ–µ. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ü–µ–ª—å –¥–æ–ª–∂–Ω–∞ –ø—Ä–æ—Ö–æ–¥–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É –•–∞—Ä–∏–∑–º—ã, –£–±–µ–∂–¥–µ–Ω–∏—è –∏–ª–∏ –ó–∞–ø—É–≥–∏–≤–∞–Ω–∏—è, —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∫–æ—Ç–æ—Ä–æ–π –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –≤—ã—à–µ –ø—Ä–æ–≤–µ—Ä–∫–∏ <strong>–í–∑—è—Ç—å –Ω–∞ –∑–∞–º–µ—Ç–∫—É</strong> –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫–∞, —á—Ç–æ–±—ã –ø–æ–ª—É—á–∏—Ç—å –ø–æ–º–æ—â—å –∏–ª–∏ —É—Å–ª—É–≥—É —É –∫–æ–≥–æ-–ª–∏–±–æ –≤ —Å–≤–æ—ë–º –ø–æ—Å–µ–ª–µ–Ω–∏–∏.'),
        ('intimidating_presence', '–û–¥–∏–Ω —Ä–∞–∑ –≤ –¥–µ–Ω—å, –ø–æ—Ç—Ä–∞—Ç–∏–≤ —á–∞—Å, –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–°–±–æ—Ä–∞</strong> —Å —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω–æ–π –≤–µ–¥—É—â–∏–º –°–õ. –ó–∞ –∫–∞–∂–¥—ã–µ 2 –ø—É–Ω–∫—Ç–∞ —Å–≤—ã—à–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω–æ–π –°–õ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç –∑–∞–≤–µ—Ä–±–æ–≤–∞—Ç—å 1 —Ä–∞–∑–±–æ–π–Ω–∏–∫–∞ –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–°–±–æ—Ä–∞</strong>. –ï—Å–ª–∏ —É —Ä–∞–∑–±–æ–π–Ω–∏–∫–∞ –º–µ–Ω—å—à–µ –ø–æ–ª–æ–≤–∏–Ω—ã –ü–ó, –æ–Ω –¥–æ–ª–∂–µ–Ω —Å–æ–≤–µ—Ä—à–∏—Ç—å –±—Ä–æ—Å–æ–∫ –¥–µ—Å—è—Ç–∏–≥—Ä–∞–Ω–Ω–æ–π –∫–æ—Å—Ç–∏, —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∫–æ—Ç–æ—Ä–æ–≥–æ –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–∏–∂–µ –∑–Ω–∞—á–µ–Ω–∏—è –í–æ–ª–∏ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫–∞; –≤ –ø—Ä–æ—Ç–∏–≤–Ω–æ–º —Å–ª—É—á–∞–µ —Ä–∞–∑–±–æ–π–Ω–∏–∫ —É–±–µ–≥–∞–µ—Ç.'),
        ('smuggler', '–ü—Ä–µ—Å—Ç—É–ø–Ω–∏–∫, –Ω–µ —É—á–∞—Å—Ç–≤—É—é—â–∏–π –≤ –±–æ—é, –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å —Ä–∞—É–Ω–¥, —á—Ç–æ–±—ã –ø—Ä–∏—Ü–µ–ª–∏—Ç—å—Å—è, –∏ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ü—Ä–∏—Ü–µ–ª–∏–≤–∞–Ω–∏—è</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –†–µ–∞ —Ö 3 —Ü–µ–ª–∏, —á—Ç–æ–±—ã –ø–æ–ª—É—á–∏—Ç—å –±–æ–Ω—É—Å –∫ —Å–ª–µ–¥—É—é—â–µ–π –∞—Ç–∞–∫–µ, —Ä–∞–≤–Ω—ã–π –ø–æ–ª–æ–≤–∏–Ω–µ –∑–Ω–∞—á–µ–Ω–∏—è <strong>–ü—Ä–∏—Ü–µ–ª–∏–≤–∞–Ω–∏—è</strong>. –ï—Å–ª–∏ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫–∞ –∑–∞–º–µ—Ç—è—Ç –ø–æ—Å–ª–µ –±—Ä–æ—Å–∫–∞, –Ω–æ –¥–æ –∞—Ç–∞–∫–∏, –±–æ–Ω—É—Å —Å–Ω–∏–∂–∞–µ—Ç—Å—è –≤ –¥–≤–∞ —Ä–∞–∑–∞.'),
        ('false_identity', '–í–º–µ—Å—Ç–æ –∞—Ç–∞–∫–∏ –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü—Ä—è–º–æ –≤ –≥–ª–∞–∑</strong>, —á—Ç–æ–±—ã –≤—Ä–µ–º–µ–Ω–Ω–æ –æ—Å–ª–µ–ø–∏—Ç—å —Ü–µ–ª—å. –î–ª—è —ç—Ç–æ–≥–æ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ, —á—Ç–æ–±—ã –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –Ω–∞—Ö–æ–¥–∏–ª—Å—è –Ω–∞ –¥–∏—Å—Ç–∞–Ω—Ü–∏–∏ –±–ª–∏–∂–Ω–µ–≥–æ –±–æ—è; –∫ —É–¥–∞—Ä—É –ø—Ä–∏ —ç—Ç–æ–º –ø—Ä–∏–º–µ–Ω—è–µ—Ç—Å—è —à—Ç—Ä–∞—Ñ -3. –ü—Ä–∏ –ø–æ–ø–∞–¥–∞–Ω–∏–∏ —Ü–µ–ª—å –ø–æ–ª—É—á–∞–µ—Ç 2d6 —É—Ä–æ–Ω–∞ –±–µ–∑ –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä–æ–≤ –∏ –æ—Å–ª–µ–ø–ª—è–µ—Ç—Å—è –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–∞—É–Ω–¥–æ–≤, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–ü—Ä—è–º–æ –≤ –≥–ª–∞–∑</strong>.'),
        ('black_market', '–£—Å—Ç—Ä–∞–∏–≤–∞—è –∑–∞—Å–∞–¥—É, –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –≤—Å—Ç—Ä–µ—á–Ω—É—é –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–£–¥–∞—Ä –∞—Å—Å–∞—Å–∏–Ω–∞</strong> –ø—Ä–æ—Ç–∏–≤ –í–Ω–∏–º–∞–Ω–∏—è —Ü–µ–ª–∏, —á—Ç–æ–±—ã —Å–∫—Ä—ã—Ç—å—Å—è –ø–æ—Å–ª–µ –∞—Ç–∞–∫–∏. –≠—Ç—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –≤ –ª—é–±–æ–π —Å–∏—Ç—É–∞—Ü–∏–∏, –Ω–æ –∫ –Ω–µ–π –ø—Ä–∏–º–µ–Ω—è—é—Ç—Å—è —à—Ç—Ä–∞—Ñ—ã –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –æ—Å–≤–µ—â—ë–Ω–Ω–æ—Å—Ç–∏ –∏ –¥—Ä—É–≥–∏—Ö —É—Å–ª–æ–≤–∏–π. –ï—Å–ª–∏ –ø—Ä–æ—Ç–∏–≤–Ω–∏–∫–æ–≤ –Ω–µ—Å–∫–æ–ª—å–∫–æ, –∫–∞–∂–¥—ã–π –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø–æ –±—Ä–æ—Å–∫—É, —á—Ç–æ–±—ã –ø–æ–ø—ã—Ç–∞—Ç—å—Å—è –∑–∞–º–µ—Ç–∏—Ç—å –ø—Ä–µ—Å—Ç—É–ø–Ω–∏–∫–∞.'),
        -- Craftsman skills
        ('large_catalog', '–£–º–µ–ª—ã–π —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ —Å–ø–æ—Å–æ–±–µ–Ω –∑–∞–ø–æ–º–Ω–∏—Ç—å –æ–≥—Ä–æ–º–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —á–µ—Ä—Ç–µ–∂–µ–π –Ω–∞ –≤—Å–µ —Å–ª—É—á–∞–∏ –∂–∏–∑–Ω–∏. –ö–æ–≥–¥–∞ —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ —É–∂–µ –∑–∞–ø–æ–º–Ω–∏–ª –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–µ –¥–æ—Å—Ç—É–ø–Ω–æ–µ –µ–º—É –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —á–µ—Ä—Ç–µ–∂–µ–π, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ë–æ–ª—å—à–æ–π –∫–∞—Ç–∞–ª–æ–≥</strong> —Å–æ –°–õ 15, —á—Ç–æ–±—ã –∑–∞–ø–æ–º–Ω–∏—Ç—å –µ—â—ë –æ–¥–∏–Ω. –ù–µ—Ç –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏—è –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–ø–æ–º–Ω–µ–Ω–Ω—ã—Ö —á–µ—Ä—Ç–µ–∂–µ–π, –Ω–æ –∑–∞ –∫–∞–∂–¥—ã–µ 10 –∑–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π –°–õ –ø—Ä–æ–≤–µ—Ä–∫–∏ –ø–æ–≤—ã—à–∞–µ—Ç—Å—è –Ω–∞ 1.'),
        ('apprentice', '–ö–æ–≥–¥–∞ —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –Ω–∞—á–∏–Ω–∞–µ—Ç –∏–∑–≥–æ—Ç–∞–≤–ª–∏–≤–∞—Ç—å –∫–∞–∫–æ–π-–ª–∏–±–æ –ø—Ä–µ–¥–º–µ—Ç, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü–æ–¥–º–∞—Å—Ç–µ—Ä—å–µ</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω–æ–≥–æ –ø—Ä–µ–¥–º–µ—Ç–∞. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –æ–Ω –ø—Ä–∏–±–∞–≤–ª—è–µ—Ç 1 –∫ —É—Ä–æ–Ω—É –∏–ª–∏ –∫ –ø—Ä–æ—á–Ω–æ—Å—Ç–∏ –∑–∞ –∫–∞–∂–¥—ã–µ 2 –ø—É–Ω–∫—Ç–∞ —Å–≤–µ—Ä—Ö —É–∫–∞–∑–∞–Ω–Ω–æ–π –°–õ. –ú–∞–∫—Å–∏–º–∞–ª—å–Ω—ã–π –±–æ–Ω—É—Å –∫ —É—Ä–æ–Ω—É –∏–ª–∏ –ø—Ä–æ—á–Ω–æ—Å—Ç–∏ —Ä–∞–≤–µ–Ω 5. –†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –Ω–µ –º–æ–∂–µ—Ç –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –£–¥–∞—á—É –¥–ª—è —É–≤–µ–ª–∏—á–µ–Ω–∏—è —ç—Ç–æ–≥–æ –±–æ–Ω—É—Å–∞.'),
        ('masterwork', '<strong>–ú–∞—Å—Ç–µ—Ä—Å–∫–∞—è —Ä–∞–±–æ—Ç–∞</strong> –ø–æ–∑–≤–æ–ª—è–µ—Ç —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫—É –∏–∑–≥–æ—Ç–∞–≤–ª–∏–≤–∞—Ç—å –ø—Ä–µ–¥–º–µ—Ç—ã —É—Ä–æ–≤–Ω—è –º–∞—Å—Ç–µ—Ä–∞. –†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –º–æ–∂–µ—Ç —Ç–∞–∫–∂–µ –≤ –ª—é–±–æ–π –º–æ–º–µ–Ω—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ú–∞—Å—Ç–µ—Ä—Å–∫–∞—è —Ä–∞–±–æ—Ç–∞</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –ø—Ä–µ–¥–º–µ—Ç–∞, —á—Ç–æ–±—ã –Ω–∞–≤—Å–µ–≥–¥–∞ –ø—Ä–∏–¥–∞—Ç—å –±—Ä–æ–Ω–µ —Å–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏–µ (–æ–Ω —Å–∞–º —Ä–µ—à–∞–µ—Ç —á–µ–º—É –∏–º–µ–Ω–Ω–æ) –∏–ª–∏ –±–æ–Ω—É—Å –æ—Ä—É–∂–∏—é: –¥—Ä–æ–±—è—â–µ–µ –æ—Ä—É–∂–∏–µ –ø–æ–ª—É—á–∞–µ—Ç —Å–≤–æ–π—Å—Ç–≤–æ –¥–µ–∑–æ—Ä–∏–µ–Ω—Ç–∏—Ä—É—é—â–µ–µ (-2), –∫–æ–ª—é—â–µ–µ –∏–ª–∏ —Ä–µ–∂—É—â–µ–µ ‚Äî –∫—Ä–æ–≤–æ–ø—É—Å–∫–∞—é—â–µ–µ (50%).'),
        ('alchemical_concoction', '–£–º–µ–ª—ã–π —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ —Å–ø–æ—Å–æ–±–µ–Ω –∑–∞–ø–æ–º–Ω–∏—Ç—å –æ–≥—Ä–æ–º–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ñ–æ—Ä–º—É–ª –Ω–∞ –≤—Å–µ —Å–ª—É—á–∞–∏ –∂–∏–∑–Ω–∏. –ö–æ–≥–¥–∞ —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ —É–∂–µ –∑–∞–ø–æ–º–Ω–∏–ª –¥–æ—Å—Ç—É–ø–Ω–æ–µ –µ–º—É —á–∏—Å–ª–æ —Ñ–æ—Ä–º—É–ª, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–°–ø–∏—Å–æ–∫ –ª–µ–∫–∞—Ä—Å—Ç–≤</strong> —Å–æ –°–õ 15, —á—Ç–æ–±—ã –∑–∞–ø–æ–º–Ω–∏—Ç—å –µ—â—ë –æ–¥–Ω—É. –ù–µ—Ç –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏—è –Ω–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–ø–æ–º–Ω–µ–Ω–Ω—ã—Ö —Ñ–æ—Ä–º—É–ª, –Ω–æ –∑–∞ –∫–∞–∂–¥—ã–µ 10 –∑–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π –°–õ –ø—Ä–æ–≤–µ—Ä–∫–∏ –ø–æ–≤—ã—à–∞–µ—Ç—Å—è –Ω–∞ 1.'),
        ('enhanced_potion', '–ö–æ–≥–¥–∞ —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ —Å–æ–±–∏—Ä–∞–µ—Ç—Å—è –∏–∑–≥–æ—Ç–æ–≤–∏—Ç—å –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–∏–π —Å–æ—Å—Ç–∞–≤, –æ–Ω –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–î–≤–æ–π–Ω–æ–π –ø–æ—Ä—Ü–∏–∏</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω–æ–π —Ñ–æ—Ä–º—É–ª—ã. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –æ–Ω —Å–æ–∑–¥–∞—ë—Ç –¥–≤–µ –ø–æ—Ä—Ü–∏–∏ —Å–æ—Å—Ç–∞–≤–∞ –∏–∑ –∏–Ω–≥—Ä–µ–¥–∏–µ–Ω—Ç–æ–≤, —Ä–∞—Å—Å—á–∏—Ç–∞–Ω–Ω—ã—Ö –Ω–∞ –æ–¥–Ω—É –ø–æ—Ä—Ü–∏—é. –≠—Ç–æ –ø—Ä–∏–º–µ–Ω–∏–º–æ –∫–æ –≤—Å–µ–º –∞–ª—Ö–∏–º–∏—á–µ—Å–∫–∏–º –ø—Ä–µ–¥–º–µ—Ç–∞–º, –≤–∫–ª—é—á–∞—è —ç–ª–∏–∫—Å–∏—Ä—ã, –º–∞—Å–ª–∞, –æ—Ç–≤–∞—Ä—ã –∏ –±–æ–º–±—ã.'),
        ('experimental_formula', '–ü–µ—Ä–µ–¥ —Å–æ–∑–¥–∞–Ω–∏–µ–º –≤–µ–¥—å–º–∞—á—å–µ–≥–æ —ç–ª–∏–∫—Å–∏—Ä–∞ —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–ê–¥–∞–ø—Ç–∞—Ü–∏–∏</strong> (3 + –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è), —á—Ç–æ–±—ã —É–º–µ–Ω—å—à–∏—Ç—å –°–õ –∏–∑–±–µ–≥–∞–Ω–∏—è –æ—Ç—Ä–∞–≤–ª–µ–Ω–∏—è –Ω–∞ 1 –∑–∞ –∫–∞–∂–¥—ã–π –ø—É–Ω–∫—Ç —Å–≤—ã—à–µ –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è. –ü—Ä–∏ –ø—Ä–æ–≤–∞–ª–µ —è–¥–æ–≤–∏—Ç–æ—Å—Ç—å —ç–ª–∏–∫—Å–∏—Ä–∞ –Ω–µ –º–µ–Ω—è–µ—Ç—Å—è. –°–õ –∏–∑–±–µ–≥–∞–Ω–∏—è –æ—Ç—Ä–∞–≤–ª–µ–Ω–∏—è –Ω–µ –º–æ–∂–µ—Ç –æ–ø—É—Å–∫–∞—Ç—å—Å—è –Ω–∏–∂–µ 12.'),
        ('workshop', '–†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–£–ª—É—á—à–µ–Ω–∏—è</strong> —Å–æ –°–õ, —É–∫–∞–∑–∞–Ω–Ω–æ–π –≤ —Ç–∞–±–ª–∏—Ü–µ –Ω–∞ –ø–æ–ª—è—Ö, —á—Ç–æ–±—ã –ø—Ä–∏–¥–∞—Ç—å –æ—Ä—É–∂–∏—é –∏–ª–∏ –±—Ä–æ–Ω–µ –æ—Å–æ–±—ã–µ —Å–≤–æ–π—Å—Ç–≤–∞ (–ø—Ä–∏ –Ω–∞–ª–∏—á–∏–∏ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤ —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫–∞). –ù–∞ —É–ª—É—á—à–µ–Ω–∏–µ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å 3 —Ä–∞—É–Ω–¥–∞. –î–ª—è —É–ª—É—á—à–µ–Ω–∏—è –Ω–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –∫—É–∑–Ω–∏—Ü—É, –Ω–æ –æ–Ω–∞ –¥–∞—ë—Ç –±–æ–Ω—É—Å +2 –∫ –ø—Ä–æ–≤–µ—Ä–∫–µ. –ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–æ–≤–∞–ª –Ω–∞–Ω–æ—Å–∏—Ç –ø—Ä–µ–¥–º–µ—Ç—É —É—Ä–æ–Ω, —Ä–∞–≤–Ω—ã–π –∑–Ω–∞—á–µ–Ω–∏—é –ø—Ä–æ–≤–∞–ª–∞.'),
        ('repair', '–†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –º–æ–∂–µ—Ç –ø–æ—Å–µ—Ä–µ–±—Ä–∏—Ç—å –∏–º–µ—é—â–µ–µ—Å—è –æ—Ä—É–∂–∏–µ –≤ –∫—É–∑–Ω–∏—Ü–µ, —Å–æ–≤–µ—Ä—à–∏–≤ –ø—Ä–æ–≤–µ—Ä–∫—É —Å–æ –°–õ 16. –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –Ω–µ–æ–±—Ö–æ–¥–∏–º—ã—Ö –¥–ª—è —ç—Ç–æ–≥–æ —Å–µ—Ä–µ–±—Ä—è–Ω—ã—Ö —Å–ª–∏—Ç–∫–æ–≤ –∑–∞–≤–∏—Å–∏—Ç –æ—Ç —Ä–∞–∑–º–µ—Ä–∞ –æ—Ä—É–∂–∏—è. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –æ—Ä—É–∂–∏–µ –Ω–∞–Ω–æ—Å–∏—Ç +1d6 —É—Ä–æ–Ω–∞ —Å–µ—Ä–µ–±—Ä–æ–º –∑–∞ –∫–∞–∂–¥—ã–µ 3 –ø—É–Ω–∫—Ç–∞ —Å–≤—ã—à–µ —Å–ª–æ–∂–Ω–æ—Å—Ç–∏, –Ω–æ –Ω–µ –±–æ–ª–µ–µ 5d6. –ü—Ä–∏ –ø—Ä–æ–≤–∞–ª–µ –æ—Ä—É–∂–∏–µ –ª–æ–º–∞–µ—Ç—Å—è.<br><br>–î–ª—è —Å–µ—Ä–µ–±—Ä–µ–Ω–∏—è –æ–¥–Ω–æ—Ä—É—á–Ω–æ–≥–æ –æ—Ä—É–∂–∏—è —Ç—Ä–µ–±—É–µ—Ç—Å—è 2 —Å–ª–∏—Ç–∫–∞ —Å–µ—Ä–µ–±—Ä–∞, –¥–≤—É—Ä—É—á–Ω–æ–≥–æ ‚Äî 4 —Å–ª–∏—Ç–∫–∞ —Å–µ—Ä–µ–±—Ä–∞, –∏ 1 —Å–ª–∏—Ç–æ–∫ —É–π–¥—ë—Ç –Ω–∞ —Å–µ—Ä–µ–±—Ä–µ–Ω–∏–µ 10 –∏–ª–∏ –º–µ–Ω–µ–µ —Å—Ç—Ä–µ–ª –∏–ª–∏ –∞—Ä–±–∞–ª–µ—Ç–Ω—ã—Ö –±–æ–ª—Ç–æ–≤.'),
        ('upgrade', '–†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫ –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ü—Ä–∏—Ü–µ–ª—å–Ω—ã–π —É–¥–∞—Ä</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –°–õ –ò–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –ø—Ä–µ–¥–º–µ—Ç–∞, —á—Ç–æ–±—ã –Ω–∞–π—Ç–∏ –≤ –Ω—ë–º –∏–∑—ä—è–Ω. –ù–∞ –æ—Å–º–æ—Ç—Ä –ø—Ä–µ–¥–º–µ—Ç–∞ —É—Ö–æ–¥–∏—Ç 1 —Ä–∞—É–Ω–¥, –Ω–æ —ç—Ç–æ –ø–æ–∑–≤–æ–ª—è–µ—Ç —Ä–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫—É —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–∏—Ü–µ–ª—å–Ω—É—é –∞—Ç–∞–∫—É —Å–æ —à—Ç—Ä–∞—Ñ–æ–º ‚àí6, —á—Ç–æ–±—ã –Ω–∞–Ω–µ—Å—Ç–∏ —Ä–∞–∑—Ä—É—à–∞—é—â–∏–π —É—Ä–æ–Ω –æ—Ä—É–∂–∏—é –∏–ª–∏ –±—Ä–æ–Ω–µ, —Ä–∞–≤–Ω—ã–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç—É –±—Ä–æ—Å–∫–∞ —à–µ—Å—Ç–∏–≥—Ä–∞–Ω–Ω—ã—Ö –∫–æ—Å—Ç–µ–π –≤ –∫–æ–ª–∏—á–µ—Å—Ç–≤–µ, —Ä–∞–≤–Ω–æ–º –∑–Ω–∞—á–µ–Ω–∏—é <strong>–ü—Ä–∏—Ü–µ–ª—å–Ω–æ–≥–æ —É–¥–∞—Ä–∞</strong>.'),
        -- Merchant skills
        ('market', '–¢–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–†—ã–Ω–∫–∞</strong> —Å –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º –°–õ, —á—Ç–æ–±—ã –Ω–∞–π—Ç–∏ –Ω—É–∂–Ω—ã–π –ø—Ä–µ–¥–º–µ—Ç –ø–æ –±–æ–ª–µ–µ –Ω–∏–∑–∫–æ–π —Ü–µ–Ω–µ. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ç–æ—Ä–≥–æ–≤–µ—Ü –Ω–∞—Ö–æ–¥–∏—Ç —Ç–æ–≥–æ, –∫—Ç–æ –ø—Ä–æ–¥–∞—Å—Ç –µ–º—É —Ç–æ—Ç –∂–µ –ø—Ä–µ–¥–º–µ—Ç –∑–∞ –ø–æ–ª—Ü–µ–Ω—ã. –ß–µ–º –±–æ–ª–µ–µ —Ä–µ–¥–∫–∏–π –ø—Ä–µ–¥–º–µ—Ç, —Ç–µ–º –≤—ã—à–µ –°–õ –ø–æ–∏—Å–∫–∞. <strong>–†—ã–Ω–æ–∫</strong> –Ω–µ –¥–µ–π—Å—Ç–≤—É–µ—Ç –Ω–∞ —ç–∫—Å–ø–µ—Ä–∏–º–µ–Ω—Ç–∞–ª—å–Ω—ã–µ, –≤–µ–¥—å–º–∞—á—å–∏ –ø—Ä–µ–¥–º–µ—Ç—ã –∏ —Ä–µ–ª–∏–∫–≤–∏–∏.'),
        ('dirty_deal', '–°–æ–≤–µ—Ä—à–∞—è –ø–æ–¥–∫—É–ø, —Ç–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ù–µ—á–µ—Å—Ç–Ω–∞—è —Å–¥–µ–ª–∫–∞</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –í–æ–ª–µ —Ö 3 —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ç–æ—Ä–≥–æ–≤–µ—Ü –¥–∞—ë—Ç –≤–∑—è—Ç–∫—É –ª—é–±—ã–º –ø—Ä–µ–¥–º–µ—Ç–æ–º, –∫–æ—Ç–æ—Ä—ã–π —É –Ω–µ–≥–æ –µ—Å—Ç—å –∏ –∫–æ—Ç–æ—Ä—ã–π —Å—Ç–æ–∏—Ç –Ω–µ –º–µ–Ω–µ–µ 5 –∫—Ä–æ–Ω. –í–∑—è—Ç–∫–∞ –≤—Å–µ–≥–¥–∞ –¥–∞—ë—Ç +3 –∫ –£–±–µ–∂–¥–µ–Ω–∏—é. –ï—Å–ª–∏ –≤–∑—è—Ç–∫–∞ —Å–æ–≤—Å–µ–º —É–∂ –Ω–µ—Å—É—Ä–∞–∑–Ω–∞, –°–õ —É–≤–µ–ª–∏—á–∏–≤–∞–µ—Ç—Å—è –Ω–∞ 5.'),
        ('promise', '–ü—Ä–∏ –ø–æ–ø—ã—Ç–∫–µ –∫—É–ø–∏—Ç—å –ø—Ä–µ–¥–º–µ—Ç —Ç–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–û–±–µ—â–∞–Ω–∏—è</strong> —Å–æ –°–õ, —Ä–∞–≤–Ω–æ–π –≠–º–ø —Ö 3 –ø—Ä–æ–¥–∞–≤—Ü–∞. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –ø—Ä–æ–¥–∞–≤–µ—Ü –≤–µ—Ä–∏—Ç –æ–±–µ—â–∞–Ω–∏—é —Ç–æ—Ä–≥–æ–≤—Ü–∞ –∑–∞–ø–ª–∞—Ç–∏—Ç—å –ø–æ–∑–∂–µ. –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –Ω–µ–¥–µ–ª—å, —á–µ—Ä–µ–∑ –∫–æ—Ç–æ—Ä–æ–µ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –≤—ã–ø–æ–ª–Ω–∏—Ç—å —ç—Ç–æ –æ–±—è–∑–∞—Ç–µ–ª—å—Å—Ç–≤–æ, —Ä–∞–≤–Ω–æ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–û–±–µ—â–∞–Ω–∏—è</strong>.'),
        ('slums', '–¢–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–¢—Ä—É—â–æ–±—ã</strong> —Å–æ –°–õ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ä–∞–∑–º–µ—Ä–∞ –ø–æ—Å–µ–ª–µ–Ω–∏—è, —á—Ç–æ–±—ã –∑–∞—Ä—É—á–∏—Ç—å—Å—è –ø–æ–º–æ—â—å—é 1 –±–µ—Å–ø—Ä–∏–∑–æ—Ä–Ω–∏–∫–∞ –∏–ª–∏ –±–µ–∑–¥–æ–º–Ω–æ–≥–æ –∑–∞ –∫–∞–∂–¥—ã–π –ø—É–Ω–∫—Ç —Å–≤—ã—à–µ –°–õ (–º–∞–∫—Å–∏–º—É–º 10). –¢–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–ø—Ä–æ—Å–∏—Ç—å —É –Ω–∏—Ö —Å–æ–≤–µ—Ç–∞ –∏ –ø–æ–ª—É—á–∏—Ç—å –±–æ–Ω—É—Å +1 –∫ –ø—Ä–æ–≤–µ—Ä–∫–∞–º –û—Ä–∏–µ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏—è –≤ –≥–æ—Ä–æ–¥–µ –∑–∞ –∫–∞–∂–¥–æ–≥–æ. –ò–Ω—Ñ–æ—Ä–º–∞—Ç–æ—Ä—ã –±–µ—Ä—É—Ç –ø–ª–∞—Ç—É –≤ 1 –∫—Ä–æ–Ω—É –Ω–∞ –∫–∞–∂–¥–æ–≥–æ, –∫–æ–≥–¥–∞ —Å –Ω–∏–º–∏ —Å–æ–≤–µ—Ç—É—é—Ç—Å—è.'),
        ('contacts', '–¢–æ—Ä–≥–æ–≤–µ—Ü —Å–æ —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å—é <strong>–°–≤–æ–π —á–µ–ª–æ–≤–µ–∫</strong> –º–æ–∂–µ—Ç —É–±–µ–¥–∏—Ç—å –¥—Ä—É–≥–æ–≥–æ –ø–µ—Ä—Å–æ–Ω–∞–∂–∞ –ø–æ—à–ø–∏–æ–Ω–∏—Ç—å –Ω–∞ –Ω–µ–≥–æ. –ó–∞–ø–ª–∞—Ç–∏—Ç–µ 10 –∫—Ä–æ–Ω –∏ —Å–æ–≤–µ—Ä—à–∏—Ç–µ –≤—Å—Ç—Ä–µ—á–Ω—É—é –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–°–≤–æ–µ–≥–æ —á–µ–ª–æ–≤–µ–∫–∞</strong> –ø—Ä–æ—Ç–∏–≤ –°–æ–ø—Ä–æ—Ç–∏–≤–ª–µ–Ω–∏—è —É–±–µ–∂–¥–µ–Ω–∏—é —Ü–µ–ª–∏. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ –ø–µ—Ä—Å–æ–Ω–∞–∂ –±—É–¥–µ—Ç —à–ø–∏–æ–Ω–∏—Ç—å –¥–ª—è —Ç–æ—Ä–≥–æ–≤—Ü–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–°–≤–æ–π —á–µ–ª–æ–≤–µ–∫</strong>. –ü–æ –∏—Å—Ç–µ—á–µ–Ω–∏–∏ —ç—Ç–æ–≥–æ —Å—Ä–æ–∫–∞ —Ç–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–Ω–æ–≤–∞ —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É, –æ–ø—è—Ç—å –∂–µ –∑–∞–ø–ª–∞—Ç–∏–≤.'),
        ('merchant_network', '–û–¥–∏–Ω —Ä–∞–∑ –∑–∞ –∏–≥—Ä–æ–≤—É—é –ø–∞—Ä—Ç–∏—é —Ç–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–ö–∞—Ä—Ç–∞ —Å–æ–∫—Ä–æ–≤–∏—â</strong> —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º, —á—Ç–æ–±—ã –≤—Å–ø–æ–º–Ω–∏—Ç—å –ø—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ–º–æ–µ –º–µ—Å—Ç–æ–Ω–∞—Ö–æ–∂–¥–µ–Ω–∏–µ —Ä–µ–ª–∏–∫–≤–∏–∏ –∏–ª–∏ —Ä—É–∏–Ω, –≤ –∫–æ—Ç–æ—Ä—ã—Ö –º–æ–∂–µ—Ç –æ–∫–∞–∑–∞—Ç—å—Å—è —á—Ç–æ-—Ç–æ –ø–æ–ª–µ–∑–Ω–æ–µ. –ú–µ—Å—Ç–æ, –≥–¥–µ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è —ç—Ç–æ—Ç –ø—Ä–µ–¥–º–µ—Ç –∏–ª–∏ —Ä—É–∏–Ω—ã, —Ä–∞—Å–ø–æ–ª–æ–∂–µ–Ω–æ –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –¥–∞–ª–µ–∫–æ –∏–ª–∏ –∂–µ –∫–∏—à–∏—Ç –æ–ø–∞—Å–Ω–æ—Å—Ç—è–º–∏. –ß—Ç–æ–±—ã –¥–æ–±—Ä–∞—Ç—å—Å—è –¥–æ –Ω–µ–≥–æ, –ø–æ—Ç—Ä–µ–±—É–µ—Ç—Å—è —Ü–µ–ª–∞—è –∏–≥—Ä–æ–≤–∞—è –ø–∞—Ä—Ç–∏—è.'),
        ('haggle', '–í—Ö–æ–¥—è –≤ –ø–æ—Å–µ–ª–µ–Ω–∏–µ –≤–ø–µ—Ä–≤—ã–µ, —Ç–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–∞—Ç–∏—Ç—å —á–∞—Å –Ω–∞ —Ä–∞—Å–ø—Ä–æ—Å—Ç—Ä–∞–Ω–µ–Ω–∏–µ –≤–µ—Å—Ç–∏ –æ —Å–≤–æ—ë–º –ø—Ä–∏–±—ã—Ç–∏–∏, –∞ –∑–∞—Ç–µ–º —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É <strong>–•–æ—Ä–æ—à–∏—Ö —Å–≤—è–∑–µ–π</strong> —Å–æ –°–õ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ä–∞–∑–º–µ—Ä–∞ –ø–æ—Å–µ–ª–µ–Ω–∏—è. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ä–µ–ø—É—Ç–∞—Ü–∏—è —Ç–æ—Ä–≥–æ–≤—Ü–∞ –≤ —ç—Ç–æ–º –ø–æ—Å–µ–ª–µ–Ω–∏–∏ –Ω–∞ 1d6 –Ω–µ–¥–µ–ª—å —É–≤–µ–ª–∏—á–∏–≤–∞–µ—Ç—Å—è –Ω–∞ –∑–Ω–∞—á–µ–Ω–∏–µ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Å–≤—ã—à–µ —É–∫–∞–∑–∞–Ω–Ω–æ–π –°–õ, –¥–µ–ª—ë–Ω–Ω–æ–µ –Ω–∞ 2 (–º–∏–Ω–∏–º—É–º 1).'),
        ('merchant_sense', '–¢–æ—Ä–≥–æ–≤–µ—Ü, –∫–æ—Ç–æ—Ä–æ–º—É –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –∏–∑–±–∞–≤–∏—Ç—å—Å—è –æ—Ç –ø—Ä–µ–¥–º–µ—Ç–∞ —Å —Å–æ–º–Ω–∏—Ç–µ–ª—å–Ω—ã–º –ø—Ä–æ–∏—Å—Ö–æ–∂–¥–µ–Ω–∏–µ–º –∏–ª–∏ –∫—Ä–∞–¥–µ–Ω–æ–≥–æ, –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–°–±—ã—Ç—á–∏–∫</strong> —Å–æ –°–õ, –æ–ø—Ä–µ–¥–µ–ª—è–µ–º–æ–π –≤–µ–¥—É—â–∏–º. –ü—Ä–∏ —É—Å–ø–µ—Ö–µ —Ç–æ—Ä–≥–æ–≤–µ—Ü –ø—Ä–æ–¥–∞—Å—Ç –ø—Ä–µ–¥–º–µ—Ç –ø–æ –ø–æ–ª–Ω–æ–π —Ä—ã–Ω–æ—á–Ω–æ–π —Ü–µ–Ω–µ –ø–æ–∫—É–ø–∞—Ç–µ–ª—é, –∫–æ—Ç–æ—Ä—ã–π –Ω–µ —Å—Ç–∞–Ω–µ—Ç –∑–∞–¥–∞–≤–∞—Ç—å –ª–∏—à–Ω–∏—Ö –≤–æ–ø—Ä–æ—Å–æ–≤ –∏ –Ω–µ —Å–¥–∞—Å—Ç —Ç–æ—Ä–≥–æ–≤—Ü–∞ —Å—Ç—Ä–∞–∂–µ.'),
        ('merchant_king', '–¢–æ—Ä–≥–æ–≤–µ—Ü –º–æ–∂–µ—Ç —Å–æ–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–≤–µ—Ä–∫—É —Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏ <strong>–í–æ–∏–Ω—Å–∫–∏–π –¥–æ–ª–≥</strong>, —á—Ç–æ–±—ã –ø–æ–ø—Ä–æ—Å–∏—Ç—å –æ –ø–æ–º–æ—â–∏ –≤–æ–∏–Ω–∞, –∫–æ—Ç–æ—Ä—ã–π —É –Ω–µ–≥–æ –≤ –¥–æ–ª–≥—É. –†–µ–∑—É–ª—å—Ç–∞—Ç –±—Ä–æ—Å–∫–∞ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–∏—Ç—å –ø–æ 3 –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º, —É–∫–∞–∑–∞–Ω–Ω—ã–º –≤ —Ç–∞–±–ª–∏—Ü–µ –Ω–∞ –ø–æ–ª—è—Ö. –í–æ–∏–Ω –±—É–¥–µ—Ç —Ä–∞–±–æ—Ç–∞—Ç—å –Ω–∞ —Ç–æ—Ä–≥–æ–≤—Ü–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π, —Ä–∞–≤–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏—é <strong>–í–æ–∏–Ω—Å–∫–æ–≥–æ –¥–æ–ª–≥–∞</strong>, –∏ –±–µ–∑ –ª–∏—à–Ω–∏—Ö –≤–æ–ø—Ä–æ—Å–æ–≤ –∏—Å–ø–æ–ª–Ω–∏—Ç –ª—é–±–æ–π –ø—Ä–∏–∫–∞–∑ –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö —Ä–∞–∑—É–º–Ω–æ–≥–æ.')
      ) AS raw_data_ru(skill_id, description)
    UNION ALL
    SELECT 'en' AS lang, skill_id, description
      FROM (VALUES
        -- Bard skills
        ('return_act', 'Before attempting a Busking roll a Bard can roll Return Act at a DC set by the GM to see whether they have played in this town before. If the roll is successful the Bard has made a name for themselves in this town already. Not only is their Busking income doubled but they gain a +2 Charisma with everyone in at that venue.'),
        ('raise_a_crowd', 'By taking a full round to perform, you can roll Raise A Crowd to captivate anyone within 20m. Anyone who doesn''t make a Resist Coercion roll higher than your initial roll can do nothing but watch you perform until they succeed at rolling above your initial roll. If attacked a target will snap out of it.'),
        ('good_friend', 'Once per session a Bard can make a Good Friend roll to find a friend to aid them. Take the total roll and split these points up between the 3 categories in the Good Friend chart in the sidebar. This friend will do one reasonable thing for old times'' sake, then cannot be called on again for free and must be convinced.'),
        ('fade', 'A Bard can make a Fade roll against multiple targets'' Awareness rolls to fade into the background. This ability allows a Bard to hide even when there are no good hiding places, by slipping into a conversation, drawing attention to something else, or the like. This ability doesn''t work if you are wearing really flashy clothing.'),
        ('spread_the_word', 'A Bard who rolls a successful Deceit roll against a target can then roll Spread the Word against the target''s Resist Coercion roll. If they succeed the target spreads the Bard''s lie around the target''s settlement or group, giving the Bard a +2 to Deceit when trying to pass off that lie again to someone else.'),
        ('acclimatize', 'When in a settlement a Bard can roll Acclimatize (see Acclimatize chart for DC). If successful, the Bard learns how to appear as a local and will no longer be treated as an outsider. This grants a +2 to Charisma &amp; Persuasion with locals and means that they won''t be questioned or harassed like an outsider.'),
        ('poison_the_well', 'A Bard can make a Poison The Well roll against a target''s EMP√ó3 when they are trying to influence a person or people. If successful, the Bard makes a pointed comment that imposes a ‚àí1 for each point they rolled above the DC to the target''s Seduction, Persuasion, Leadership, Intimidation or Charisma rolls.'),
        ('needling', 'A Bard can make a Needling roll against a target''s Resist Coercion roll. If successful, the Bard goads them with obscenities and threats until they attack. The target takes a negative to their attack and defense equal to half the Bard''s Needling value, lasting for as many rounds as the Needling value.'),
        ('et_tu_brute', 'A Bard can roll Et Tu Brute against a target''s WILL√ó3 to turn them against one ally. If successful the Bard''s lies and half-truths makes the target treat that ally with mistrust and animosity for as many days as the Et Tu Brute value or until they make a Resist Coercion roll that beats the Et Tu Brute roll.'),
        -- Witcher skills
        ('meditation', 'A Witcher can enter a meditative trance which grants all the benefits of sleeping but allows them to remain vigilant. While meditating a Witcher is considered awake for the purpose of noticing anything within double their <strong>Meditation</strong> value in meters.'),
        ('magical_source', 'As a Witcher uses signs more often their body becomes more used to the effort. For every 2 points a Witcher has in <strong>Magical Source</strong> they gain 1 points of Vigor threshold. When this ability reaches level 10, your maximum Vigor threshold becomes 7. This skill can be trained like other skills.'),
        ('heliotrope', 'When a Witcher is targeted by a spell, invocation, or hex they can roll <strong>Heliotrope</strong> to attempt to negate the effects. They must roll a Heliotrope roll that equals or beats the opponent''s roll and then expend an amount of Stamina equal to half the Stamina spent to cast the magic.'),
        ('iron_stomach', 'After decades of drinking toxic witcher potions, witcher bodies adapt to the toxins. A witcher can endure 5% more toxicity from drinking potions and decoctions per 2 points they spend on <strong>Iron Stomach</strong>. This skill can be trained like other skills. At level 10, a witcher''s maximum toxicity is 150%.'),
        ('frenzy', 'When poisoned, a witcher goes into a frenzy and deals an extra 1 melee damage per level in <strong>Frenzy</strong>. While in a <strong>Frenzy</strong>, your single goal is to get to a place of safety or kill the target that poisoned you. When the poison wears off, the <strong>Frenzy</strong> ends. You can attempt to end Frenzy early with a DC:15 Endurance roll.'),
        ('transmutation', 'When taking decoctions a Witcher can roll <strong>Transmutation</strong> at DC:18. A success allows their body to assimilate slightly more of the mutagen than usual and gain a bonus based on which decoction they take. The decoction lasts half as long as it normally would. The extra mutations are too subtle to spot.'),
        ('parry_arrows', 'A Witcher can roll <strong>Parry Arrows</strong> at a ‚àí3 to deflect physical projectiles. When parrying, the Witcher can choose a target within 10m. That target must take a defense action against the Witcher''s <strong>Parry Arrows</strong> roll or be Staggered by the flying projectile.<br><br><b>Parrying Bombs.</b> Bombs and other area of effect attacks detonate after the parry resolves. If the second target dodged the attack, roll on the Scatter Table to see where the attack lands.'),
        ('quick_strike', 'After a Witcher takes their turn they can spend 5 STA and make a <strong>Quick Strike</strong> roll at a DC equal to their opponent''s REF√ó3. On success, they make another single strike in that round. This attack must be made against the opponent they rolled against, but can include disarms, trips, and other attacks.'),
        ('whirl', 'By spending 5 STA per round, a witcher can enter a <strong>Whirl</strong>, where the witcher makes one attack against everyone within sword range each turn, with their <strong>Whirl</strong> roll acting as the attack roll. The witcher can only maintain this Whirl, dodge, and move 2m each round. Doing anything else or being hit halts the <strong>Whirl</strong>.'),
        -- Doctor skills
        ('diagnose', 'When able to look over a wounded person or monster, a Doctor can roll Diagnose at a DC determined by the GM. If they succeed they assess any Critical Wounds the subject has and learn how many Health Points it has left. This also gives a +2 to any Healing Hands checks to heal those wounds.'),
        ('analysis', 'When about to perform a Healing Hands roll, a Doctor can take a turn to make an Analysis roll at a DC equal to the severity of the Critical Wound. If they succeed they gain insight into the wounds, and for every 2 they roll over the DC (minimum 1) the surgery takes 1 turn less.'),
        ('effective_surgery', 'Before starting to heal a Critical Wound a Doctor can make an Effective Surgery roll at a DC equal to the wound''s Healing Hands DC. If they succeed they treat the wounds so skilfully that they heal twice as fast. This ability can be used on critical wounds and can also be used on regular wounds.'),
        ('healing_tent', 'Healing Tent allows a Doctor to roll against a DC set by the GM to create a covered area that provides an optimal medical environment. This takes 1 hour but adds +3 to Healing Hands/First Aid rolls inside, and +2 to the healing rate of anyone in the tent for a number of days equal to your Healing Tent value.'),
        ('improvised_medicine', 'A Doctor can make an Improvisation roll at a DC equal to the crafting DC for a specific medical alchemical item to substitute something else on hand for the same effect. This roll takes one round and if it is failed it can be made again. Improvisation is very specific and works only on this one injury.'),
        ('herbal_remedy', 'By mixing alchemical substances, a Doctor can create an herbal remedy that grants bonuses/effects based on what was put into it (see the Healing Remedy chart in the sidebar). Each remedy remains viable for 3 days and must be burned or chewed to provide the bonus, allowing only 1 use. Making a remedy takes 1 turn.'),
        ('bloody_wound', 'A Doctor who does damage with a bladed weapon can make a Bleeding Wound roll against a DC of 15. On success, the attack causes bleeding at a rate of 1 point per 2 points rolled over the DC. The bleeding can only be stopped by a First Aid roll, at a DC equal to the Doctor''s Bleeding Wound roll.'),
        ('practical_butchery', 'A Doctor can roll Practical Carnage against a DC equal to the opponent''s BODYx3 to cause the target''s wounds and critical wounds to heal half as fast. Other Doctors with the Effective Surgery skill and items that raise the healing rate of wounds and critical wounds can counteract the effect.'),
        ('crippling_wound', 'A Doctor can make a Crippling Wound roll against the target''s defense. This attack takes a -6 to hit but imposes a negative to the target''s REFLEX, BODY, or SPEED equal to 1 per 3 points above their defense roll. This negative can only be removed with an Effective Surgery roll that beats your attack roll.'),
        -- Mage skills
        ('scheming', 'A Mage can make a <strong>Scheming</strong> roll at a DC equal to a target''s INT√ó3. On success the Mage gets a +3 to Deceit, Seduction, Intimidation, or Persuasion against that target from their observations of how the target works. The bonus from this ability applies for a number of days equal to the Mage''s <strong>Scheming</strong> value.'),
        ('grape_vine', 'A Mage can take 1 hour and make a <strong>Grape Vine</strong> roll against a target''s EMP√ó3. Success spreads rumors throughout a settlement or city, lowering the target''s reputation there by half your <strong>Grape Vine</strong> value for a number of days equal to your <strong>Grape Vine</strong> value.'),
        ('assets', 'Once per game a Mage can make an <strong>Assets</strong> roll to remember an asset they ''acquired'' some time ago. Take the total of your roll and distribute it between the 4 columns on the table in the sidebar to find out who you know. This asset will help you, but how much depends on their relationship with you.'),
        ('reverse_engineer', 'By taking 1 hour to study an alchemical solution a Mage can roll <strong>Reverse Engineer</strong> at a DC equal to the Crafting DC for the alchemical item +3. Success allows them to reverse-engineer and write down the item''s formula. This formula is 3 points harder to craft, but reliably creates the desired item.'),
        ('distillation', 'A Mage can roll <strong>Distillation</strong> instead of Alchemy when creating an alchemical solution. Success at this roll creates a dose of that solution that has half again the effect that they would normally have, either in duration, damage, or resistance DC (your choice). Always round down when increasing.'),
        ('mutate', 'A mage can spend all of their stamina and a full day experimenting on a subject to roll <strong>Mutate</strong> at a DC equal to (28 ‚àí (subject''s BODY + WILL)/2) to mutate the subject. Success grants the subject use of the Mutagen with the appropriate minor mutation. Failure throws the subject into Death State and inflicts the larger mutation.<br><br>An individual can only be mutated twice. Further mutations will replace existing ones.'),
        ('in_touch', 'As a Mage utilizes magic more and more, their body becomes more used to the flow. Every point a Mage has in <strong>In Touch</strong> grants +2 points to Vigor threshold. When this ability reaches level 10 your maximum Vigor threshold becomes 25. This skill can be trained, like other skills.'),
        ('immutable', 'A Mage can roll <strong>Immutable</strong> at DC:16 whenever they would normally be affected by dimetrium. Success means that the Mage mostly shrugs off the dimetrium. They are still somewhat dizzy and uncomfortable but retain half of their total Vigor threshold and can perform magic.'),
        ('expanded_magic', 'By channelling magic through various magical foci a Mage can wield incredible power. A Mage can roll <strong>Expanded Magic</strong> before attempting to cast a spell or ritual, at a DC of 16. On success the mage can channel the spell or ritual through any 2 of their foci they choose, reducing the Stamina cost twice.'),
        -- Man At Arms skills
        ('extreme_range', 'When making a ranged attack that would take range penalties, a Man At Arms can lower the penalty by up to half their <strong>Extreme Range</strong> value. They can also make an <strong>Extreme Range</strong> roll (DC:16) to attack targets within 3 times the range of their weapon at a ‚àí10, which can be modified by <strong>Extreme Range</strong>.'),
        ('twin_shot', 'When making a ranged attack with a thrown weapon or a bow, a Man At Arms can roll <strong>Twin Shot</strong> in place of their normal weapon skill. If they hit, they strike with two projectiles and damage two randomly rolled parts of the body. Even if the attack is aimed, the second projectile will hit a random location.'),
        ('precise_aim', 'A Man At Arms who scores a critical with their ranged weapon can immediately roll <strong>Pin Point Aim</strong> at a DC equal to the target''s DEX√ó3. If they succeed, they add their <strong>Pin Point Aim</strong> value to their critical roll. These points only affect the location value of the Critical Wound.'),
        ('bloodhound', 'When tracking a target or trying to find a trail, a Man At Arms adds their <strong>Bloodhound</strong> value to <strong>Wilderness Survival</strong> rolls to find the trail or follow it. If the Man At Arms loses the trail while tracking with this ability, they can roll <strong>Bloodhound</strong> at a DC set by the GM to pick the trail back up immediately.'),
        ('warrior_trap', 'A Man At Arms can make a <strong>Booby Trap</strong> roll to set a makeshift trap in a specific area. See the Booby Trap table for traps that can be built. The Man At Arms can only build one type of trap at a time. Every trap has a 2m radius tripwire and requires an <strong>Awareness</strong> roll at a DC equal to your <strong>Booby Trap</strong> roll to spot.'),
        ('tactical_advantage', 'Instead of moving, a Man At Arms can roll <strong>Tactical Awareness</strong> to gain insight into a whole group of opponents. The Man At Arms gains +3 to attack and defense rolls against every enemy within 10m whose DEX√ó3 is lower than that roll, for one round. This ability also tells the Man At Arms what each affected opponent is about to do.'),
        ('fury', 'A Man At Arms can roll <strong>Fury</strong> at a DC equal to their EMP√ó3. If they succeed, the Man At Arms becomes immune to fear, spells that change emotions, and Verbal Combat for a number of rounds equal to their <strong>Fury</strong> value times 2. During this time, rage clouds their thinking and instinct takes over.'),
        ('two_handed', 'By spending 10 STA and rolling <strong>Zweihand</strong> minus 3 against an opponent''s defense, a Man At Arms can make one attack which does double damage and has armor piercing. If the weapon already has armor piercing, it gains improved armor piercing. A weapon with improved armor piercing gains 3d6 damage.'),
        ('ignore_pain', 'A number of times per game session equal to their BODY value, a Man At Arms can spend 10 STA to immediately roll <strong>Shrug It Off</strong> when an enemy strikes a Critical Wound on them. If their roll beats the enemy''s attack roll, they can negate the Critical Wound, taking the damage as if the enemy hadn''t rolled a critical.'),
        -- Priest skills
        ('divine_power', 'A Priest can become more in tune with their god, gaining 1 point of Vigor threshold per skill level in <strong>Divine Power</strong>. This brings your Vigor threshold to a total of 12 at level 10. <strong>Divine Power</strong> can be trained like other skills and stacks with <strong>Nature Attunement</strong>. The Vigor thresholds are not separate.'),
        ('divine_authority', 'Peasants and the common folk of the world see Priests as agents of the god''s will. A Priest can add their <strong>Divine Authority</strong> to their Leadership rolls if they are in an area where their religion is worshiped. Even when outside such areas of worship a Priest adds half this value, due to their presence.'),
        ('foresight', 'At the will of the GM, a Priest can be overcome by visions of the future, sending them into a catatonic state for 3 rounds. After this time the Priest can roll <strong>Precognition</strong> at a DC set by the GM to decipher the visions that they are stricken by. Such visions are composed of symbolism and metaphors.'),
        ('one_with_nature', 'A Priest can become more in tune with nature, gaining 1 point of Vigor threshold per skill level in <strong>Nature Attunement</strong>. This brings your Vigor threshold to a total of 12 at level 10. <strong>Nature Attunement</strong> can be trained like other skills and stacks with <strong>Divine Power</strong>. The Vigor thresholds are not separate.'),
        ('nature_s_signs', 'When in a purely natural environment a druid can roll <strong>Read Nature</strong> at a DC set by the GM. On a success, the druid reads the signs around them to learn everything that passed through that area and what they did in the area. <strong>Read Nature</strong> renders a very localized picture and cannot track things.'),
        ('nature_s_ally', 'A Druid adds <strong>Animal Compact</strong> to any Wilderness Survival rolls they make to handle animals. A Druid can also make a compact with an animal. By taking a full round and rolling <strong>Animal Compact</strong>, they make one Beast or animal their ally for a number of hours equal to their <strong>Animal Compact</strong> value. Monsters are unaffected.'),
        ('bloody_rituals', 'A Priest casting a ritual can make a <strong>Blood Ritual</strong> check against the casting DC of the ritual. If they succeed, they can cast the ritual without required alchemical substances by sacrificing 5 HP in blood per missing alchemical substance. This blood can come from others, but must be spilled at the time of the ritual.'),
        ('zeal', 'A Priest can roll <strong>Fervor</strong> against a target''s current INT√ó3. On success, the rallying power of the Priest''s words grants 1d6 temporary health for every point rolled over the DC (maximum 5). This lasts for as many rounds as their <strong>Fervor</strong> √ó2 and only works once per target per day.'),
        ('holy_fire', 'A Priest can roll <strong>Word of God</strong> to convince people that they are speaking directly for the gods. Anyone who fails a Resist Coercion roll sees the Priest as a messiah and follows along as an apostle. A Priest can have as many apostles as their <strong>Word of God</strong> value. In combat, use bandit stats for apostles with stat outs.<br><br>Any time you give a truly strange or uncharacteristic command to your apostles, you must make a <strong>Word of God</strong> roll at a DC set by the GM. You can fail 3 times before your apostles leave you. If your last failure is a fumble, your apostles will attack you or brand you as a heretic.'),
        -- Criminal skills
        ('case_joint', 'A Criminal can take an hour to wander the streets of a Settlement and roll <strong>Case The Area</strong> against a DC in the Case The Area chart. If successful, the Criminal memorizes guard patterns, street layouts, and hiding spots for a +2 to Stealth in that area for a number of days equal to their <strong>Case The Area</strong> value.'),
        ('repeat_lockpick', 'Whenever a Criminal successfully picks a lock they can roll <strong>Mental Key</strong> at a DC equal to the Lock Picking DC to memorize its tumbler positions. This allows the Criminal to open the lock without a Lock Picking roll. You can memorize as many locks as you have points in INT and can always replace one.'),
        ('lay_low', 'Once per session a Criminal can roll <strong>Go To Ground</strong> to find a hideout where they can lie low for a while. Take the total value of your <strong>Go To Ground</strong> roll and split the points between the 3 categories in the Go To Ground table in the sidebar. This hideout remains until destroyed, and you can always return to it.'),
        ('vulnerability', 'A Criminal can roll <strong>Weak Spot</strong> against a sentient target''s Deceit roll to identify the target''s most valued possession or person. This also grants the Criminal a +1 to Intimidate for every 2 points they rolled above the target''s Deceit. This Intimidation bonus lasts until something happens to change the target''s weak spot.'),
        ('take_note', 'A Criminal can roll <strong>Marked Man</strong> at a DC equal the target''s EMP√ó3 to mark a target by carving a mark on their door, or the like. If successful the target must make a Charisma, Persuasion, or Intimidation check that beats your <strong>Marked Man</strong> roll to get any help or service from anyone in their settlement.'),
        ('intimidating_presence', 'Once per day, by taking an hour,a Criminal can roll a <strong>Rally</strong> check against a DC set by the GM. For every 2 you roll above the DC they recruit 1 Bandit for a number of days equal to your <strong>Rally</strong> value. If a Bandit is knocked below half health they must roll under the Criminal''s WILL on a 10 sided die or flee.'),
        ('smuggler', 'A Criminal who''s not in active combat and takes a round to aim can roll <strong>Careful Aim</strong> at a DC equal to their target''s REF√ó3 to gain a bonus on their next attack equal to half their <strong>Careful Aim</strong> value. Being spotted after making this roll but before attacking halves the bonus.'),
        ('false_identity', 'A Criminal can roll <strong>Eye Gouge</strong> in place of an attack to temporarily blind a target. <strong>Eye Gouge</strong> requires the Criminal to be in melee range and imposes a -3 to hit. However if it hits, the target takes an unmodified 2d6 damage and is blinded for a number of rounds equal to the <strong>Eye Gouge</strong> value.'),
        ('black_market', 'When ambushing a target, a Criminal can make an <strong>Assassin''s Strike</strong> roll against the target''s Awareness roll to conceal themselves after an attack. This ability can be used in any situation but it imposes penalties based on light and cover conditions. Multiple opponents can each roll to spot the Criminal.'),
        -- Craftsman skills
        ('large_catalog', 'A skilled Craftsman can keep a mental catalogue of diagrams in their head at all times. When a Craftsman has memorized as many diagrams as they can, they may roll <strong>Extensive Catalogue</strong> at DC:15 to memorize one more. There is no limit, but every 10 diagrams they have memorized adds 1 to the DC.'),
        ('apprentice', 'A Craftsman who begins crafting an item can roll <strong>Journeyman</strong> at a DC equal to the item''s crafting DC. If they succeed they add +1 DMG for weapons or +1 SP for armor for every 2 points they rolled above the DC. The maximum bonus they can give to DMG or SP is 5.'),
        ('masterwork', 'Master Crafting allows a Craftsman to make items that are master grade. They can also roll a <strong>Master Crafting</strong> roll at any time at a DC equal to the item''s crafting DC to permanently grant armor resistance (their choice) or weapons a 50% bleeding or -2 Stun value based on damage type.'),
        ('alchemical_concoction', 'A skilled Craftsman can keep a mental catalogue of formulae in their head at all times. When a Craftsman has memorized as many formulae as they can, they may roll <strong>Mental Pharmacy</strong> at DC:15 to memorize one more. There is no limit, but every 10 formulae they have memorized adds 1 to the DC.'),
        ('enhanced_potion', 'Any time a craftsman sets out to make an alchemical item they can make a <strong>Double Dose</strong> roll at a DCequal to the formula''s crafting DC. If they succeed they create two units of the formula with the ingredients of one. This applies to all items created with alchemy, including potions, oils, decoctions, and bombs.'),
        ('experimental_formula', 'Craftsmen can roll an <strong>Adaptation</strong> check (3 + the crafting DC) before making a witcher potion to lower its DC to avoid poisoning by 1 for every point they rolled over the crafting DC. If they fail, the potion comes out as poisonous as it normally would be. The DC to avoid poisoning can never be lower than 12.'),
        ('workshop', 'A Craftsman can make an <strong>Augmentation</strong> roll at a DC listed in the Augmentation chart to augment a weapon or Armor with their crafting tools. This augmentation takes 3 rounds. While a forge isn''t required, it grants a +2 to the roll. A fumble results in the item taking damage equal to the fumble value.'),
        ('repair', 'A Craftsman can coat an existing weapon in silver with a forge and a number of units of silver ingots based on the size of the weapon. The DC for this roll is 16. If you succeed, add +1d6 silver damage to a weapon per 3 points you rolled above the DC, up to 5d6. Failing the roll breaks the weapon.'),
        ('upgrade', 'A Craftsman can roll <strong>Pinpoint</strong> with a DC equal an item''s crafting DC to search for a flaw in the item''s design. This takes 1 turn studying, but allows the Craftsman to make a targeted attack at a -6 to do ablation damage to the armor or weapon equal to half their <strong>Pinpoint</strong> value in 6-sided dice.'),
        -- Merchant skills
        ('market', 'A Merchant can roll <strong>Options</strong> against a DC set by the GM to find a lower price on an item. If they succeed the Merchant finds another person selling the same item for half the price. The higher the item rarity, the higher the DC should be. <strong>Options</strong> does not affect experimental, witcher, or relic items.'),
        ('dirty_deal', 'When bribing a target a Merchant can roll <strong>Hard Bargain</strong> at a DC equal to the opponent''s WILLx3. If they succeed, they can bribe the opponent with any item they have at hand that is worth 5 crowns. The object always grants +3 to Persuasion. The DC rises by 5 for truly ridiculous bribes.'),
        ('promise', 'When attempting to buy an item, a Merchant can make a <strong>Promise</strong> roll at a DC equal to the Salesperson''s EMPx3. If they succeed the salesperson accepts the Merchant''s promise to pay for the item later. This promise holds the salesperson over for a number of weeks equal to your <strong>Promise</strong> ability.'),
        ('slums', 'A Merchant can make a <strong>Rookery</strong> roll at a DC based on the settlement they are in to gain the aid of 1 urchin or vagrant per 1 point they rolled over the DC (maximum 10). These people can be consulted to grant +1 per person on Streetwise rolls. Informants take 1 crown each as payment each time they are consulted.'),
        ('contacts', 'A Merchant with <strong>Insider</strong> can convince a person to spy for them. Spend 10 crowns and roll <strong>Insider</strong> versus the person''s Resist Coercion roll. If it is successful the person will spy on a target for as many days as your <strong>Insider</strong> value. At the end of this time you can roll again, but must pay again.'),
        ('merchant_network', 'Once per session a Merchant can roll <strong>Treasure Map</strong> at a DC set by the GM to remember the supposed location of a relic item, or a ruin that may hide something useful. This location will, of course, be out of the way or exceedingly dangerous, requiring a quest. Reaching this item or ruin should require a full session.'),
        ('haggle', 'On first entering a settlement, a Merchant can spend an hour spreading word of their arrival, then roll <strong>Well Connected</strong> at a DC based on the settlement. Success raises their reputation in that settlement by a number equal to the amount you rolled over the DC divided by 2 (minimum 1), for 1d6 Weeks.'),
        ('merchant_sense', 'A Merchant who has to get rid of a dubious or stolen item can make a <strong>Fence</strong> roll at a DC determined by the GM. If they succeed, they sell the item (at full market price) to a buyer who won''t ask any serious questions and won''t turn them in to the Guard.'),
        ('merchant_king', 'A Merchant can roll <strong>Warrior''s Debt</strong> to call on a fighter who owes them. Split your roll between the 3 sections on the Warrior table in the sidebar. This warrior will work for you for a number of days equal to your <strong>Warrior''s Debt</strong> value and takes any reasonable order you give without asking questions.')
      ) AS raw_data_en(skill_id, description)
  ),
  ins_prof_skill_descriptions AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
    SELECT ck_id(meta.su_su_id || '.wcc_skills.' || raw_prof_skill_descriptions.skill_id || '.description') AS id
         , meta.entity, 'description', raw_prof_skill_descriptions.lang, raw_prof_skill_descriptions.description
      FROM raw_prof_skill_descriptions
      CROSS JOIN meta
    ON CONFLICT (id, lang) DO NOTHING
  )
UPDATE wcc_skills
SET skill_desc_id = ck_id('witcher_cc.wcc_skills.' || skill_id || '.description')
WHERE skill_type = 'professional'
  AND skill_desc_id IS NULL
  AND skill_id IN (
    'return_act', 'raise_a_crowd', 'good_friend', 'fade', 'spread_the_word', 'acclimatize',
    'poison_the_well', 'needling', 'et_tu_brute', 'meditation', 'magical_source', 'heliotrope',
    'iron_stomach', 'frenzy', 'transmutation', 'parry_arrows', 'quick_strike', 'whirl',
    'diagnose', 'analysis', 'effective_surgery', 'healing_tent', 'improvised_medicine', 'herbal_remedy',
    'bloody_wound', 'practical_butchery', 'crippling_wound', 'scheming', 'grape_vine', 'assets',
    'reverse_engineer', 'distillation', 'mutate', 'in_touch', 'immutable', 'expanded_magic',
    'extreme_range', 'twin_shot', 'precise_aim', 'bloodhound', 'warrior_trap', 'tactical_advantage',
    'fury', 'two_handed', 'ignore_pain', 'divine_power', 'divine_authority', 'foresight',
    'one_with_nature', 'nature_s_signs', 'nature_s_ally', 'bloody_rituals', 'zeal', 'holy_fire',
    'case_joint', 'repeat_lockpick', 'lay_low', 'vulnerability', 'take_note', 'intimidating_presence',
    'smuggler', 'false_identity', 'black_market', 'large_catalog', 'apprentice', 'masterwork',
    'alchemical_concoction', 'enhanced_potion', 'experimental_formula', 'workshop', 'repair', 'upgrade',
    'market', 'dirty_deal', 'promise', 'slums', 'contacts', 'merchant_network', 'haggle', 'merchant_sense', 'merchant_king'
  )
  AND EXISTS (
    SELECT 1 FROM i18n_text 
    WHERE id = ck_id('witcher_cc.wcc_skills.' || wcc_skills.skill_id || '.description')
  );

-- –í—Å—Ç–∞–≤–∫–∞ –ø—Ä–æ—Ñ–µ—Å—Å–∏–π (Bard, Witcher, Mage)
WITH
  meta AS (SELECT 'witcher_cc' AS su_su_id, 'wcc_professions' AS entity),
  raw_professions AS (
    SELECT 'ru' AS lang, raw_data_ru.*
      FROM (VALUES
        ('bard', '–ë–∞—Ä–¥', 'core'),
        ('witcher', '–í–µ–¥—å–º–∞–∫', 'core'),
        ('doctor', '–ú–µ–¥–∏–∫', 'core'),
        ('mage', '–ú–∞–≥', 'core'),
        ('man_at_arms', '–í–æ–∏–Ω', 'core'),
        ('priest', '–ñ—Ä–µ—Ü', 'core'),
        ('criminal', '–ü—Ä–µ—Å—Ç—É–ø–Ω–∏–∫', 'core'),
        ('craftsman', '–†–µ–º–µ—Å–ª–µ–Ω–Ω–∏–∫', 'core'),
        ('merchant', '–¢–æ—Ä–≥–æ–≤–µ—Ü', 'core')
      ) AS raw_data_ru(prof_id, name, dlc)
    UNION ALL
    SELECT 'en' AS lang, prof_id, name, dlc
      FROM (VALUES
        ('bard', 'Bard', 'core'),
        ('witcher', 'Witcher', 'core'),
        ('doctor', 'Doctor', 'core'),
        ('mage', 'Mage', 'core'),
        ('man_at_arms', 'Man At Arms', 'core'),
        ('priest', 'Priest', 'core'),
        ('criminal', 'Criminal', 'core'),
        ('craftsman', 'Craftsman', 'core'),
        ('merchant', 'Merchant', 'core')
      ) AS raw_data_en(prof_id, name, dlc)
  ),
  ins_prof_names AS (
    INSERT INTO i18n_text (id, entity, entity_field, lang, text)
    SELECT ck_id(meta.su_su_id || '.wcc_professions.' || raw_professions.prof_id || '.name') AS id
         , meta.entity, 'name', raw_professions.lang, raw_professions.name
      FROM raw_professions
      CROSS JOIN meta
    ON CONFLICT (id, lang) DO NOTHING
  )
INSERT INTO wcc_professions (prof_id, dlc, prof_name_id, prof_desc_id)
SELECT DISTINCT
  raw_professions.prof_id,
  raw_professions.dlc,
  ck_id(meta.su_su_id || '.wcc_professions.' || raw_professions.prof_id || '.name') AS prof_name_id,
  -- prof_desc_id will be set to NULL initially and updated later when profession descriptions 
  -- are inserted in 090_* files. The ID format matches: witcher_cc.wcc_profession_o{NN}.answer_options.description
  NULL::uuid AS prof_desc_id
FROM raw_professions
CROSS JOIN meta
WHERE raw_professions.lang = 'ru'
ON CONFLICT (prof_id) DO NOTHING;

-- Update prof_desc_id after profession descriptions are created in 090_* files
-- This will be executed after those files run
UPDATE wcc_professions
SET prof_desc_id = ck_id('witcher_cc.wcc_profession_o' || 
  CASE prof_id
    WHEN 'bard' THEN '01'
      WHEN 'witcher' THEN '02'
      WHEN 'doctor' THEN '03'
      WHEN 'mage' THEN '04'
      WHEN 'man_at_arms' THEN '05'
      WHEN 'priest' THEN '06'
      WHEN 'criminal' THEN '07'
      WHEN 'craftsman' THEN '08'
      WHEN 'merchant' THEN '09'
    END || '.answer_options.description')
WHERE prof_desc_id IS NULL
  AND EXISTS (
    SELECT 1 FROM i18n_text 
    WHERE id = ck_id('witcher_cc.wcc_profession_o' || 
      CASE wcc_professions.prof_id
        WHEN 'bard' THEN '01'
        WHEN 'witcher' THEN '02'
        WHEN 'doctor' THEN '03'
        WHEN 'mage' THEN '04'
        WHEN 'man_at_arms' THEN '05'
        WHEN 'priest' THEN '06'
        WHEN 'criminal' THEN '07'
        WHEN 'craftsman' THEN '08'
        WHEN 'merchant' THEN '09'
      END || '.answer_options.description')
  );

